	[176] = { name = [[Green Riding Nether Ray]], sourceText = '|cFFFFD200Vendor: |rGrella|n|cFFFFD200Zone: |rTerokkar Forest, Outland|n|cFFFFD200Faction: |rSha\'tari Skyguard - Exalted|n|cFFFFD200Cost: |r200|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Nether Rays are among the most trusted, versatile mounts in all of Outland and beyond.]], },
	[177] = { name = [[Red Riding Nether Ray]], sourceText = '|cFFFFD200Vendor: |rGrella|n|cFFFFD200Zone: |rTerokkar Forest, Outland|n|cFFFFD200Faction: |rSha\'tari Skyguard - Exalted|n|cFFFFD200Cost: |r200|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Nether Rays are among the most trusted, versatile mounts in all of Outland and beyond.]], },
	[178] = { name = [[Purple Riding Nether Ray]], sourceText = '|cFFFFD200Vendor: |rGrella|n|cFFFFD200Zone: |rTerokkar Forest, Outland|n|cFFFFD200Faction: |rSha\'tari Skyguard - Exalted|n|cFFFFD200Cost: |r200|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Nether Rays are among the most trusted, versatile mounts in all of Outland and beyond.]], },
	[179] = { name = [[Silver Riding Nether Ray]], sourceText = '|cFFFFD200Vendor: |rGrella|n|cFFFFD200Zone: |rTerokkar Forest, Outland|n|cFFFFD200Faction: |rSha\'tari Skyguard - Exalted|n|cFFFFD200Cost: |r200|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Nether Rays are among the most trusted, versatile mounts in all of Outland and beyond.]], },
	[180] = { name = [[Blue Riding Nether Ray]], sourceText = '|cFFFFD200Vendor: |rGrella|n|cFFFFD200Zone: |rTerokkar Forest, Outland|n|cFFFFD200Faction: |rSha\'tari Skyguard - Exalted|n|cFFFFD200Cost: |r200|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Nether Rays are among the most trusted, versatile mounts in all of Outland and beyond.]], },
	[183] = { name = [[Ashes of Al'ar]], sourceText = '|cFFFFD200Drop: |rKael\'thas Sunstrider|n|cFFFFD200Location: |rThe Eye, Tempest Keep',  description = [[Al\'ar was the beloved pet of Kael'thas Sunstrider, who often boasted that death would never claim it. Perhaps he was right.]], },
	[185] = { name = [[Raven Lord]], sourceText = '|cFFFFD200Drop:|r Anzu|n|cFFFFD200Zone: |rSethekk Halls (Heroic)',  description = [[The rest be forgotten to walk upon the ground, clipped wings and shame. -Word of the Raven]], },
	[186] = { name = [[Onyx Netherwing Drake]], sourceText = '|cFFFFD200Quest: |rOnyxien the Onyx Netherwing Drake|n|cFFFFD200Zone: |rShattrath City, Outland',  description = [[After promising to fly you to the ends of Outland, the netherwing drake was eventually drawn to the blue skies of Azeroth as well.]], },
	[187] = { name = [[Azure Netherwing Drake]], sourceText = '|cFFFFD200Quest: |rSuraku the Azure Netherwing Drake|n|cFFFFD200Zone: |rShattrath City, Outland',  description = [[After promising to fly you to the ends of Outland, the netherwing drake was eventually drawn to the blue skies of Azeroth as well.]], },
	[188] = { name = [[Cobalt Netherwing Drake]], sourceText = '|cFFFFD200Quest: |rJorus the Cobalt Netherwing Drake|n|cFFFFD200Zone: |rShattrath City, Outland',  description = [[After promising to fly you to the ends of Outland, the netherwing drake was eventually drawn to the blue skies of Azeroth as well.]], },
	[189] = { name = [[Purple Netherwing Drake]], sourceText = '|cFFFFD200Quest: |rMalfas the Purple Netherwing Drake|n|cFFFFD200Zone: |rShattrath City, Outland',  description = [[After promising to fly you to the ends of Outland, the netherwing drake was eventually drawn to the blue skies of Azeroth as well.]], },
	[190] = { name = [[Veridian Netherwing Drake]], sourceText = '|cFFFFD200Quest: |rZoya the Veridian Netherwing Drake|n|cFFFFD200Zone: |rShattrath City, Outland',  description = [[After promising to fly you to the ends of Outland, the netherwing drake was eventually drawn to the blue skies of Azeroth as well.]], },
	[191] = { name = [[Violet Netherwing Drake]], sourceText = '|cFFFFD200Quest: |rVoranaku the Violet Netherwing Drake|n|cFFFFD200Zone: |rShattrath City, Outland',  description = [[After promising to fly you to the ends of Outland, the netherwing drake was eventually drawn to the blue skies of Azeroth as well.]], },
	[197] = { name = [[Swift Spectral Tiger]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Historians claim that these incorporeal beasts were created when Draenor exploded, unleashing energies that warped the world\'s flora and fauna.]], },
	[199] = { name = [[Amani War Bear]], sourceText = '|cFFFFD200Legacy|r',  description = [[Only the swiftest heroes venturing into the troll temple of Zul'Aman were able to subdue these rare battle mounts.]], },
	[201] = { name = [[Brewfest Ram]], sourceText = '|cFFFFD200Legacy|r',  description = [[Brewers retired this mount from active advertising service after complaints that the beasts were "too temperamental" to control in major cities.]], },
	[202] = { name = [[Swift Brewfest Ram]], sourceText = '|cFFFFD200Drop:|r Keg-Shaped Treasure Chest|n|cFFFFD200Holiday:|r Brewfest',  description = [[Dwarves attribute this breed\'s even temperament to rigorous training, but other races argue that a daily diet of strong ale has something to do with it.]], },
	[203] = { name = [[Cenarion War Hippogryph]], sourceText = '|cFFFFD200Vendor: |rFedryen Swiftspear|n|cFFFFD200Zone: |rZangarmarsh, Outland|n|cFFFFD200Faction: |rCenarion Expedition - Exalted|n|cFFFFD200Cost: |r2000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Members of the Cenarion Circle have shared a long and prosperous partnership with their hippogryph allies. When conflict is unavoidable, the Cenarion War Hippogryph is called into service.]], },
	[204] = { name = [[Turbo-Charged Flying Machine]], sourceText = '|cFFFFD200Profession: |rEngineering',  description = [[Only the most talented engineers have the courage and the mental stamina to create a flying machine that boasts stability and safety.  Turbo-charging one is just crazy!]], },
	[205] = { name = [[Flying Machine]], sourceText = '|cFFFFD200Profession: |rEngineering',  description = [[The backbone of the Alliance air force is comprised predominantly of gnomish flying machines due to their maneuverability and speed.]], },
	[206] = { name = [[Merciless Nether Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[The most aggressive nether drakes were awarded those heroes who showed significant prowess and commitment in the gladiatorial arena.]], },
	[207] = { name = [[Merciless Nether Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[The most aggressive nether drakes were awarded those heroes who showed significant prowess and commitment in the gladiatorial arena.]], },
	[211] = { name = [[X-51 Nether-Rocket]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[First designed as a booster for a much larger rocket, the X-51 was determined to give the perfect thrust capacity for a single rider payload.]], },
	[212] = { name = [[X-51 Nether-Rocket X-TREME]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[What it lacks in safety features it more than makes up for in speed and style.  Not for the faint of heart.]], },
	[213] = { name = [[Swift White Hawkstrider]], sourceText = '|cFFFFD200Drop: |rKael\'thas Sunstrider|n|cFFFFD200Zone: |rMagisters\' Terrace',  description = [[I may question Prince Kael'thas\'s loyalties, but never his style. -Elrodan]], },
	[219] = { name = [[Headless Horseman\'s Mount]], sourceText = '|cFFFFD200Drop:|r Loot-Filled Pumpkin|n|cFFFFD200Holiday:|r Hallow\'s End|n',  description = [[Be it into the flood, or into the fire, this one will go where you require.]], },
	[220] = { name = [[Black War Elekk]], sourceText = '|cFFFFD200Vendor: |rLieutenant Karter|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r15|Hitem:137642|h|TInterface/ICONS/Ability_PVP_GladiatorMedallion.BLP:0|t|h',  description = [[Uniquely aggressive and pugnacious elekk bulls are culled from the herd and sold to Ethereal traders. The traders then train the bulls for combat and sell them to owners unconcerned about safety.]], },
	[221] = { name = [[Acherus Deathcharger]], sourceText = '|cFFFFD200Trainer:|r Death Knight|n',  description = [[Some horses merely adopt the dark. This horse was born in it, molded by it.]], },
	[222] = { name = [[Swift Zhevra]], sourceText = '|cFFFFD200Recruit-a-Friend |r',  description = [[Zhevras are rarely used as mounts in Azeroth due to their stubborn nature and tendency to bite.]], },
	[223] = { name = [[Vengeful Nether Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[The most aggressive nether drakes were awarded those heroes who showed significant prowess and commitment in the gladiatorial arena.]], },
	[224] = { name = [[Swift Zhevra]], sourceText = '|cFFFFD200Recruit-a-Friend |r',  description = [[Zhevras are rarely used as mounts in Azeroth due to their stubborn nature and tendency to bite.]], },
	[225] = { name = [[Brewfest Riding Kodo]], sourceText = '|cFFFFD200Legacy|r',  description = [[You can't remember exactly how you got this mount. Then again, you can't remember much of anything that happens at Brewfest.]], },
	[226] = { name = [[Great Brewfest Kodo]], sourceText = '|cFFFFD200Drop:|r Keg-Shaped Treasure Chest|n|cFFFFD200Holiday:|r Brewfest',  description = [[Coren Direbrew won this prize after drinking a Tauren druid under the table - and the druid was in bear form. It was one of Direbrew\'s proudest moments.]], },
	[230] = { name = [[Big Battle Bear]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[He\'s big, he\'s ready for battle, and he\'s a bear. What more could you want in a mount?]], },
	[236] = { name = [[Winged Steed of the Ebon Blade]], sourceText = '|cFFFFD200Vendor: |rDread Commander Thalanor|n|cFFFFD200Zone: |rEastern Plaguelands|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Dead or alive, you're coming with me.]], },
	[237] = { name = [[White Polar Bear]], sourceText = '|cFFFFD200Drop:|r Hyldnir Spoils|n|cFFFFD200Location:|r The Storm Peaks',  description = [[The frost vrykul believe that wearing a necklace of polar bear claws will grant them otherworldly strength.]], },
	[238] = { name = [[Swift Spectral Gryphon]], sourceText = '|cFFFFD200Legacy|r',  description = [[Even the dead need a lift every now and then.]], },
	[240] = { name = [[Mechano-Hog]], sourceText = '|cFFFFD200Profession: |rEngineering',  description = [[Difficult to build and to maintain, these hogs more than make up for their expense through sheer cool factor.]], },
	[241] = { name = [[Brutal Nether Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[The most aggressive nether drakes were awarded those heroes who showed significant prowess and commitment in the gladiatorial arena.]], },
	[243] = { name = [[Big Blizzard Bear]], sourceText = '|cFFFFD200Promotion:|r BlizzCon|n',  description = [[Whenever you ride this mount, you feel the urge to shout, "Cry havoc and let slip the murlocs of war!"]], },
	[246] = { name = [[Azure Drake]], sourceText = '|cFFFFD200Drop:|r Alexstrasza\'s Gift|n|cFFFFD200Location:|r Eye of Eternity',  description = [[Always speak politely to an enraged dragon.]], },
	[247] = { name = [[Blue Drake]], sourceText = '|cFFFFD200Drop:|r Alexstrasza\'s Gift|n|cFFFFD200Location:|r Eye of Eternity',  description = [[Come not between the dragon and his wrath.]], },
	[248] = { name = [[Bronze Drake]], sourceText = '|cFFFFD200Drop: |rInfinite Corruptor|n|cFFFFD200Zone: |rThe Culling of Stratholme',  description = [[Lost time is never found again. Oh, never mind I found it. -Chronormu]], },
	[249] = { name = [[Red Drake]], sourceText = '|cFFFFD200Vendor: |rCielstrasza|n|cFFFFD200Zone: |rDragonblight|n|cFFFFD200Faction: |rThe Wyrmrest Accord - Exalted|n|cFFFFD200Cost: |r2000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[You should never listen to minstrels' fancies. A dragon would never hurt a soul, unless they tried to hurt him first.]], },
	[250] = { name = [[Twilight Drake]], sourceText = '|cFFFFD200Drop:|r Sartharion|n|cFFFFD200Location:|r The Obsidian Sanctum',  description = [[But the most important rule, the rule you can never forget, no matter how much he cries, no matter how much he begs, never feed him after midnight.]], },
	[251] = { name = [[Black Polar Bear]], sourceText = '|cFFFFD200Legacy|r',  description = [[The taunka use black polar bear pelts in their religious rites, usually at the funerals of those who hunt black polar bears.]], },
	[253] = { name = [[Black Drake]], sourceText = '|cFFFFD200Drop:|r Sartharion',  description = [[It never pays to leave a live dragon out of the equation.]], },
	[254] = { name = [[Black War Mammoth]], sourceText = '|cFFFFD200Vendor: |rKnight Dameron|n|cFFFFD200Zone: |rWintergrasp|n|cFFFFD200Cost: |r15|Hitem:137642|h|TInterface/ICONS/Ability_PVP_GladiatorMedallion.BLP:0|t|h',  description = [[Normally placid beasts, these intelligent animals form a strong bond with their riders, making them loyal and fearless in battle.]], },
	[255] = { name = [[Black War Mammoth]], sourceText = '|cFFFFD200Vendor: |rStone Guard Mukar|n|cFFFFD200Zone: |rWintergrasp|n|cFFFFD200Cost: |r15|Hitem:137642|h|TInterface/ICONS/Ability_PVP_GladiatorMedallion.BLP:0|t|h',  description = [[Sure-footed and unflappable, these dark-furred mammoths are prized for their steady temperament going into battle.]], },
	[256] = { name = [[Wooly Mammoth]], sourceText = '|cFFFFD200Vendor: |rMei Francis|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Directly descended from the original progenitor of the mammoth race, wooly mammoths are considered more feral and primal than their shorter-haired cousins.]], },
	[257] = { name = [[Wooly Mammoth]], sourceText = '|cFFFFD200Vendor: |rMei Francis|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[This wild variant of the mammoth is not bred for riding. Most wooly mammoths are captured in the wild and tamed by their riders, resulting in a fierce, cunning mount.]], },
	[258] = { name = [[Ice Mammoth]], sourceText = '|cFFFFD200Vendor: |rLillehoff|n|cFFFFD200Zone: |rThe Storm Peaks|n|cFFFFD200Faction: |rThe Sons of Hodir - Revered|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The Sons of Hodir believe that riding an ice-white mammoth will protect the rider from frostbite and snow blindness.]], },
	[259] = { name = [[Ice Mammoth]], sourceText = '|cFFFFD200Vendor: |rLillehoff|n|cFFFFD200Zone: |rThe Storm Peaks|n|cFFFFD200Faction: |rThe Sons of Hodir - Revered|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Many ice giants claim that they have trained their favored white mammoths to trigger avalanches with their trumpeting calls.]], },
	[262] = { name = [[Red Proto-Drake]], sourceText = '|cFFFFD200Achievement: |rGlory of the Hero|n|cFFFFD200Category: |rDungeons & Raids',  description = [[The vrykul were the first to recognize their potential. Or maybe the second, depending on how you look at it.]], },
	[263] = { name = [[Black Proto-Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[Bred specifically to honor those heroes who showed exceptional resilience and fortitude facing the dangers in Northrend.]], },
	[264] = { name = [[Blue Proto-Drake]], sourceText = '|cFFFFD200Drop: |rSkadi the Ruthless|n|cFFFFD200Zone: |rUtgarde Pinnacle',  description = [[The Storm Peaks! There we will find the beast strong enough to bear me into battle. -Skadi the Ruthless]], },
	[265] = { name = [[Time-Lost Proto-Drake]], sourceText = '|cFFFFD200Drop: |rTime-Lost Proto-Drake|n|cFFFFD200Zone: |rThe Storm Peaks',  description = [[The only reason for time is so that everything doesn't happen at once.]], },
	[266] = { name = [[Plagued Proto-Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[Bred specifically to honor those heroes who showed significant courage and persistence facing the dangers in Northrend.]], },
	[267] = { name = [[Violet Proto-Drake]], sourceText = '|cFFFFD200Achievement: |rWhat a Long, Strange Trip It\'s Been|n|cFFFFD200Category: |rWorld Events',  description = [[I cannot help but wonder if things would have turned out different if the titans had elevated even one more dragonflight. -Alexstrasza]], },
	[268] = { name = [[Albino Drake]], sourceText = '|cFFFFD200Achievement: |rLeading the Cavalry|n|cFFFFD200Category: |rGeneral',  description = [[Even the Draconic Native Association tests proved inconclusive in determining if the albino drake is related to a known dragonflight. -Breanni]], },
	[269] = { name = [[Armored Brown Bear]], sourceText = '|cFFFFD200Vendor: |rMei Francis|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r750|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[This ursine mount\'s swiftness is enhanced by its heavy armor, imbued with magic from Kirin Tor magi.]], },
	[270] = { name = [[Armored Brown Bear]], sourceText = '|cFFFFD200Vendor: |rMei Francis|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r750|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Your cries of "For the Horde!" will be much more menacing from atop a heavily armored bear that\'s barreling down on Alliance at top speed.]], },
	[271] = { name = [[Black War Bear]], sourceText = '|cFFFFD200Achievement: |rFor The Alliance!|n|cFFFFD200Category: |rPlayer vs. Player',  description = [[Killing leaders of the Horde is its own reward, but this intimidating beast is an excellent memento.]], },
	[272] = { name = [[Black War Bear]], sourceText = '|cFFFFD200Achievement: |rFor The Horde!|n|cFFFFD200Category: |rPlayer vs. Player',  description = [[This formidable mount is bought with the blood of Alliance leaders, but you would have killed them for free.]], },
	[273] = { name = [[Grand Caravan Mammoth]], sourceText = '|cFFFFD200Legacy|r',  description = [[Merchants trust only this thundering, mountainous steed to protect them when venturing down Azeroth\'s most dangerous trade routes.]], },
	[274] = { name = [[Grand Caravan Mammoth]], sourceText = '|cFFFFD200Legacy|r',  description = [[A common sight on trade routes all across Azeroth, this calm, stalwart mount is the favorite of merchants traveling to exotic and distant markets.]], },
	[275] = { name = [[Mekgineer\'s Chopper]], sourceText = '|cFFFFD200Profession: |rEngineering',  description = [[Sporting superb engineering craftsmanship, this chopper is one of the few personal vehicles on Azeroth built for two.]], },
	[276] = { name = [[Armored Snowy Gryphon]], sourceText = '|cFFFFD200Vendor: |rMei Francis|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r2000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The nice thing is that when they attack, blood almost never stains their feathers. -Mei Francis]], },
	[277] = { name = [[Armored Blue Wind Rider]], sourceText = '|cFFFFD200Vendor: |rMei Francis|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r2000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[They are not native to Northrend, but they have adapted extremely well to the cold. -Mei Francis]], },
	[278] = { name = [[Green Proto-Drake]], sourceText = '|cFFFFD200Drop:|r Cracked Egg',  description = [[The vrykul were the first to recognize their potential. Or maybe the second, depending on how you look at it.]], },
	[279] = { name = [[Magnificent Flying Carpet]], sourceText = '|cFFFFD200Profession: |rTailoring',  description = [[Standard features include a sturdy imbued frostweave frame and real iceweb spider silk threading.  Spare head pillow sold separately.]], },
	[280] = { name = [[Traveler\'s Tundra Mammoth]], sourceText = '|cFFFFD200Vendor: |rMei Francis|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r20000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Ancient manuscripts depict merchants traveling across the frozen tundra with these colossal beasts, leading scholars to believe they are the first domesticated mounts.]], },
	[284] = { name = [[Traveler\'s Tundra Mammoth]], sourceText = '|cFFFFD200Vendor: |rMei Francis|n|cFFFFD200Zone: |rCrystalsong Forest|n|cFFFFD200Cost: |r20000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Riders and merchants alike trust only this strong and steadfast mammoth to carry them safely across the frigid tundra.]], },
	[285] = { name = [[Flying Carpet]], sourceText = '|cFFFFD200Profession: |rTailoring',  description = [[Hop aboard this flying carpet to see the shining, shimmering, and splendid world of Azeroth, wonder by wonder.]], },
	[286] = { name = [[Grand Black War Mammoth]], sourceText = '|cFFFFD200Drop: |rBoss Drop|n|cFFFFD200Location: |rVault of Archavon',  description = [[Once used by giants to carry heavily armored warriors, these grand war mammoths can easily bear more than one rider of less-than-giant stature.]], },
	[287] = { name = [[Grand Black War Mammoth]], sourceText = '|cFFFFD200Drop: |rBoss Drop|n|cFFFFD200Location: |rVault of Archavon',  description = [[Rarely, a war mammoth is born stronger and more tractable than its peers. These mammoths are dubbed "grand" and carry more than one rider to the front lines.]], },
	[288] = { name = [[Grand Ice Mammoth]], sourceText = '|cFFFFD200Vendor: |rLillehoff|n|cFFFFD200Zone: |rThe Storm Peaks|n|cFFFFD200Faction: |rThe Sons of Hodir - Exalted|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[At home on the narrow, icy trails of the Storm Peaks, these mammoths are the only mounts the ice giants trust to carry large loads and multiple riders.]], },
	[289] = { name = [[Grand Ice Mammoth]], sourceText = '|cFFFFD200Vendor: |rLillehoff|n|cFFFFD200Zone: |rThe Storm Peaks|n|cFFFFD200Faction: |rThe Sons of Hodir - Exalted|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Due to high winds and avalanches, the Sons of Hodir recommend bringing at least two companions if venturing into the dangerous Storm Peaks.]], },
	[291] = { name = [[Blue Dragonhawk]], sourceText = '|cFFFFD200Achievement: |rMountain o\' Mounts|n|cFFFFD200Category: |rGeneral',  description = [[Dragonhawks are among the deadliest known predators. They are able to spot prey from great distances, rush in, and rend with both talon and beak.]], },
	[292] = { name = [[Red Dragonhawk]], sourceText = '|cFFFFD200Achievement: |rMountain o\' Mounts|n|cFFFFD200Category: |rGeneral',  description = [[Dragonhawks are among the deadliest known predators. They are able to spot prey from great distances, rush in, and rend with both talon and beak.]], },
	[293] = { name = [[Illidari Doomhawk]], sourceText = '|cFFFFD200Drop: |rDoomwalker|n|cFFFFD200Zone: |rTanaris',  description = [[Once a year, a rift in time appears, and strange things occur. The Illidari scouts and Doomwalker were quite surprised to find themselves in Tanaris.]], },
	[294] = { name = [[Stormwind Steed]], sourceText = '|cFFFFD200Vendor: |rCorporal Arthur Flew|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[Although raised in Stormwind, this breed showed unusual aptitude for the cold weather in Icecrown.]], },
	[295] = { name = [[Darkspear Raptor]], sourceText = '|cFFFFD200Vendor: |rSamamba|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[This raptor breed was originally cultivated by the Drakkari, but has since been adopted by the Darkspear tribe in Northrend.]], },
	[296] = { name = [[Ironforge Ram]], sourceText = '|cFFFFD200Vendor: |rDerrick Brindlebeard|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[Wildhammer Fact Checker claims that this specific breed was the first type of ram domesticated by the dwarves when they settled in Dun Morogh.]], },
	[297] = { name = [[Darnassian Nightsaber]], sourceText = '|cFFFFD200Vendor: |rRook Hawkfist|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[When a night elf\'s mount perishes, it is custom for the rider to keep one of the saber\'s great fangs as a token of remembrance.]], },
	[298] = { name = [[Gnomeregan Mechanostrider]], sourceText = '|cFFFFD200Vendor: |rRillie Spindlenut|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[When you absolutely, positively have to be there right now. -Rillie Spindlenut]], },
	[299] = { name = [[Exodar Elekk]], sourceText = '|cFFFFD200Vendor: |rIrisee|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[Exodar Elekk are elekk which have been anointed by O'ros, the Naaru who journeyed from Draenor to Azeroth with Velen and his followers.]], },
	[300] = { name = [[Orgrimmar Wolf]], sourceText = '|cFFFFD200Vendor: |rFreka Bloodaxe|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[For a brief time, many orcs would declare themselves "hungry like an Orgrimmar wolf." Some blood elves still use the phrase ironically.]], },
	[301] = { name = [[Thunder Bluff Kodo]], sourceText = '|cFFFFD200Vendor: |rDoru Thunderhorn|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[An alpha member of the herd, this kodo is a prime example of the mighty creatures which thunder through Mulgore.]], },
	[302] = { name = [[Silvermoon Hawkstrider]], sourceText = '|cFFFFD200Vendor: |rTrellis Morningsun|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[So I says I feel like chicken tonight. Next thing, I am flat out and that thing is trying to see what my eyes taste like. -Bozzle Blastbolt]], },
	[303] = { name = [[Forsaken Warhorse]], sourceText = '|cFFFFD200Vendor: |rEliza Killian|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[When fallen heroes are raised into undeath, so too are their horses.]], },
	[304] = { name = [[Mimiron\'s Head]], sourceText = '|cFFFFD200Drop:|r Yogg-Saron|n|cFFFFD200Location:|r Ulduar',  description = [[A token of titan craftsmanship hoarded by the Old God, Yogg-Saron. Currently the single largest mechanical gnome head in Azeroth.]], },
	[305] = { name = [[Argent Hippogryph]], sourceText = '|cFFFFD200Vendor: |rFaction Quartermasters|n|cFFFFD200Location: |rArgent Tournament, Icecrown|n|cFFFFD200Cost: |r150|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h|n',  description = [[Argent Hippogryphs will partner with only the bravest and most battle-tested champions.]], },
	[306] = { name = [[Ironbound Proto-Drake]], sourceText = '|cFFFFD200Achievement: |rGlory of the Ulduar Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[His skin more hard than brass was found, that sword nor spear could pierce nor wound.]], },
	[307] = { name = [[Rusted Proto-Drake]], sourceText = '|cFFFFD200Achievement: |rGlory of the Ulduar Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Not as shiny as the other one, but It\\'s still a great find lads. Brann Bronzebeard]], },
	[308] = { name = [[Blue Skeletal Warhorse]], sourceText = '|cFFFFD200Legacy|r',  description = [[A rare sight. Most disappeared once Baron Rivendare ruled in Stratholme.]], },
	[309] = { name = [[White Kodo]], sourceText = '|cFFFFD200Vendor: |rHarb Clawhoof|n|cFFFFD200Zone: |rMulgore|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [["The kodo is the backbone of our people. Wherever the Horde goes - in trade, settlement, or war - there you will find the mighty kodo." - Harb Clawhoof]], },
	[310] = { name = [[Black Wolf]], sourceText = '|cFFFFD200Vendor: |rWorld Vendors|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Can howl loudly enough to be heard for miles.]], },
	[311] = { name = [[Venomhide Ravasaur]], sourceText = '|cFFFFD200Quest: |rThey Grow Up So Fast|n|cFFFFD200Zone: |rUn\'Goro Crater',  description = [[You raised this thing from a scaly pup - Mor'vek would be so proud. But he'd still make you clean up the mess it made in the corner.]], },
	[312] = { name = [[Sea Turtle]], sourceText = '|cFFFFD200Profession:|r Fishing|n',  description = [[Often caught by fishermen at a young age, these mounts are much more comfortable in the water than they are on land.]], },
	[313] = { name = [[Deadly Gladiator\'s Frost Wyrm]], sourceText = '|cFFFFD200Legacy|r',  description = [[These reanimated vessels of destruction do not fear death; they dispense it.]], },
	[314] = { name = [[Black Skeletal Horse]], sourceText = '|cFFFFD200Vendor: |rZachariah Post|n|cFFFFD200Zone: |rTirisfal Glades|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The Forsaken believe that without a purpose, even beasts of burden suffer in undeath.]], },
	[317] = { name = [[Furious Gladiator\'s Frost Wyrm]], sourceText = '|cFFFFD200Legacy|r',  description = [[Some of Azeroth\'s mightiest dragons have been summoned across the veil between life and death to serve new masters with undying loyalty.]], },
	[318] = { name = [[Great Red Elekk]], sourceText = '|cFFFFD200Vendor: |rIrisee|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Never seen in Nagrand, the first red elekk calves were born in Azeroth after the Exodar\'s arrival. Some breeders theorize that the unique coloration is somehow caused by the same radiation which ravaged Bloodmyst Isle.]], },
	[319] = { name = [[Swift Moonsaber]], sourceText = '|cFFFFD200Vendor: |rRook Hawkfist|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Many night elves revere these proud mounts because their shimmering coats symbolize the goddess of the moon, Elune.]], },
	[320] = { name = [[Swift Red Hawkstrider]], sourceText = '|cFFFFD200Vendor: |rTrellis Morningsun|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[What do you mean is it dyed? Oh, the bird. We did touch up the red just a little. -Trellis Morningsun]], },
	[321] = { name = [[Swift Gray Steed]], sourceText = '|cFFFFD200Vendor: |rCorporal Arthur Flew|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[A veteran of some of the toughest battles fought by the Argent Crusade.]], },
	[322] = { name = [[Great Golden Kodo]], sourceText = '|cFFFFD200Vendor: |rDoru Thunderhorn|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[An alpha member of the herd, this kodo is a prime example of the mighty creatures which thunder through the Southern Barrens.]], },
	[323] = { name = [[Turbostrider]], sourceText = '|cFFFFD200Vendor: |rRillie Spindlenut|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The mirror\'s not so you can look back, but so your foes can see something besides your tailpipes. -Rillie Spindlenut]], },
	[324] = { name = [[Swift Violet Ram]], sourceText = '|cFFFFD200Vendor: |rDerrick Brindlebeard|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The Barak Tor'ol ram\'s thick bony skull and massive horns make it the ideal mount for charging through groups of armored enemies.]], },
	[325] = { name = [[Swift Purple Raptor]], sourceText = '|cFFFFD200Vendor: |rSamamba|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Longtime allies of the trolls, these jungle hunters form a special bond with their masters. They become mount, guardian, and friend.]], },
	[326] = { name = [[White Skeletal Warhorse]], sourceText = '|cFFFFD200Vendor: |rEliza Killian|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[When fallen heroes are raised into undeath, so too are their horses.]], },
	[327] = { name = [[Swift Burgundy Wolf]], sourceText = '|cFFFFD200Vendor: |rFreka Bloodaxe|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The wolves of the Horde are befriended, not domesticated.]], },
	[328] = { name = [[Magic Rooster]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Magic in the hands of a genius can do wonderful, miraculous things. Magic in the hands of an idiot can make a giant rooster.]], },
	[329] = { name = [[Silver Covenant Hippogryph]], sourceText = '|cFFFFD200Vendor: |rHiren Loresong|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Faction: |rThe Silver Covenant - Exalted|n|cFFFFD200Cost: |r150|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[Silver Covenant Hippogryphs are fiercely loyal, both to their riding partners, and to the Alliance.]], },
	[330] = { name = [[Sunreaver Dragonhawk]], sourceText = '|cFFFFD200Vendor: |rVasarin Redmorn|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Faction: |rThe Sunreavers - Exalted|n|cFFFFD200Cost: |r150|Hcurrency:241|h|TInterface/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[The Sunreavers of Dalaran select only the swiftest and most formidable of dragonhawks as their mounts.]], },
	[331] = { name = [[Quel'dorei Steed]], sourceText = '|cFFFFD200Vendor: |rHiren Loresong|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Faction: |rThe Silver Covenant - Exalted|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[Comes from a lineage of fine horses that once grazed near the shores of the Well of Eternity.]], },
	[332] = { name = [[Sunreaver Hawkstrider]], sourceText = '|cFFFFD200Vendor: |rVasarin Redmorn|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Faction: |rThe Sunreavers - Exalted|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TInterface/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[I demand speed, grace and discipline on the battlefield. This one will serve nicely. -Eressea Dawnsinger]], },
	[333] = { name = [[Magic Rooster]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Magic in the hands of a genius can do wonderful, miraculous things. Magic in the hands of an idiot can make a giant rooster.]], },
	[334] = { name = [[Magic Rooster]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Magic in the hands of a genius can do wonderful, miraculous things. Magic in the hands of an idiot can make a giant rooster.]], },
	[335] = { name = [[Magic Rooster]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Magic in the hands of a genius can do wonderful, miraculous things. Magic in the hands of an idiot can make a giant rooster.]], },
	[336] = { name = [[Ochre Skeletal Warhorse]], sourceText = '|cFFFFD200Vendor: |rZachariah Post|n|cFFFFD200Zone: |rTirisfal Glades|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[When fallen heroes are raised into undeath, so too are their horses.]], },
	[337] = { name = [[Striped Dawnsaber]], sourceText = '|cFFFFD200Vendor: |rLelanai|n|cFFFFD200Zone: |rTeldrassil|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Night elves once believed that seeing this light colored saber breed in the wild was an ill omen.]], },
	[338] = { name = [[Argent Charger]], sourceText = '|cFFFFD200Vendor: |rDame Evniki Kapsalis|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h|n',  description = [[Trained to accept only champions of the Light as a rider.]], },
	[340] = { name = [[Relentless Gladiator\'s Frost Wyrm]], sourceText = '|cFFFFD200Legacy|r',  description = [[In life, it could not be tamed, but necromancy and esoteric binding rituals have now combined to bend this indomitable creature to its rider\'s will.]], },
	[341] = { name = [[Argent Warhorse]], sourceText = '|cFFFFD200Vendor: |rDame Evniki Kapsalis|n|cFFFFD200Zone: |rIcecrown|n|cFFFFD200Cost: |r100|Hcurrency:241|h|TINTERFACE/ICONS/Ability_Paladin_ArtofWar.blp:0|t|h',  description = [[Given only to those who prove themselves a champion in the Argent Tournament.]], },
	[342] = { name = [[Swift Horde Wolf]], sourceText = '|cFFFFD200Legacy|r',  description = [[A few of the Argent Crusade\'s swiftest mounts were awarded to those members of the Horde who performed exceptionally during the tournament in Icecrown.]], },
	[343] = { name = [[Swift Alliance Steed]], sourceText = '|cFFFFD200Legacy|r',  description = [[A few of the Argent Crusade\'s swiftest mounts were awarded to those members of the Alliance who performed exceptionally during the tournament in Icecrown.]], },
	[344] = { name = [[Crusader\'s White Warhorse]], sourceText = '|cFFFFD200Legacy|r',  description = [[Awarded to only the most skilled champions from the Trial of the Crusader, these steeds were chosen for Alliance members from Tirion Fordring\'s own personal stable.]], },
	[345] = { name = [[Crusader\'s Black Warhorse]], sourceText = '|cFFFFD200Legacy|r',  description = [[Awarded to only the most skilled champions from the Trial of the Crusader, these steeds were chosen for Horde members from Tirion Fordring\'s own personal stable.]], },
	[349] = { name = [[Onyxian Drake]], sourceText = '|cFFFFD200Drop: |rOnyxia|n|cFFFFD200Zone: |rOnyxia\'s Lair',  description = [[She has a lot in common with her mother, except for the being killed and reanimated. But she is young still. -Maloriak]], },
	[350] = { name = [[Sunwalker Kodo]], sourceText = '|cFFFFD200Paladin|r',  description = [[This magnificent beast, adorned in sacred charms and painted with holy imagery, radiates with the warmth of the sun.]], },
	[351] = { name = [[Great Sunwalker Kodo]], sourceText = '|cFFFFD200Paladin|r',  description = [[At daybreak, the noble Sunwalkers often perform rituals to bless their mounts, imbuing them with holy magic.]], },
	[352] = { name = [[X-45 Heartbreaker]], sourceText = '|cFFFFD200Drop:|r Heart-Shaped Box',  description = [[Apothecary Hummel painted this masterpiece bright pink for an unrequited love.]], },
	[358] = { name = [[Wrathful Gladiator\'s Frost Wyrm]], sourceText = '|cFFFFD200Legacy|r',  description = [[Many believe that during their early lives, these wyrms were forced to fight their own kind for the amusement of others. Perhaps that is why they possess a particularly combative temperament.]], },
	[363] = { name = [[Invincible]], sourceText = '|cFFFFD200Drop: |rThe Lich King|n|cFFFFD200Zone: |rIcecrown Citadel',  description = [[The famous steed of Arthas Menethil, who serves its master in life and in death.  Riding him is truly a feat of strength.]], },
	[364] = { name = [[Icebound Frostbrood Vanquisher]], sourceText = '|cFFFFD200Achievement: |rGlory of the Icecrown Raider (25 player)|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Raised from the chilling depths of Icecrown, Frostbrood Vanquishers are considered among the fiercest and most relentless of all frost wyrms.]], },
	[365] = { name = [[Bloodbathed Frostbrood Vanquisher]], sourceText = '|cFFFFD200Achievement: |rGlory of the Icecrown Raider (10 player)|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Bloodbathed Vanquishers are so named when it is determined that a reanimated frost wyrm died a particularly gruesome and bloody death.]], },
	[366] = { name = [[Crimson Deathcharger]], sourceText = '|cFFFFD200Quest: |rMograine\'s Reunion|n|cFFFFD200Zone: |rIcecrown Citadel',  description = [[Stabled in the Icecrown Citadel to serve the Lich King\\'s most favored minions.]], },
	[367] = { name = [[Exarch\'s Elekk]], sourceText = '|cFFFFD200Legacy|r',  description = [[Draenei exarchs have long marched into war atop these imposing mounts, bred for their razor-sharp tusks.]], },
	[368] = { name = [[Great Exarch\'s Elekk]], sourceText = '|cFFFFD200Quest|r |n',  description = [[Only the most distinguished paladins are given the honor of riding this elekk. Its plated armor and purple adornments have been blessed by Prophet Velen himself.]], },
	[371] = { name = [[Blazing Hippogryph]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Hippogryphs are highly intelligent, sentient beings; far more than mere "mounts."]], },
	[372] = { name = [[Wooly White Rhino]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Captured by Toby "Mother Goose" Ironbolt on the Borean Tundra, this shaggy beast carries an enormous grudge after being bridled by a gnome.]], },
	[373] = { name = [[Vashj'ir Seahorse]], sourceText = '|cFFFFD200Quest: |rThe Abyssal Ride|n|cFFFFD200Zone: |rKelp\'thar Forest',  description = [[Stubborn until broken, these underwater mounts provide the swiftest means of travel around the depths of Vashj'ir.]], },
	[375] = { name = [[Frosty Flying Carpet]], sourceText = '|cFFFFD200Profession: |rTailoring',  description = [["I remember my first flying carpet... yeti hair and burlap.  Not nearly as nice as this one here." - Frozo the Renowned]], },
	[376] = { name = [[Celestial Steed]], sourceText = '|cFFFFD200Trading Post|r',  description = [[A beacon of hope; a guiding light in the darkness.]], },
	[382] = { name = [[X-53 Touring Rocket]], sourceText = '|cFFFFD200Recruit-a-Friend |r',  description = [[Riding a giant cylinder filled with highly explosive goblin rocket fuel all by yourself is just crazy...so bring a friend.]], },
	[386] = { name = [[Fossilized Raptor]], sourceText = '|cFFFFD200Profession: |rArchaeology',  description = [[Legends tell of a spell placed upon a hunting raptor who dared to snarl at young Tyrande Whisperwind as she bathed in a jungle pool. The beast is cursed to wander Azeroth -  until it apologizes.]], },
	[388] = { name = [[Goblin Trike]], sourceText = '|cFFFFD200Vendor: |rKall Worthaton|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[A compilation of nuts, bolts, gears, and treated wood paneling make for an unusually smooth ride.]], },
	[389] = { name = [[Goblin Turbo-Trike]], sourceText = '|cFFFFD200Vendor: |rKall Worthaton|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The turbo trike\'s tri-wheel design adds superior stability and a smaller turn radius over its two wheeled cousins.]], },
	[391] = { name = [[Volcanic Stone Drake]], sourceText = '|cFFFFD200Achievement: |rGlory of the Cataclysm Hero|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Fire can transform stone in exciting ways, but never let the Stonemother know I said that. -Earthcaller Yevaa]], },
	[392] = { name = [[Drake of the East Wind]], sourceText = '|cFFFFD200Achievement: |rGlory of the Cataclysm Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[In Deathwing\'s mad quest to forge new dragons, he ignored amazing breeds never before seen on Azeroth. -Thrall]], },
	[393] = { name = [[Phosphorescent Stone Drake]], sourceText = '|cFFFFD200Drop:|r Aeonaxx|n|cFFFFD200Location:|r Deepholm|n',  description = [[The Stonemother has shown little concern for the rebellious Aeonaxx, even allowing his spawn to be taken from her realm.]], },
	[394] = { name = [[Drake of the West Wind]], sourceText = '|cFFFFD200Vendor: |rQuartermaster Brazie|n|cFFFFD200Zone: |rTol Barad Peninsula|n|cFFFFD200Faction: |rBaradin\'s Wardens - Exalted',  description = [[Harnessing the elemental powers of the storm, these majestic beasts crackle like lightning and roar like thunder.]], },
	[395] = { name = [[Drake of the North Wind]], sourceText = '|cFFFFD200Drop: |rAltairus|n|cFFFFD200Zone: |rThe Vortex Pinnacle',  description = [[They sicken of the calm who know the storm.]], },
	[396] = { name = [[Drake of the South Wind]], sourceText = '|cFFFFD200Drop: |rAl\'Akir|n|cFFFFD200Zone: |rThrone of the Four Winds',  description = [[Gentle as a summer breeze, but that says little considering where it came from. -Itesh]], },
	[397] = { name = [[Vitreous Stone Drake]], sourceText = '|cFFFFD200Drop: |rSlabhide|n|cFFFFD200Zone: |rThe Stonecore',  description = [[Those would be perfect in a pendant, and that would make a fantastic ring. Let me know if this thing sheds or molts or whatever. -Kalinda]], },
	[398] = { name = [[Brown Riding Camel]], sourceText = '|cFFFFD200Vendor: |rBlacksmith Abasi|n|cFFFFD200Zone: |rUldum|n|cFFFFD200Faction: |rRamkahen - Exalted|n|cFFFFD200Cost: |r100|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Fennimore Quigley is credited with milking the first camel and breaking the record for "longest unintentional flight," all in the same afternoon.]], },
	[399] = { name = [[Tan Riding Camel]], sourceText = '|cFFFFD200Vendor: |rBlacksmith Abasi|n|cFFFFD200Zone: |rUldum|n|cFFFFD200Faction: |rRamkahen - Exalted|n|cFFFFD200Cost: |r100|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Fennimore Quigley is credited with milking the first camel and breaking the record for "longest unintentional flight," all in the same afternoon.]], },
	[400] = { name = [[Grey Riding Camel]], sourceText = '|cFFFFD200Drop: |rDormus the Camel-Hoarder|n|cFFFFD200Zone: |rFeralas',  description = [[Fennimore Quigley is credited with milking the first camel and breaking the record for "longest unintentional flight," all in the same afternoon.]], },
	[401] = { name = [[Dark Phoenix]], sourceText = '|cFFFFD200Vendor: |rGuild Vendors|n|cFFFFD200Cost: |r3000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Despite their name, Dark Phoenixes are known to have a warm and cheerful disposition.]], },
	[403] = { name = [[Golden King]], sourceText = '|cFFFFD200Vendor: |rWorld Vendors|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Don't let the saddle fool you, he\'s wild, not a tame lion.]], },
	[404] = { name = [[Ultramarine Qiraji Battle Tank]], sourceText = '|cFFFFD200Profession: |rArchaeology',  description = [[The Tol'vir sorcerer Azj'Aqir was said to possess a scepter that would allow him to control a Qiraj battle tank outside of its Silithus home.]], },
	[405] = { name = [[Spectral Steed]], sourceText = '|cFFFFD200Vendor: |rQuartermaster Brazie|n|cFFFFD200Zone: |rTol Barad Peninsula|n|cFFFFD200Faction: |rBaradin\'s Wardens - Exalted|n|cFFFFD200Cost: |r165|Hcurrency:391|h|Tinterface/ICONS/achievement_zone_tolbarad.blp:0|t|h',  description = [[Dark spirits flee before the radiance of its presence.]], },
	[406] = { name = [[Spectral Wolf]], sourceText = '|cFFFFD200Vendor: |rPogg|n|cFFFFD200Zone: |rTol Barad Peninsula|n|cFFFFD200Faction: |rHellscream\'s Reach - Exalted|n|cFFFFD200Cost: |r165|Hcurrency:391|h|TInterface/ICONS/Achievement_Zone_TolBarad.blp:0|t|h',  description = [[Fears only fire.]], },
	[407] = { name = [[Sandstone Drake]], sourceText = '|cFFFFD200Profession: |rAlchemy',  description = [[Sandstone Drakes were studied in great detail long ago by the famed alchemist Walleye Sprysprocket. It has even been said that Walleye developed potions to mimic the drakes' physiology.]], },
	[408] = { name = [[Mottled Drake]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay|n',  description = [[I thought the thing was ill until it took to the air. It flies as true as any of my own. -Alexstrasza]], },
	[409] = { name = [[Kor'kron Annihilator]], sourceText = '|cFFFFD200Vendor: |rGoram|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Vicious in battle and surprisingly loyal, this battle scorpion serves only those who show true commitment to a powerful guild.]], },
	[410] = { name = [[Armored Razzashi Raptor]], sourceText = '|cFFFFD200Drop: |rBloodlord Mandokir|n|cFFFFD200Zone: |rZul\'Gurub',  description = [[The purebred royal raptors of Zul'Gurub, these mounts have become a rare sight in Stranglethorn Vale. The Gurubashi have been known to fly into a rage at the sight of any non-troll riding one of their precious beasts.]], },
	[411] = { name = [[Swift Zulian Panther]], sourceText = '|cFFFFD200Drop: |rHigh Priestess Kilnara|n|cFFFFD200Zone: |rZul\'Gurub',  description = [[The jungle trolls have long coveted panther fangs, using them for rituals or as ingredients in mojos.]], },
	[412] = { name = [[Amani Dragonhawk]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Dragonhawks liberated from troll enslavement are considered among the most loyal of all their kin.]], },
	[413] = { name = [[Flameward Hippogryph]], sourceText = '|cFFFFD200Achievement: |rThe Molten Front Offensive|n|cFFFFD200Category: |rCataclysm',  description = [[Flameward Hippogryphs revel in the defeat of Ragnaros, Lord of Fire.]], },
	[415] = { name = [[Pureblood Fire Hawk]], sourceText = '|cFFFFD200Drop: |rRagnaros|n|cFFFFD200Zone: |rFirelands',  description = [[Legends state that the Pureblood line may be traced back to the very creation of the Firelands.]], },
	[416] = { name = [[Felfire Hawk]], sourceText = '|cFFFFD200Achievement:|r Mountacular|n|n',  description = [[Felfire is deadly to the touch.  Luckily, the hawk can suppress the flames, choosing not to incinerate its rider in an emerald effigy.]], },
	[417] = { name = [[Corrupted Fire Hawk]], sourceText = '|cFFFFD200Achievement: |rGlory of the Firelands Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Millagazor was a fire hawk loyal to Ragnaros. At this time it is unknown how many of Millagazor\\'s eggs were corrupted.]], },
	[418] = { name = [[Savage Raptor]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay|n',  description = [[One-time companion of Kanga the Primal, the fallen troll hero. The raptor\\'s name is unknown, for Kanga only spoke to it in bear form.]], },
	[419] = { name = [[Amani Battle Bear]], sourceText = '|cFFFFD200Drop:|r Kasha\'s Bag',  description = [[The Amani trolls decorate these ferocious mounts in magic amulets and ceremonial masks as a way to venerate the bear god Nalorakk.]], },
	[420] = { name = [[Subdued Seahorse]], sourceText = '|cFFFFD200Drop: |rPoseidus|n|cFFFFD200Zone: |rVashj\'ir',  description = [["One has not truly lived until they have felt the tidal current rushing through their hair while atop a majestic seahorse," said no one.  Ever.]], },
	[421] = { name = [[Winged Guardian]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[Unearthed recently in an ancient tomb, this mighty winged lion may have once served one of the ancient Tol'vir princes.]], },
	[422] = { name = [[Vicious War Steed]], sourceText = '|cFFFFD200Achievement: |rVeteran of the Alliance II|n|cFFFFD200Category: |rRated Battleground',  description = [[Armored to withstand collisions in battle.]], },
	[423] = { name = [[Vicious War Wolf]], sourceText = '|cFFFFD200Achievement: |rVeteran of the Horde II|n|cFFFFD200Category: |rRated Battleground',  description = [[At Warchief Hellscream\'s command, Horde trainers began raising wolves to thrive in the shock and ferocity of sustained combat.]], },
	[424] = { name = [[Vicious Gladiator\'s Twilight Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[Heroes who proved their might in the gladiatorial arenas during the Cataclysm were awarded the most exceptional of the twilight drake broods.]], },
	[425] = { name = [[Flametalon of Alysrazor]], sourceText = '|cFFFFD200Drop: |rAlysrazor|n|cFFFFD200Zone: |rFirelands',  description = [[As with their matriarch, Alysrazor, the fury and fervor of these elemental avians are unquenchable.]], },
	[426] = { name = [[Swift Shorestrider]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[A rarity amongst the tallstrider breeds of Kalimdor, but easily recognized by its pungent dead seaweed aroma.]], },
	[428] = { name = [[Ruthless Gladiator\'s Twilight Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[Heroes who proved their might in the gladiatorial arenas during the Cataclysm were awarded the most exceptional of the twilight drake broods.]], },
	[429] = { name = [[Swift Forest Strider]], sourceText = '|cFFFFD200Vendor: |rLhara|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r180 |Hcurrency:515|h|Tinterface/ICONS/INV_misc_ticket_darkmoon_01.blp:0|t|h|n',  description = [[This vibrant breed can only be found in the forests of Darkmoon Island.]], },
	[430] = { name = [[Swift Springstrider]], sourceText = '|cFFFFD200Vendor: |rNoblegarden Merchant|n|cFFFFD200Zone: |rDurotar, Eversong Woods, Mulgore, Tirisfal Glades, Azuremyst Isle, |nDun Morogh, Elwynn Forest, Teldrassil|n|cFFFFD200Cost: |r500|Hitem:44791|h|TINTERFACE/ICONS/ACHIEVEMENT_NOBLEGARDEN_CHOCOLATE_EGG.BLP:0|t|h|n',  description = [[The joys of Noblegarden are best when celebrated with this loyal, loving, and blandly-colored freak of nature.]], },
	[431] = { name = [[Swift Lovebird]], sourceText = '|cFFFFD200World Event:|r Love is in the Air|n|n|cFFFFD200Vendor: |rLovely Merchant (Alliance)|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r270|Hitem:49927|h|TINTERFACE/ICONS/INV_VALENTINESCARD01.BLP:0|t|h|n|n|cFFFFD200Vendor: |rLovely Merchant (Horde)|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r270|Hitem:49927|h|TINTERFACE/ICONS/INV_VALENTINESCARD01.BLP:0|t|h',  description = [[It\\'s like red but not quite.]], },
	[432] = { name = [[White Riding Camel]], sourceText = '|cFFFFD200Vendor:|r Landro Longshot|n|cFFFFD200Location:|r Booty Bay|n',  description = [[Fennimore Quigley is credited with milking the first camel and breaking the record for "longest unintentional flight," all in the same afternoon.]], },
	[433] = { name = [[Corrupted Hippogryph]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[It is a sad fact that even the wise and majestic hippogryph may fall prey to corruption.]], },
	[434] = { name = [[Darkmoon Dancing Bear]], sourceText = '|cFFFFD200Vendor: |rLhara|n|cFFFFD200Zone: |rDarkmoon Island|n|cFFFFD200Cost: |r180|Hcurrency:515|h|TINTERFACE/ICONS/INV_Misc_Ticket_Darkmoon_01.blp:0|t|h|n|n',  description = [[These fearsome mounts have a habit of suddenly breaking out into dance amid battle, much to the annoyance of their riders.]], },
	[435] = { name = [[Mountain Horse]], sourceText = '|cFFFFD200Vendor: |rAstrid Langstrump|n|cFFFFD200Zone: |rDarnassus|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Images of this breed are often found on kegs of watered-down dwarven brew.]], },
	[436] = { name = [[Swift Mountain Horse]], sourceText = '|cFFFFD200Vendor: |rAstrid Langstrump|n|cFFFFD200Zone: |rDarnassus|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Longs for the day it may return to its home in Gilneas.]], },
	[439] = { name = [[Tyrael\'s Charger]], sourceText = '|cFFFFD200Trading Post|r',  description = [[A symbol of justice throughout all known realms.]], },
	[440] = { name = [[Spectral Gryphon]], sourceText = '|cFFFFD200Promotion:|r Scroll of Resurrection|n',  description = [[When you said an old friend was coming back, this was not quite what I expected. -Bralla Cloudwing]], },
	[441] = { name = [[Spectral Wind Rider]], sourceText = '|cFFFFD200Promotion:|r Scroll of Resurrection|n',  description = [[How do you not fall right through that thing? -Maztha]], },
	[442] = { name = [[Blazing Drake]], sourceText = '|cFFFFD200Drop:|r Elementium Fragment|n|cFFFFD200Location:|r Dragon Soul',  description = [[Blazing Drakes exhibit a boundless passion for life, and are considered by many to be among the most loyal mounts on Azeroth.]], },
	[443] = { name = [[Twilight Harbinger]], sourceText = '|cFFFFD200Achievement: |rGlory of the Dragon Soul Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Whatever dark magic once compelled the Twilight Harbinger is now broken... or is it?]], },
	[444] = { name = [[Life-Binder\'s Handmaiden]], sourceText = '|cFFFFD200Drop:|r Elementium Fragment|n|cFFFFD200Location:|r Dragon Soul|n|n',  description = [[Alexstrasza, the former dragon Aspect of Life, is known to be a kind and patient mentor. Under her tutelage, only the most dedicated pupils acquire the title of "Life-Binder\'s Handmaiden."]], },
	[445] = { name = [[Experiment 12-B]], sourceText = '|cFFFFD200Drop:|r Lesser Cache of the Aspects|n|cFFFFD200Location:|r Dragon Soul|n',  description = [[Some believe Experiment 12-B is the result of a covert SI-7 initiative, others consider it the outcome of embyronic tinkering by goblin scientists. No matter the case, this much is certain: given its strength, speed and agility, Experiment 12-B was a success!]], },
	[446] = { name = [[Heart of the Aspects]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[You are Azeroth\'s true guardians and the future of this world is in your hands, for the dawning of the age of mortals has begun. - Alexstrasza]], },
	[447] = { name = [[Feldrake]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay',  description = [[Fighting the Burning Legion millennia ago, this valiant drake fell to corruption after its body was infused with raw demonic magic.]], },
	[448] = { name = [[Jade Cloud Serpent]], sourceText = '|cFFFFD200Quest: |rRiding the Skies|n|cFFFFD200Zone: |rThe Jade Forest',  description = [[Jade Cloud Serpents are light and agile; quick and deadly.]], },
	[449] = { name = [[Azure Water Strider]], sourceText = '|cFFFFD200Vendor: |rNat Pagle|n|cFFFFD200Zone: |rKrasarang Wilds|n|cFFFFD200Faction: |rThe Anglers - Exalted|n|cFFFFD200Cost: |r1000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [["Normally bugs are for bait, but how could you look into its eyes and even think that?" -Nat Pagle]], },
	[450] = { name = [[Pandaren Kite]], sourceText = '|cFFFFD200Achievement: |rPandaren Ambassador|n|cFFFFD200Category: |rPandaria',  description = [[This "mount," awarded by the Lorewalkers of Pandaria, is a reward to the hero of their organization.]], },
	[451] = { name = [[Jeweled Onyx Panther]], sourceText = '|cFFFFD200Profession:|r Jewelcrafting|n',  description = [[Trade Prince Gallywix recently ordered dozens of these glittering jewel-encrusted mounts to roam the grounds of his pleasure palace.]], },
	[452] = { name = [[Green Dragon Turtle]], sourceText = '|cFFFFD200Vendor: |rTurtlemaster Odai|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rOld Whitenose|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r1|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Cherished for their heavy carrying capacity and steady gait, these stalwart beasts are the mount of choice for most native Pandaren.]], },
	[453] = { name = [[Great Red Dragon Turtle]], sourceText = '|cFFFFD200Vendor: |rTurtlemaster Odai|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rOld Whitenose|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r10|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [["Packaged upgrades include; dual-lantern fog lights, emergency brew keg, and genuine Tanji leather seat."  - Turtlemaster Odai]], },
	[454] = { name = [[Cindermane Charger]], sourceText = '|cFFFFD200Recruit-a-Friend|r',  description = [[Formed by Ragnaros himself as a gift for the daughter he never had.]], },
	[455] = { name = [[Obsidian Nightwing]], sourceText = '|cFFFFD200Recruit-a-Friend|r ',  description = [[When you can't run, you crawl, and when you can't crawl you find someone to carry you.]], },
	[456] = { name = [[Sapphire Panther]], sourceText = '|cFFFFD200Profession: |rJewelcrafting',  description = [[Legends say that an ancient pandaren emperor crafted the first jeweled panther for his son after the boy\'s twin brother died of an illness.]], },
	[457] = { name = [[Jade Panther]], sourceText = '|cFFFFD200Profession:|r Jewelcrafting|n',  description = [[Legends say that an ancient pandaren emperor crafted the first jeweled panther for his son after the boy\'s twin brother died of an illness.]], },
	[458] = { name = [[Ruby Panther]], sourceText = '|cFFFFD200Profession: |rJewelcrafting',  description = [[Legends say that an ancient pandaren emperor crafted the first jeweled panther for his son after the boy\'s twin brother died of an illness.]], },
	[459] = { name = [[Sunstone Panther]], sourceText = '|cFFFFD200Profession: |rJewelcrafting',  description = [[Legends say that an ancient pandaren emperor crafted the first jeweled panther for his son after the boy\'s twin brother died of an illness.]], },
	[460] = { name = [[Grand Expedition Yak]], sourceText = '|cFFFFD200Vendor: |rUncle Bigpocket|n|cFFFFD200Zone: |rKun-Lai Summit|n|cFFFFD200Cost: |r120000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[These beasts of burden are known to carry over five times their own weight and to be capable of lasting several days without food or water.]], },
	[462] = { name = [[White Riding Yak]], sourceText = '|cFFFFD200Legacy|r',  description = [[Riding Yaks are bred to be slimmer and more agile than their kin.]], },
	[463] = { name = [[Amber Scorpion]], sourceText = '|cFFFFD200Vendor: |rAmbersmith Zikk|n|cFFFFD200Zone: |rDread Wastes|n|cFFFFD200Faction: |rThe Klaxxi - Exalted|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The Klaxxi ride massive scorpions into battle against their enemies.  A carapace hardened by amber provides superior protection while on the battlefield.]], },
	[464] = { name = [[Azure Cloud Serpent]], sourceText = '|cFFFFD200Quest: |rRiding the Skies|n|cFFFFD200Zone: |rThe Jade Forest',  description = [[Azure Cloud Serpents are believed by the Pandaren to be harbingers of good fortune.]], },
	[465] = { name = [[Golden Cloud Serpent]], sourceText = '|cFFFFD200Quest: |rRiding the Skies|n|cFFFFD200Zone: |rThe Jade Forest',  description = [[Golden Cloud Serpents are a treasure to be enjoyed by all who visit the continent of Pandaria.]], },
	[466] = { name = [[Thundering Jade Cloud Serpent]], sourceText = '|cFFFFD200Vendor: |rShay Pressler (Alliance)|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r3000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rGoram (Horde)|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r3000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[From their infancy, Thundering Cloud Serpents are drawn to storms. They have been known to chase lightning through the blackened, roiling clouds, and to bathe in the pouring rain.]], },
	[467] = { name = [[Cataclysmic Gladiator\'s Twilight Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[Heroes who proved their might in the gladiatorial arenas during the Cataclysm were awarded the most exceptional of the twilight drake broods.]], },
	[468] = { name = [[Imperial Quilen]], sourceText = '|cFFFFD200Promotion:|r Mists of Pandaria Collector\'s Edition',  description = [[Favorite pets of the ancient Mogu princes, Quilen make loyal and fearless companions and mounts.]], },
	[469] = { name = [[Depleted-Kyparium Rocket]], sourceText = '|cFFFFD200Profession: |rEngineering',  description = [[When Kyparite is depleted of its amber essence it becomes fairly resistant to the corrosion from rocket fuel.]], },
	[470] = { name = [[Geosynchronous World Spinner]], sourceText = '|cFFFFD200Profession: |rEngineering',  description = [[This rocket was originally designed to speed up the rotation of the world making for shorter nights.  Gnomes with insomnia can have lofty goals.]], },
	[471] = { name = [[Onyx Cloud Serpent]], sourceText = '|cFFFFD200Quest:|r Surprise Attack!|n|cFFFFD200Location:|r Townlong Steppes|n',  description = [[Onyx Cloud serpents are known to be more aggressive and daring than their cousins, the Heavenly Onyx Cloud Serpents.]], },
	[472] = { name = [[Crimson Cloud Serpent]], sourceText = '|cFFFFD200Achievement: |rGlory of the Pandaria Hero|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Crimson Cloud Serpents are given to flights of fancy, striking out on a whim for seemingly random journeys, sometimes for days at a time.]], },
	[473] = { name = [[Heavenly Onyx Cloud Serpent]], sourceText = '|cFFFFD200Drop:|r Sha of Anger|n|cFFFFD200Location:|r Kun-Lai Summit',  description = [[Heavenly Onyx Cloud Serpents are born with violet scales covering the entirety of their body. Over time, these scales darken to the color of onyx.]], },
	[474] = { name = [[Heavenly Crimson Cloud Serpent]], sourceText = '|cFFFFD200Achievement: |rGlory of the Pandaria Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Heavenly Crimson Cloud Serpents are considered to be among the noblest of dragons.]], },
	[475] = { name = [[Heavenly Golden Cloud Serpent]], sourceText = '|cFFFFD200Vendor: |rMistweaver Ku|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Faction: |rEmperor Shaohao - Exalted',  description = [[Warmth and hope emanate from this great creature, which glows like a sunrise over the mountains.]], },
	[476] = { name = [[Yu'lei, Daughter of Jade]], sourceText = '|cFFFFD200Vendor: |rMistweaver Xia|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVEcurrency-justice.BLP:0|t|h',  description = [[Warmth and wisdom emanate from this great creature, which glows like moonlight passing through the finest jade.]], },
	[477] = { name = [[Heavenly Azure Cloud Serpent]], sourceText = '|cFFFFD200Achievement: |rLord of the Reins|n|cFFFFD200Category: |rMounts',  description = [[Warmth and serenity emanate from this great creature, which glows like the cloudless sky of a summer afternoon.]], },
	[478] = { name = [[Astral Cloud Serpent]], sourceText = '|cFFFFD200Drop:|r Cache of Pure Energy',  description = [[It is believed that Astral Cloud Serpents possess answers to some of life\'s greatest mysteries. Pity they can't talk.]], },
	[479] = { name = [[Azure Riding Crane]], sourceText = '|cFFFFD200Vendor: |rJaluu the Generous|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n|cFFFFD200Faction: |rGolden Lotus - Exalted|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The majestic crane is a treasured friend of the pandaren.]], },
	[480] = { name = [[Golden Riding Crane]], sourceText = '|cFFFFD200Vendor: |rJaluu the Generous|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n|cFFFFD200Faction: |rGolden Lotus - Exalted|n|cFFFFD200Cost: |r2500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The crane is a symbol of wisdom and nobility, their ability to carry us into the skies is nothing short of miraculous. - Jaluu the Generous]], },
	[481] = { name = [[Regal Riding Crane]], sourceText = '|cFFFFD200Vendor: |rJaluu the Generous|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n|cFFFFD200Faction: |rGolden Lotus - Exalted|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The crane is a symbol of wisdom and nobility. Their ability to carry us into the skies is nothing short of miraculous. - Jaluu the Generous]], },
	[484] = { name = [[Black Riding Yak]], sourceText = '|cFFFFD200Legacy|r',  description = [[Grummles traveling Kun-Lai Summit by night prefer to ride this dark-haired yak to remain unseen, though they remove the bells from its caparison first.]], },
	[485] = { name = [[Brown Riding Yak]], sourceText = '|cFFFFD200Legacy|r',  description = [[Riding Yaks are bred to be slimmer and more agile than their kin.]], },
	[486] = { name = [[Grey Riding Yak]], sourceText = '|cFFFFD200Vendor: |rUncle Bigpocket|n|cFFFFD200Zone: |rKun-Lai Summit|n|cFFFFD200Cost: |r3000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Riding Yaks are bred to be slimmer and more agile than their kin.]], },
	[487] = { name = [[Blonde Riding Yak]], sourceText = '|cFFFFD200Vendor: |rUncle Bigpocket|n|cFFFFD200Zone: |rKun-Lai Summit|n|cFFFFD200Cost: |r3000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Riding Yaks are bred to be slimmer and more agile than their kin.]], },
	[488] = { name = [[Crimson Water Strider]], sourceText = '|cFFFFD200Vendor: |rNat Pagle|n|cFFFFD200Zone: |rGarrison: Fishing Shack|n|cFFFFD200Faction: |rNat Pagle - Honored|n|cFFFFD200Cost: |r100|Hitem:117397|h|TINTERFACE/ICONS/INV_Misc_Coin_19.blp:0|t|h',  description = [["Normally bugs are for bait, but how could you look into its eyes and even think that?" -Nat Pagle]], },
