	[503] = { name = [[Crimson Pandaren Phoenix]], sourceText = '|cFFFFD200Vendor: |rKai Featherfall|n|cFFFFD200Location: |rVale of Eternal Blossoms|n|cFFFFD200Cost: |r1|Hitem:90045|h|TINTERFACE/ICONS/INV_MISC_PHOENIXEGG.BLP:0|t|h',  description = [[The plumage of the Crimson Phoenix is said to represent prosperity and happiness.]], },
	[504] = { name = [[Thundering August Cloud Serpent]], sourceText = '|cFFFFD200Vendor: |rSage Whiteheart (Alliance)|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n|cFFFFD200Faction: |rThe August Celestials - Exalted|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rSage Lotusbloom (Horde)|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n|cFFFFD200Faction: |rThe August Celestials - Exalted|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[From their infancy, Thundering Cloud Serpents are drawn to storms. They have been known to chase lightning through the blackened, roiling clouds, and to bathe in the pouring rain.]], },
	[505] = { name = [[Green Shado-Pan Riding Tiger]], sourceText = '|cFFFFD200Vendor: |rRushi the Fox|n|cFFFFD200Zone: |rTownlong Steppes|n|cFFFFD200Faction: |rShado-Pan - Exalted|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [["When waking a tiger, use a long stick. Better yet, tell your friend to wake the tiger instead." --Pandaren Proverb]], },
	[506] = { name = [[Blue Shado-Pan Riding Tiger]], sourceText = '|cFFFFD200Vendor: |rRushi the Fox|n|cFFFFD200Zone: |rTownlong Steppes|n|cFFFFD200Faction: |rShado-Pan - Exalted|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [["When waking a tiger, use a long stick. Better yet, tell your friend to wake the tiger instead." --Pandaren Proverb]], },
	[507] = { name = [[Red Shado-Pan Riding Tiger]], sourceText = '|cFFFFD200Vendor: |rRushi the Fox|n|cFFFFD200Zone: |rTownlong Steppes|n|cFFFFD200Faction: |rShado-Pan - Exalted|n|cFFFFD200Cost: |r2500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [["When waking a tiger, use a long stick. Better yet, tell your friend to wake the tiger instead." --Pandaren Proverb]], },
	[508] = { name = [[Brown Riding Goat]], sourceText = '|cFFFFD200Vendor: |rGina Mudclaw|n|cFFFFD200Zone: |rValley of the Four Winds|n|cFFFFD200Faction: |rThe Tillers - Exalted|n|cFFFFD200Cost: |r500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[It takes unflagging patience and a gentle touch to train obstinate mountain goats for riding.]], },
	[509] = { name = [[Red Flying Cloud]], sourceText = '|cFFFFD200Vendor: |rTan Shin Tiao|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n|cFFFFD200Faction: |rThe Lorewalkers - Exalted|n|cFFFFD200Cost: |r600|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[An ancient Mogu artifact awarded to those who are deeply committed to the discovery and sharing of Pandaren lore.]], },
	[510] = { name = [[White Riding Goat]], sourceText = '|cFFFFD200Vendor: |rGina Mudclaw|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Faction: |rThe Tillers - Exalted|n|cFFFFD200Cost: |r3250|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rGina Mudclaw|n|cFFFFD200Zone: |rValley of the Four Winds|n|cFFFFD200Faction: |rThe Tillers - Exalted|n|cFFFFD200Cost: |r3250|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[It takes unflagging patience and a gentle touch to train obstinate mountain goats for riding.]], },
	[511] = { name = [[Black Riding Goat]], sourceText = '|cFFFFD200Vendor: |rGina Mudclaw|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Faction: |rThe Tillers - Exalted|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rGina Mudclaw|n|cFFFFD200Zone: |rValley of the Four Winds|n|cFFFFD200Faction: |rThe Tillers - Exalted|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[It takes unflagging patience and a gentle touch to train obstinate mountain goats for riding.]], },
	[515] = { name = [[Son of Galleon]], sourceText = '|cFFFFD200Drop:|r Galleon|n|cFFFFD200Location:|r Valley of the Four Winds|n',  description = [[Trained from a young age, these massive kushan are taught to thunder into battle carrying a full saurok warband.]], },
	[516] = { name = [[Pandaren Kite]], sourceText = '|cFFFFD200Achievement: |rPandaren Ambassador|n|cFFFFD200Category: |rPandaria',  description = [[This token of honor is a gift from the Lorewalkers of Pandaria. Flying manual seemingly not included, however.]], },
	[517] = { name = [[Thundering Ruby Cloud Serpent]], sourceText = '|cFFFFD200Drop: |rAlani|n|cFFFFD200Zone: |rVale of Eternal Blossoms',  description = [[From their infancy, Thundering Cloud Serpents are drawn to storms. They have been known to chase lightning through the blackened, roiling clouds, and to bathe in the pouring rain.]], },
	[518] = { name = [[Ashen Pandaren Phoenix]], sourceText = '|cFFFFD200Vendor: |rWorld Vendors|n|cFFFFD200Cost: |r1|Hitem:90045|h|TINTERFACE/ICONS/INV_MISC_PHOENIXEGG.BLP:0|t|h',  description = [[Both the name and color of this majestic phoenix are believed to symbolize rebirth.]], },
	[519] = { name = [[Emerald Pandaren Phoenix]], sourceText = '|cFFFFD200Vendor: |rWorld Vendors|n|cFFFFD200Cost: |r1|Hitem:90045|h|TINTERFACE/ICONS/INV_MISC_PHOENIXEGG.BLP:0|t|h',  description = [[The colors of this rare phoenix\'s plumage are said to bring great fortune.]], },
	[520] = { name = [[Violet Pandaren Phoenix]], sourceText = '|cFFFFD200Vendor: |rWorld Vendors|n|cFFFFD200Cost: |r1|Hitem:90045|h|TINTERFACE/ICONS/INV_MISC_PHOENIXEGG.BLP:0|t|h',  description = [[The Violet Phoenix\'s plumage is said to symbolize a union of opposing forces.]], },
	[521] = { name = [[Jade Pandaren Kite]], sourceText = '|cFFFFD200Achievement: |rWe\'re Going to Need More Saddles|n|cFFFFD200Category: |rGeneral',  description = [[The residents of Pandaria salute your enthusiasm for wildlife preservation. They do have concerns about housing 150 animals per visit, though.]], },
	[522] = { name = [[Sky Golem]], sourceText = '|cFFFFD200Profession: |rEngineering',  description = [[A glorious feat of engineering, the sky golem takes traditional shredder technology to a whole new level (and altitude!).]], },
	[523] = { name = [[Swift Windsteed]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[One of Pandaria\'s most elusive creatures, said to bring good fortune to its rider.]], },
	[526] = { name = [[Grand Armored Gryphon]], sourceText = '|cFFFFD200Vendor: |rAgent Malley|n|cFFFFD200Zone: |rKrasarang Wilds|n|cFFFFD200Faction: |rOperation: Shieldwall - Exalted|n|cFFFFD200Cost: |r2000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[This noble gryphon is as staunch a battle companion as any member of the Alliance could ask for.]], },
	[527] = { name = [[Grand Armored Wyvern]], sourceText = '|cFFFFD200Vendor: |rTuskripper Grukna|n|cFFFFD200Zone: |rKrasarang Wilds|n|cFFFFD200Faction: |rDominance Offensive - Exalted|n|cFFFFD200Cost: |r2000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[This ferocious beast, renowned among wyvern handlers, was one of the first mounts chosen to take part in the Horde\'s Dominance Offensive.]], },
	[528] = { name = [[Grand Gryphon]], sourceText = '|cFFFFD200Quest: |rThe Silence|n|cFFFFD200Zone: |rKun-Lai Summit',  description = [[Gryphons' keen eyesight allows them to see over vast distances and warn their riders of any dangers that lie ahead.]], },
	[529] = { name = [[Grand Wyvern]], sourceText = '|cFFFFD200Quest: |rBreath of Darkest Shadow|n|cFFFFD200Zone: |rKun-Lai Summit',  description = [[Wyverns hail from some of the most inhospitable regions of Kalimdor, making them masters of adapting to any environment.]], },
	[530] = { name = [[Armored Skyscreamer]], sourceText = '|cFFFFD200Achievement: |rGlory of the Thundering Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Only those heroic souls who surmount every challenge the mogu tyrants place before them can proudly fly the skies on a war-prepared skyscreamer.]], },
	[531] = { name = [[Spawn of Horridon]], sourceText = '|cFFFFD200Drop: |rHorridon|n|cFFFFD200Zone: |rThrone of Thunder',  description = [[When you see a hurricane coming, you get out of the way. Horridon would fight the hurricane and win.]], },
	[532] = { name = [[Ghastly Charger]], sourceText = '|cFFFFD200Vendor: |rLandro Longshot|n|cFFFFD200Location:|r Booty Bay|n',  description = [[Not all of Medivh\'s victims are gone for good.]], },
	[533] = { name = [[Cobalt Primordial Direhorn]], sourceText = '|cFFFFD200Drop:|r Oondasta|n|cFFFFD200Location:|r Isle of Giants',  description = [["When ya fight alongside raptors, ya want a mount dey can't eat." -Unknown dinomancer]], },
	[534] = { name = [[Amber Primordial Direhorn]], sourceText = '|cFFFFD200Drop: |rZandalari Warbringer|n|cFFFFD200Zone: |rDread Wastes, Kun-Lai Summit, Townlong Steppes, Valley of the Four Winds',  description = [[There is an old Zandalari saying about wild direhorns: "Run!"]], },
	[535] = { name = [[Slate Primordial Direhorn]], sourceText = '|cFFFFD200Drop: |rZandalari Warbringer|n|cFFFFD200Zone: |rDread Wastes, Krasarang Wilds, Kun-Lai Summit, Townlong Steppes, Valley of the Four Winds',  description = [[The masks aren't to control the direhorn; they are so no one sees your face when it turns on you.]], },
	[536] = { name = [[Jade Primordial Direhorn]], sourceText = '|cFFFFD200Drop: |rZandalari Warbringer|n|cFFFFD200Zone: |rDread Wastes, Kun-Lai Summit, Townlong Steppes, Valley of the Four Winds',  description = [[The festive coloring belies deadly intent.]], },
	[537] = { name = [[Bone-White Primal Raptor]], sourceText = '|cFFFFD200Quest: |rA Mountain of Giant Dinosaur Bones|n|cFFFFD200Zone: |rIsle of Giants',  description = [["They are lethal at eight months, very lethal." -Hemet Nesingwary Jr.]], },
	[538] = { name = [[Red Primal Raptor]], sourceText = '|cFFFFD200Drop:|r Cracked Primal Egg',  description = [[Raptors have spread to nearly all of the lands of Azeroth. It is uncertain whether the trolls led them or followed them.]], },
	[539] = { name = [[Black Primal Raptor]], sourceText = '|cFFFFD200Drop:|r Cracked Primal Egg',  description = [[Speed, cunning, and ferocity are the things trolls and raptors admire about each other.]], },
	[540] = { name = [[Green Primal Raptor]], sourceText = '|cFFFFD200Drop:|r Cracked Primal Egg',  description = [["Raptors are as loyal as they are vicious. If you ain't friend, you are food." -Kijara]], },
	[541] = { name = [[Malevolent Gladiator\'s Cloud Serpent]], sourceText = '|cFFFFD200Legacy|r',  description = [[These cloud serpents have scales that... are the color of clouds, actually.]], },
	[542] = { name = [[Thundering Cobalt Cloud Serpent]], sourceText = '|cFFFFD200Drop:|r Nalak|n|cFFFFD200Location:|r Isle of Thunder|n',  description = [[This is a descendant of the irascible Nalak. Lightning erupts from its scales and arcs over the saddle. Dare you sit?]], },
	[543] = { name = [[Clutch of Ji-Kun]], sourceText = '|cFFFFD200Drop: |rJi-Kun|n|cFFFFD200Zone: |rThrone of Thunder',  description = [[Although Ji-Kun feasted on the failed creations of mogu flesh-shapers, her progeny is willing to eat more pedestrian carcasses.]], },
	[544] = { name = [[Armored Bloodwing]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[This monstrous, flesh-eating bat is the undisputed ruler of the night skies.]], },
	[545] = { name = [[Golden Primal Direhorn]], sourceText = '|cFFFFD200Vendor: |rHiren Loresong|n|cFFFFD200Zone: |rIsle of Thunder|n|cFFFFD200Faction: |rKirin Tor Offensive - Exalted|n|cFFFFD200Cost: |r3000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Sharp at both ends and crafty in the middle. Leave it to the trolls to think this was a good idea.]], },
	[546] = { name = [[Crimson Primal Direhorn]], sourceText = '|cFFFFD200Vendor: |rVasarin Redmorn|n|cFFFFD200Zone: |rIsle of Thunder|n|cFFFFD200Faction: |rSunreaver Onslaught - Exalted|n|cFFFFD200Cost: |r3000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Leave it to the blood elves to pick the red one.]], },
	[547] = { name = [[Hearthsteed]], sourceText = '|cFFFFD200Promotion:|r Hearthstone|n',  description = [[Each step echoes with the rage of your three opponents.]], },
	[548] = { name = [[Armored Red Dragonhawk]], sourceText = '|cFFFFD200Achievement: |rMount Parade|n|cFFFFD200Category: |rGeneral',  description = [[This rare breed of dragonhawk is a mark of honor in the Horde, gifted by the blood elves. It reflects your tireless effort to amass a small army of mounts.]], },
	[549] = { name = [[Armored Blue Dragonhawk]], sourceText = '|cFFFFD200Achievement: |rMount Parade|n|cFFFFD200Category: |rGeneral',  description = [[After you amassed a massive menagerie of 200 mounts, the animal authorities of the Alliance sent you a means to keep them in line (and out of their hair).]], },
	[550] = { name = [[Brawler\'s Burly Mushan Beast]], sourceText = '|cFFFFD200Vendor: |rPaul North|n|cFFFFD200Zone: |rBrawl\'gar Arena|n|cFFFFD200Cost: |r1500|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[For years pandaren believed these creatures to be untamable. Just ask the first pandaren to do so, Henshi "Limp Along" Homebrew.]], },
	[551] = { name = [[Enchanted Fey Dragon]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[This majestic creature is Brightwing\'s older brother. He affirms that she was, in fact, adopted.]], },
	[552] = { name = [[Ironbound Wraithcharger]], sourceText = '|cFFFFD200Vendor: |rAuzin|n|cFFFFD200Zone: |rDalaran|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h',  description = [[Its ethereal spirit is barely contained by its iron shell. The penultimate creation of Salanar the Horseman, these powerful steeds were once thought to have all been destroyed.]], },
	[554] = { name = [[Vicious Kaldorei Warsaber]], sourceText = '|cFFFFD200Vendor: |rNecrolord Sipe|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r1|Hitem:103533|h|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t|h',  description = [[The blades adorning this mount\'s armor are designed to both inspire fear and pre-slice meals for the warsaber.]], },
	[555] = { name = [[Vicious Skeletal Warhorse]], sourceText = '|cFFFFD200Vendor: |rDeathguard Netharian|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r1|Hitem:103533|h|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t|h',  description = [[Only rewarded to those who have bested their foes in battle.]], },
	[557] = { name = [[Spawn of Galakras]], sourceText = '|cFFFFD200Achievement: |rGlory of the Orgrimmar Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[This formidable creature\'s lineage may be traced to Galakras, and yet further to Galakrond, one of the most infamous of all proto-dragons.]], },
	[558] = { name = [[Kor'kron War Wolf]], sourceText = '|cFFFFD200Achievement: |rAhead of the Curve: Garrosh Hellscream (10/25 player)|n|cFFFFD200Category: |rFeats of Strength',  description = [[The Kor'kron use only the mightiest wolves for their mounts, bedecking them in armor that would break the backs of lesser creatures.]], },
	[559] = { name = [[Kor'kron Juggernaut]], sourceText = '|cFFFFD200Drop: |rGarrosh Hellscream|n|cFFFFD200Location:|r Siege of Orgrimmar',  description = [[The Kor'kron Juggernaut is a weapon of unbridled destruction, born from the mind of Garrosh Hellscream and made real by his engineers.]], },
	[560] = { name = [[Ashhide Mushan Beast]], sourceText = '|cFFFFD200Vendor: |rSpeaker Gulan|n|cFFFFD200Zone: |rTimeless Isle|n|cFFFFD200Cost: |r500|Hcurrency:789|h|TINTERFACE/ICONS/TimelessCoin-Bloody.blp:0|t|h',  description = [[Some people say that riding an ashhide mushan will result in favorable health and increased prosperity. These people are called "mount vendors."]], },
	[561] = { name = [[Thundering Onyx Cloud Serpent]], sourceText = '|cFFFFD200Drop:|r Huolon|n|cFFFFD200Location:|r Timeless Isle',  description = [[This ebony cloud serpent is a lightning-wreathed vision of such rarity as to have driven many mortals to madness.]], },
	[562] = { name = [[Tyrannical Gladiator\'s Cloud Serpent]], sourceText = '|cFFFFD200Legacy|r',  description = [[These cloud serpents always look elegant and slim in classic black.]], },
	[563] = { name = [[Grievous Gladiator\'s Cloud Serpent]], sourceText = '|cFFFFD200Legacy|r',  description = [[These brilliantly colored cloud serpents have scales that are a mixture of ruby, gold, and nearly violet.]], },
	[564] = { name = [[Prideful Gladiator\'s Cloud Serpent]], sourceText = '|cFFFFD200Legacy|r',  description = [[These brilliantly colored cloud serpents have scales that range from gold to pink. Or we could call it coral.]], },
	[568] = { name = [[Emerald Hippogryph]], sourceText = '|cFFFFD200Recruit-a-Friend|r',  description = [[These majestic animals come in various colorings, but the emerald variety can only be tamed by the magic of friendship.]], },
	[571] = { name = [[Iron Skyreaver]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[Iron Horde beastmasters bred this rylak for war and then girded it for battle in black steel and smoldering flame.]], },
	[593] = { name = [[Warforged Nightmare]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[A nightmarish steed of hardened steel and vengeful spirits, this companion will follow you to the depths of the Firelands and back without complaint.]], },
	[594] = { name = [[Grinning Reaver]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[The difference between a smile and a grin is the difference between a kiss and tasting.]], },
	[600] = { name = [[Dread Raven]], sourceText = '|cFFFFD200Promotion:|r Warlords of Draenor Collector\'s Edition|n',  description = [[Filled with the hatred of an ancient god, the dread raven are the Arakkoa\'s most trusted guardians.]], },
	[603] = { name = [[Creeping Carpet]], sourceText = '|cFFFFD200Profession:|r Tailoring',  description = [[Can't fly, but can creep very quickly.]], },
	[606] = { name = [[Core Hound]], sourceText = '|cFFFFD200Achievement:|r Boldly, You Sought the Power of Ragnaros|n|cFFFFD200Category:|r Feats of Strength',  description = [[Molten to the core!]], },
	[607] = { name = [[Gorestrider Gronnling]], sourceText = '|cFFFFD200Achievement:|r Glory of the Draenor Raider',  description = [[Once "befriended", a gronnling will happily carry its rider to their death.]], },
	[608] = { name = [[Witherhide Cliffstomper]], sourceText = '|cFFFFD200Vendor: |rTrader Araanda|n|cFFFFD200Zone: |rLunarfall|n|cFFFFD200Cost: |r20000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTrader Darakk|n|cFFFFD200Zone: |rFrostwall|n|cFFFFD200Cost: |r20000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Tiny notches cover the hooves of this variation of clefthoof, allowing it to safely scale mountainous regions in search of food.]], },
	[609] = { name = [[Trained Icehoof]], sourceText = '|cFFFFD200Source:|r Garrison Stables|n',  description = [[It wasn't until you owned this mount that you understood how much the world of Draenor would test you. Clefthoof hair gets in everything!]], },
	[611] = { name = [[Tundra Icehoof]], sourceText = '|cFFFFD200Drop:|r Vengeance, Deathtalon, Terrorfist, Doomroller|n|cFFFFD200Zone:|r Tanaan Jungle|n',  description = [[A translucent layer of down hair covers this breed\'s leathery hide, acting as the perfect insulation for cold environments.]], },
	[612] = { name = [[Bloodhoof Bull]], sourceText = '|cFFFFD200Drop:|r Nakk the Thunderer|n|cFFFFD200Location:|r Nagrand',  description = [[Members of the Warsong clan have long hunted these clefthoof to use their crimson-hued skin for battle standards.]], },
	[613] = { name = [[Ironhoof Destroyer]], sourceText = '|cFFFFD200Drop:|r Blackhand|n|cFFFFD200Location:|r Blackrock Foundry (Mythic)',  description = [[Blackhand is said to have personally designed the fiery armaments used on the Iron Horde\'s clefthoof battle mounts.]], },
	[614] = { name = [[Mottled Meadowstomper]], sourceText = '|cFFFFD200Drop:|r Luk\'hok|n|cFFFFD200Zone:|r Nagrand',  description = [[The elekk\'s long trunk allows it to sniff out sources of food, avoid danger, and steal snacks from your bags when you're not looking.]], },
	[615] = { name = [[Trained Meadowstomper]], sourceText = '|cFFFFD200Quest:|r Garrison Stables',  description = [[The draenei perfected elekk husbandry long ago, adorning the creatures with Light-infused crystals to make them more docile.]], },
	[616] = { name = [[Shadowhide Pearltusk]], sourceText = '|cFFFFD200Drop:|r Invader\'s Forgotten Treasure',  description = [[Some legends say the draenei first befriended the elekk after one of the beasts saved an injured scout from a pack of vicious podlings.]], },
	[617] = { name = [[Dusty Rockhide]], sourceText = '|cFFFFD200Vendor:|r Vindicator Nuurem|n|cFFFFD200Faction:|r Council of Exarchs - Exalted|n|cFFFFD200Location:|r Stormshield|n|cFFFFD200Cost:|r 5,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h',  description = [[Ever since acquiring this mount, you have been plagued by strange fever dreams of hippety-hoppety clippety-cloppety elekk on parade.]], },
	[618] = { name = [[Armored Irontusk]], sourceText = '|cFFFFD200Vendor:|r |n|cFFFFD200Faction:|r Sha\'tari Defense - Exalted|n|cFFFFD200Location:|r Garrison Trading Post|n|cFFFFD200Cost:|r 5,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h',  description = [[It is said an elekk never forgets. No wonder it despises the Iron Horde, considering the torment the orcs put the beast through.]], },
	[619] = { name = [[Blacksteel Battleboar]], sourceText = '|cFFFFD200Achievement:|r Guild Glory of the Draenor Raider|n',  description = [[Refuses to run unless its head is covered in heavy, spiked metal. It\\'s an ego thing.]], },
	[620] = { name = [[Rocktusk Battleboar]], sourceText = '|cFFFFD200Vendor: |rTrader Araanda|n|cFFFFD200Zone: |rLunarfall|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTrader Darakk|n|cFFFFD200Zone: |rFrostwall|n|cFFFFD200Cost: |r10000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[It\\'s not the metal helmet that attracts mates; It\\'s the sweet mohawk.]], },
	[621] = { name = [[Armored Frostboar]], sourceText = '|cFFFFD200Achievement:|r Advanced Husbandry|n',  description = [[Subsists almost entirely on a diet of frozen berries. Though it finds slow off-worlders equally tasty.]], },
	[622] = { name = [[Armored Razorback]], sourceText = '|cFFFFD200Drop:|r Vengeance, Deathtalon, Terrorfist, Doomroller|n|cFFFFD200Zone:|r Tanaan Jungle|n',  description = [[Never... ever... EVER... let it smell bacon on you.]], },
	[623] = { name = [[Frostplains Battleboar]], sourceText = '|cFFFFD200Achievement:|r Glory of the Draenor Hero',  description = [[If it didn't have the cool tusk ring, it would feel so silly in that outfit.]], },
	[624] = { name = [[Wild Goretusk]], sourceText = '|cFFFFD200Vendor: |rZ\'tenga the Walker|n|cFFFFD200Zone: |rTanaan Jungle|n|cFFFFD200Faction: |rThe Saberstalkers - Honored|n|cFFFFD200Cost: |r1000|Hitem:124099|h|TInterface/ICONS/INV_misc_blacksaberonfang.blp:0|t|h',  description = [[If you want a gentle ride, go find a "tame happytusk" or something.]], },
	[625] = { name = [[Domesticated Razorback]], sourceText = '|cFFFFD200Vendor:|r Gazrix Gearlock / Mimi Wizzlebub|n|cFFFFD200Faction:|r Steamwheedle "Preservation" Society - Exalted|n|cFFFFD200Location:|r Stormshield / Warspear|n|cFFFFD200Cost:|r 5,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h|n',  description = [["Domesticated" does not mean "friendly." Bring some treats.]], },
	[626] = { name = [[Giant Coldsnout]], sourceText = '|cFFFFD200Drop:|r Invader\'s Forgotten Treasure',  description = [[Its snout is not just cold but wet. Not a pleasant way to wake up.]], },
	[627] = { name = [[Great Greytusk]], sourceText = '|cFFFFD200Drop:|r Gorok|n|cFFFFD200Zone:|r Frostfire Ridge|n',  description = [[Sheds its tusks once a year. Is extremely shy until they grow back.]], },
	[628] = { name = [[Trained Rocktusk]], sourceText = '|cFFFFD200Quest:|r Garrison Stables',  description = [[Thoroughly trained in how to snort, trot, and stomp on the faces of its enemies.]], },
	[629] = { name = [[Trained Riverwallow]], sourceText = '|cFFFFD200Quest:|r Garrison Stables',  description = [[Riverwallows are one of the heartiest beasts on Draenor, resulting in their increased stamina and distinctive coloring.]], },
	[630] = { name = [[Sapphire Riverbeast]], sourceText = '|cFFFFD200Drop:|r Silthide|n|cFFFFD200Zone:|r Talador',  description = [[This animal\'s color mimics the clear blue of Draenor\'s river shallows, making it almost invisible to aquatic predators.]], },
	[631] = { name = [[Mudback Riverbeast]], sourceText = '|cFFFFD200Garrison Mission:|r It\'s a Boat, It\'s a Plane, It\'s... Just a Riverbeast.|n',  description = [[Special webbing that grows on the soles of the mudback\'s feet gives them extra traction in mud and slick terrain.]], },
	[632] = { name = [[Mosshide Riverwallow]], sourceText = '|cFFFFD200Vendor:|r Dawn-Seeker Krek / Dawn-Seeker Alkset|n|cFFFFD200Location:|r Stormshield / Warspear|n|cFFFFD200Cost:|r 50,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h',  description = [[Aggressive and territorial, the mosshide is known for hiding in river-shore scrub and ambushing unwary passersby.]], },
	[633] = { name = [[Hellfire Infernal]], sourceText = '|cFFFFD200Drop:|r Gul\'dan (Mythic)|n|cFFFFD200Zone:|r The Nighthold',  description = [[From the bowels of the nether is summoned a creature of pure hatred, bent on burning down any life in its way.]], },
	[634] = { name = [[Solar Spirehawk]], sourceText = '|cFFFFD200Drop:|r Rukhmar|n',  description = [[Looking into the eyes of this mystical beast is like peering into the heart of the sun. Goggles are recommended when you're riding.]], },
	[635] = { name = [[Shadowmane Charger]], sourceText = '|cFFFFD200Vendor:|r Ravenspeaker Skeega / Shadow-Sage Brakoss|n|cFFFFD200Faction:|r Arakkoa Outcasts - Exalted|n|cFFFFD200Location:|r Stormshield / Warspear|n|cFFFFD200Cost:|r 5,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h|n',  description = [[Draenei poets have likened this talbuk\'s coloring to shadows cast by moonlight in an ashen forest.]], },
	[636] = { name = [[Swift Breezestrider]], sourceText = '|cFFFFD200Drop:|r Pathrunner|n|cFFFFD200Zone:|r Shadowmoon Valley|n',  description = [[Draenei have relied on this mount, the swiftest of all talbuk, to relay messengers and their missives safely to their destinations.]], },
	[637] = { name = [[Trained Silverpelt]], sourceText = '|cFFFFD200Location:|r Garrison Stables',  description = [[Prized for its intelligence, this breed of talbuk can be trained to execute defensive kicks and gorings in battle.]], },
	[638] = { name = [[Breezestrider Stallion]], sourceText = '|cFFFFD200Vendor:|r Dazzerian|n|cFFFFD200Faction:|r Vol\'jin\'s Spear - Exalted|n|cFFFFD200Location:|r Warspear|n|cFFFFD200Cost:|r 5,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h|n',  description = [[These breezestriders' red coloration reflects their temperament: sanguine, fierce, and courageous.]], },
	[639] = { name = [[Pale Thorngrazer]], sourceText = '|cFFFFD200Vendor:|r Crafticus Mindbender|n|cFFFFD200Faction:|r Wrynn\'s Vanguard - Exalted|n|cFFFFD200Location:|r Stormshield|n|cFFFFD200Cost:|r 5,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h',  description = [[This talbuk subsists completely on a diet of thistles and thorns, making its insides as tough as its outside.]], },
	[640] = { name = [[Vicious War Ram]], sourceText = '|cFFFFD200Vendor: |rNecrolord Sipe|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r1|Hitem:103533|h|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t|h',  description = [[Until it head-butts you below the belt, it has not truly accepted you as its rider.]], },
	[641] = { name = [[Vicious War Raptor]], sourceText = '|cFFFFD200Vendor: |rDeathguard Netharian|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r1|Hitem:103533|h|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t|h',  description = [[Because sharp teeth and claws weren't enough.]], },
	[642] = { name = [[Garn Steelmaw]], sourceText = '|cFFFFD200Drop:|r Invader\'s Forgotten Treasure',  description = [[A trainer once tried to put decorative metal in his steelmaw wolf\'s mouth. He had to wait three days to get it back.]], },
	[643] = { name = [[Warsong Direfang]], sourceText = '|cFFFFD200Drop:|r Vengeance, Deathtalon, Terrorfist, Doomroller|n|cFFFFD200Zone:|r Tanaan Jungle|n',  description = [[Has a will of iron but is a sweetheart if you give it a clefthoof bone to chew on.]], },
	[644] = { name = [[Armored Frostwolf]], sourceText = '|cFFFFD200Achievement:|r The Stable Master|n',  description = [[It\\'s trained to survive the harsh climate of Frostfire Ridge, so it snuggles as if its life depended on it.]], },
	[645] = { name = [[Ironside Warwolf]], sourceText = '|cFFFFD200Vendor:|r Kil\'rip|n|cFFFFD200Faction:|r Laughing Skull - Exalted|n|cFFFFD200Location:|r Horde Garrison Trading Post|n|cFFFFD200Cost:|r 5,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h',  description = [[It has an armored spike on its tail. Scratch its belly at your own risk.]], },
	[646] = { name = [[Coldflame Infernal]], sourceText = '|cFFFFD200Trading Post|r ',  description = [[The strange icy flames cause you to sweat and shiver at the same time.]], },
	[647] = { name = [[Trained Snarler]], sourceText = '|cFFFFD200Quest:|r Garrison Stables',  description = [[Enormously cute as a puppy. Slightly less so now.]], },
	[648] = { name = [[Swift Frostwolf]], sourceText = '|cFFFFD200Vendor:|r Beska Redtusk|n|cFFFFD200Faction:|r Frostwolf Orcs - Exalted|n|cFFFFD200Location:|r Warspear|n|cFFFFD200Cost:|r 5,000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t + 5,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h',  description = [[Amidst the cold heights of Frostfire, the frostwolves hunt anything they can catch. The fastest among them eat well.]], },
	[649] = { name = [[Smoky Direwolf]], sourceText = '|cFFFFD200Drop:|r Invader\'s Forgotten Treasure',  description = [[Believed to reside mostly in the north. Hates weddings.]], },
	[650] = { name = [[Dustmane Direwolf]], sourceText = '|cFFFFD200Profession:|r Leatherworking|n',  description = [[Cautious by direwolf standards, the dustmane can only be coaxed to ride with something it considers special.]], },
	[651] = { name = [[Warlord\'s Deathwheel]], sourceText = '|cFFFFD200Achievement:|r Warlord\'s Deathwheel|n|cFFFFD200Category:|r Feats of Strength',  description = [[Often referred to as the "Goblin Lawn Mower", this growling behemoth treats enemies like they are made of grass... ready to be mown down.]], },
	[652] = { name = [[Champion\'s Treadblade]], sourceText = '|cFFFFD200Vendor:|r Paulie|n|cFFFFD200Zone:|r Stormwind, Old Town|n|cFFFFD200Cost: |r100000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Forged by human, gnome, and dwarf, this armored masterpiece is designed to slice a tauren into a tri-tip dinner with a flick of the handlebars.]], },
	[654] = { name = [[Challenger\'s War Yeti]], sourceText = '|cFFFFD200Achievement:|r Challenge Warlord: Silver|n',  description = [[Thankfully they move fast and mostly leave the smell behind them.]], },
	[655] = { name = [[Sunhide Gronnling]], sourceText = '|cFFFFD200Drop:|r Poundfist|n|cFFFFD200Zone:|r Gorgrond',  description = [[Loves the sun, hates everything else.]], },
	[657] = { name = [[Garn Nighthowl]], sourceText = '|cFFFFD200Drop:|r Nok-Karosh|n|cFFFFD200Location:|r Frostfire Ridge',  description = [[The garn are the most fierce predators on Draenor, and always hunt in packs.]], },
	[663] = { name = [[Bloodfang Widow]], sourceText = '|cFFFFD200Vendor:|r The Mad Merchant|n',  description = [[Its long, serrated limbs drip with the blood of its enemies and/or mate.]], },
	[664] = { name = [[Emerald Drake]], sourceText = '|cFFFFD200Achievement:|r Drake Collector|n|cFFFFD200Category:|r Mounts',  description = [["Is the emerald drake rare?  You have no idea.  If I had a silver for every time someone asked me about one, I could have retired years ago." --Mei Francis]], },
	[678] = { name = [[Chauffeured Mechano-Hog]], sourceText = '|cFFFFD200Achievement:|r Heirloom Hoarder|n',  description = [[Why drive yourself around when you can hire someone to do it for you?]], },
	[679] = { name = [[Chauffeured Mekgineer\'s Chopper]], sourceText = '|cFFFFD200Achievement:|r Heirloom Hoarder',  description = [[Transportation is a precise business.]], },
	[682] = { name = [[Voidtalon of the Dark Star]], sourceText = '|cFFFFD200Drop: |rVoidtalon Egg',  description = [[Corrupted by the presence of the Dark Star in Draenor, the Voidtalons resisted being broken by the Shadowmoon Clan and were ultimately destroyed by Ner'zhul. Only a single egg remains, lost in the void.]], },
	[741] = { name = [[Mystic Runesaber]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[Infused with arcane power, the Mystic Runesaber has long been hidden by chaotic nether energies. Recently they have emerged from the shadows in search of ley lines to sate their thirst for magic.]], },
	[751] = { name = [[Felsteel Annihilator]], sourceText = '|cFFFFD200Drop: |rArchimonde|n|cFFFFD200Location:|r Hellfire Citadel|n|cFFFFD200Difficulty:|r Mythic|n',  description = [[Forged deep within Hellfire Citadel by a mad blacksmith, the Felsteel Annihilator combines the metalworking knowledge of the Iron Horde with the chaotic fel energies of the Burning Legion into an unholy union of steel and flame.]], },
	[753] = { name = [[Corrupted Dreadwing]], sourceText = '|cFFFFD200Vendor: |rDawn-Seeker Krisek|n|cFFFFD200Zone: |rTanaan Jungle|n|cFFFFD200Faction: |rOrder of the Awakened - Friendly|n|cFFFFD200Cost:|r 150,000|Hcurrency:823|h|Tinterface/ICONS/INV_apexis_draenor.blp:0|t|h',  description = [[Bathed in the blood of demons, these corrupted Dread Ravens are significantly more aggressive, and will often kill one another if kept in confinement.]], },
	[755] = { name = [[Vicious War Mechanostrider]], sourceText = '|cFFFFD200Vendor: |rNecrolord Sipe|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r1|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t',  description = [[Equipped with head-mounted Critter Destroyer 9000.]], },
	[756] = { name = [[Vicious War Kodo]], sourceText = '|cFFFFD200Vendor: |rDeathguard Netharian|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r1|Hitem:103533|h|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t|h',  description = [[The spiked ball on the tail is strong enough to crush through even the most reinforced Alliance defenses.]], },
	[758] = { name = [[Infernal Direwolf]], sourceText = '|cFFFFD200Achievement:|r Glory of the Hellfire Raider',  description = [[A mighty wolf of Draenor, touched by the fel energies unleashed by Gul'dan in Tanaan Jungle. It has not gone completely mad... yet.]], },
	[759] = { name = [[Primal Gladiator\'s Felblood Gronnling]], sourceText = '|cFFFFD200Achievement: |rGladiator: Warlords Season 1|n|cFFFFD200Category: |rFeats of Strength',  description = [[One of the gronnlings of Tanaan Jungle caught in the eruption of fel energies unleashed by Gul'dan, and now beholden to an unquenchable lust for chaos.]], },
	[760] = { name = [[Wild Gladiator\'s Felblood Gronnling]], sourceText = '|cFFFFD200Achievement: |rGladiator: Warlords Season 2|n|cFFFFD200Category: |rFeats of Strength',  description = [[One of the gronnlings of Tanaan Jungle caught in the eruption of fel energies unleashed by Gul'dan, and now beholden to an unquenchable lust for chaos.]], },
	[761] = { name = [[Warmongering Gladiator\'s Felblood Gronnling]], sourceText = '|cFFFFD200Achievement: |rGladiator: Warlords Season 3|n|cFFFFD200Category: |rFeats of Strength',  description = [[One of the gronnlings of Tanaan Jungle caught in the eruption of fel energies unleashed by Gul'dan, and now beholden to an unquenchable lust for chaos.]], },
	[762] = { name = [[Coalfist Gronnling]], sourceText = '|cFFFFD200Garrison Mission:|r Breaker Two',  description = [[This little gronnling is well-known for smashing his way into garrison inns and making a mess of the pots and pans.]], },
	[763] = { name = [[Illidari Felstalker]], sourceText = '|cFFFFD200Promotion:|r Legion Collector\'s Edition|n',  description = [[The Illidari have broken some lesser demons for use against their Legion masters.]], },
	[764] = { name = [[Grove Warden]], sourceText = '|cFFFFD200Quest: |rDark Waters|n|cFFFFD200Zone: |rMoonglade',  description = [[These magical companions are the wardens of peaceful, primordial groves within the Emerald Dream.]], },
	[765] = { name = [[Bristling Hellboar]], sourceText = '|cFFFFD200Vendor: |rZ\'tenga the Walker|n|cFFFFD200Zone: |rTanaan Jungle|n|cFFFFD200Faction: |rThe Saberstalkers - Exalted|n|cFFFFD200Cost: |r5000|Hitem:124099|h|TInterface/ICONS/INV_misc_blacksaberonfang.blp:0|t|h',  description = [[The one and only source for the rare culinary ingredient: cursed bacon.]], },
	[767] = { name = [[Charger]], sourceText = '|cFFFFD200Paladin|r',  description = [[Redeemed by the Light. Devoted to your cause.]], },
	[768] = { name = [[Deathtusk Felboar]], sourceText = '|cFFFFD200Vendor: |rShadow Hunter Denjai|n|cFFFFD200Zone: |rTanaan Jungle|n|cFFFFD200Faction: |rVol\'jin\'s Headhunters - Exalted|n|n|cFFFFD200Vendor: |rVindicator Krethos|n|cFFFFD200Zone: |rTanaan Jungle|n|cFFFFD200Faction: |rHand of the Prophet - Exalted',  description = [[Veterans of the battle for Outland\'s Hellfire Penninsula are traumatized by thoughts of these beasts to this day.]], },
	[769] = { name = [[Minion of Grumpus]], sourceText = '|cFFFFD200World Event:|r Feast of Winter Veil',  description = [[No one quite knows when the legend of Grumpus began in Draenor, but his toys are just the worst.]], },
	[772] = { name = [[Soaring Skyterror]], sourceText = '|cFFFFD200Achievement:|r Draenor Pathfinder',  description = [[The extra-light equipment on this chromatic rylak allows it to swoop and glide through the clouds easily, free from the bounds of the land below.]], },
	[928] = { name = [[Dune Scavenger]], sourceText = '|cFFFFD200Zone:|r Vol\'dun|n',  description = [[These resilient scavengers can survive for months in the wastes of Vol\'dun with scarce access to food and water.]], },
	[773] = { name = [[Grove Defiler]], sourceText = '|cFFFFD200Achievement: |rGlory of the Legion Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[The Grove Wardens of the Emerald Dream were some of the first to fall to the corrupting influence of the Nightmare.]], },
	[775] = { name = [[Prestigious War Steed]], sourceText = '|cFFFFD200Achievement:|r Free For All, More For Me|n',  description = [[Armored to withstand collisions in battle.]], },
	[776] = { name = [[Swift Spectral Rylak]], sourceText = '|cFFFFD200Legacy|r',  description = [[Even the dead need a lift every now and then.]], },
	[778] = { name = [[Eclipse Dragonhawk]], sourceText = '|cFFFFD200Vendor: |rCupri|n|cFFFFD200Zone: |rShattrath City|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h',  description = [[Captured from the renegade blood elves at Eclipse Point, these purple dragonhawks were often sought after by Scryers posted to the Sanctum of the Stars.]], },
	[779] = { name = [[Spirit of Eche'ro]], sourceText = '|cFFFFD200Profession:|r Archaeology',  description = [[The spirit of Huln Highmountain\'s pet moose.]], },
	[780] = { name = [[Felsaber]], sourceText = '|cFFFFD200Class:|r Demon Hunter',  description = [[A special creation of Lord Illidan gifted to his most trusted demon hunters.]], },
	[781] = { name = [[Infinite Timereaver]], sourceText = '|cFFFFD200World Event:|r Timewalking Dungeons',  description = [[Fickle and savage, these drakes slip in and out of time until they find a worthy rider.]], },
