	[1011] = { name = [[Shu-Zen, the Divine Sentinel]], sourceText = '|cFFFFD200In-Game Shop|r |n',  description = [[As the Golden Lotus worked tirelessly to repair the Vale of Eternal Blossoms, a single statue came to life and wordlessly defended them from the Sha that assaulted them. Believed by some to be a sign of good fortune and others to be a gift from the Light, Shu-Zen provides faithful companionship to any that would defend Azeroth from the maddening whispers of the Old Gods.]], },
