	[1054] = { name = [[Teldrassil Hippogryph]], sourceText = '|cFFFFD200Quest: |rFrom the Ashes...',  description = [[One of the last of the great armored hippogryphs to escape the burning of Teldrassil, it carried the last few citizens it could to safety before collapsing of exhaustion.]], },
