	[1169] = { name = [[Surf Jelly]], sourceText = '|cFFFFD200Drop:|r Island Expeditions|n',  description = [[The time between being stung and being enveloped and devoured is short enough to consider not swimming in the Great Sea.]], },
	[1172] = { name = [[Conqueror\'s Scythemaw]], sourceText = '|cFFFFD200Achievement: |rConqueror of Azeroth|n|cFFFFD200Category: |rWorld',  description = [[The battle-hardened shell of this mighty beast can turn aside even the most hefty hammer or sharpened spear.]], },
	[1173] = { name = [[Broken Highland Mustang]], sourceText = '|cFFFFD200Drop: |rKnight-Captain Aldrin|n|cFFFFD200Zone: |rArathi Highlands',  description = [[One of the Arathi horses, taken and broken by the Horde conquerors of Stromgarde.]], },
	[1174] = { name = [[Highland Mustang]], sourceText = '|cFFFFD200Drop: |rDoomrider Helgrim|n|cFFFFD200Zone: |rArathi Highlands',  description = [[A valiant steed on the Arathi Highlands, it knows no fear.]], },
	[1175] = { name = [[Twilight Avenger]], sourceText = '|cFFFFD200Drop:|r Island Expeditions|n',  description = [[Little is known about the fate of the twilight dragonflight, though they seem to exist in numbers in Grim Batol to this day.]], },
	[1176] = { name = [[Craghorn Chasm-Leaper]], sourceText = '|cFFFFD200Drop:|r Island Expeditions|n',  description = [[These mighty leapers have adapted to island life by learning to catch seagulls swooping through crags and fissures.]], },
	[1178] = { name = [[Qinsho\'s Eternal Hound]], sourceText = '|cFFFFD200Drop:|r Island Expeditions|n',  description = [[Qinsho wrought his eternal guardian from pure gold and imbued it with the resilience of the earth.]], },
	[1179] = { name = [[Palehide Direhorn]], sourceText = '|cFFFFD200Vendor: |rTalutu|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r500000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTricky Nick|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r500000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Pale-skinned direhorns are quite rare, justifying the expense.]], },
	[1180] = { name = [[Swift Albino Raptor]], sourceText = '|cFFFFD200Drop: |rBeastrider Kama|n|cFFFFD200Zone: |rArathi Highlands',  description = [[The Witherbark warrior Beastrider Kama once cut bloody swathes of terror across the Arathi Highlands astride such a monster.]], },
	[1182] = { name = [[Lil' Donkey]], sourceText = '|cFFFFD200Drop: |rOverseer Krix|n|cFFFFD200Zone: |rArathi Highlands',  description = [[Likely stolen from Newstead in the Arathi Highlands, this stalwart little donkey was destined to be eaten by kobolds.]], },
	[1183] = { name = [[Skullripper]], sourceText = '|cFFFFD200Drop: |rSkullripper|n|cFFFFD200Zone: |rArathi Highlands',  description = [[She\'s a clever girl.]], },
	[1185] = { name = [[Witherbark Direwing]], sourceText = '|cFFFFD200Drop: |rNimar the Slayer|n|cFFFFD200Zone: |rArathi Highlands',  description = [[Only a handful of these giant bats remain in the Arathi Highlands, carefully raised by the Witherbark forest trolls who first brought them here.]], },
	[1190] = { name = [[Pureheart Courser]], sourceText = '|cFFFFD200Achievement: |r100 Exalted Reputations|n|cFFFFD200Category: |rReputation',  description = [[These coursers have long been known to exist, but only befriend those that have gone to great lengths for friendship.]], },
	[1191] = { name = [[Frenzied Feltalon]], sourceText = '|cFFFFD200Achievement: |rA Horde of Hoofbeats|n|cFFFFD200Category: |rMounts',  description = [[The Legion experimented with the corruption of many different types of wildlife, such as this fiery bird species from a molten world.]], },
	[1192] = { name = [[Prestigious Bloodforged Courser]], sourceText = '|cFFFFD200Achievement: |rHonor Level 500|n|cFFFFD200Category: |rHonor',  description = [[Forged in a fiery hatred, the offspring of a nightmare and a dream, this steed feeds on grains grown with the tears of your enemies.]], },
	[1193] = { name = [[Meat Wagon]], sourceText = '|cFFFFD200Promotion|r |n',  description = [[A favorite unit of the Lich King, both for its power as a siege weapon, and the satisfying sound of flung meat.]], },
	[1194] = { name = [[Vicious White Warsaber]], sourceText = '|cFFFFD200Vendor: |rNecrolord Sipe|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r1|Hitem:103533|h|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t|h',  description = [[If you try to give it a belly scratch, you're asking for trouble.]], },
	[1195] = { name = [[Vicious Black Warsaber]], sourceText = '|cFFFFD200Vendor: |rNecrolord Sipe|n|cFFFFD200Zone: |rStormwind City|n|cFFFFD200Cost: |r1|Hitem:103533|h|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t|h',  description = [[The only thing these big cats bring home as gifts are the severed limbs of your enemies.]], },
	[1196] = { name = [[Vicious Black Bonesteed]], sourceText = '|cFFFFD200Vendor: |rDeathguard Netharian|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r1|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t',  description = [[These skeletal steeds know no fear, eat no grains, and can travel impossible distances.]], },
	[1197] = { name = [[Vicious White Bonesteed]], sourceText = '|cFFFFD200Vendor: |rDeathguard Netharian|n|cFFFFD200Zone: |rOrgrimmar|n|cFFFFD200Cost: |r1|TINTERFACE/ICONS/ACHIEVEMENT_BG_KILL_ON_MOUNT.BLP:0|t',  description = [[There are few other ways to ride in to battle that feature as many spikes and bones.]], },
	[1198] = { name = [[Kul Tiran Charger]], sourceText = '|cFFFFD200Achievement: |rAllied Races: Kul Tiran Human',  description = [[The Kul Tiran military prides itself on mastery of both sea, through a vast navy, and land, with armed cavalry.]], },
	[1608] = { name = [[Soar]], sourceText = '',  description = [[]], },
	[1199] = { name = [[Blackpaw]], sourceText = '|cFFFFD200Drop:|r Agathe Wyrmwood|n|cFFFFD200Zone:|r Darkshore|n|n|cFFFFD200Drop:|r Blackpaw|n|cFFFFD200Zone:|r Darkshore',  description = [[Blackpaw was just hibernating, minding his own business, and then suddenly there was a warfront.]], },
	[1200] = { name = [[Ashenvale Chimaera]], sourceText = '|cFFFFD200Drop:|r Alash\'anir|n|cFFFFD200Zone:|r Darkshore|n',  description = [[Ashenvale chimaeras were used frequently by the Kaldorei to decimate structures in battles against the humans, orcs, and undead.]], },
	[1201] = { name = [[Frightened Kodo]], sourceText = '|cFFFFD200Drop:|r Frightened Kodo|n|cFFFFD200Zone:|r Darkshore|n',  description = [[Nothing frightens a kodo like seeing a bear with antlers.]], },
	[1203] = { name = [[Umber Nightsaber]], sourceText = '|cFFFFD200Drop:|r Moxo the Beheader|n|cFFFFD200Zone:|r Darkshore|n|n|cFFFFD200Drop:|r Athil Dewfire|n|cFFFFD200Zone:|r Darkshore',  description = [[Nightsabers are an integral part of the Kaldorei\'s army, serving as both mount and assault weapon in one.]], },
	[1204] = { name = [[Sandy Nightsaber]], sourceText = '|cFFFFD200Vendor: |rProvisioner Stoutforge|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r350|Hcurrency:1717|h|TInterface/ICONS\UI_Alliance_7LegionMedal.BLP:0|t|h|n|n|cFFFFD200Vendor: |rProvisioner Mukra|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r350|Hcurrency:1717|h|TInterface/ICONS\UI_Alliance_7LegionMedal.BLP:0|t|h',  description = [[A rare color for a nightsaber, and rarely seen, they are prized amongst huntresses.]], },
	[1205] = { name = [[Kaldorei Nightsaber]], sourceText = '|cFFFFD200Drop:|r Croz Bloodrage|n|cFFFFD200Zone:|r Darkshore|n|n|cFFFFD200Drop:|r Shadowclaw|n|cFFFFD200Zone:|r Darkshore',  description = [[The connection between a nightsaber and its hunter or huntress is nearly inseparable.]], },
	[1206] = { name = [[Blue Marsh Hopper]], sourceText = '|cFFFFD200Vendor: |rGottum|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Cost: |r333333|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[That squishing sound every time it takes a step? That\'s a feature.]], },
	[1207] = { name = [[Yellow Marsh Hopper]], sourceText = '|cFFFFD200Vendor: |rGottum|n|cFFFFD200Zone: |rNazmir|n|cFFFFD200Cost: |r333333|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[No one makes giant, slimy spawns like Krag'wa the Huge.]], },
	[1208] = { name = [[Saltwater Seahorse]], sourceText = '|cFFFFD200Vendor: |rCaptain Klarisa|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r500|Hcurrency:1710|h|TInterface/ICONS/INV_Misc_AzsharaCoin.BLP:0|t|h|n|n|cFFFFD200Vendor: |rCaptain Zen\'taga|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r500|Hcurrency:1710|h|TInterface/ICONS/INV_Misc_AzsharaCoin.BLP:0|t|h',  description = [[These giant seahorses are naturally curious, and are often found exploring sunken ship wreckage for shiny things.]], },
	[1209] = { name = [[Stonehide Elderhorn]], sourceText = '|cFFFFD200Drop:|r Island Expeditions',  description = [[Stonehide elderhorns, one of the few types of moose to inhabit island climates, have thinner fur than most elderhorns.]], },
	[1210] = { name = [[Bloodthirsty Dreadwing]], sourceText = '|cFFFFD200Vendor: |rProvisioner Mukra|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r750|Hcurrency:1716|h|TInterface/ICONS/INV_HordeWarEffort.BLP:0|t|h',  description = [[The forsaken prefer the use of these bloodthirsty hunters, both for their speed as a mount, and the psychological effect they have on their enemies.]], },
	[1211] = { name = [[Bloodgorged Hunter]], sourceText = '|cFFFFD200Drop: |rIsland Expeditions',  description = [[If he wasn't so full, you wouldn't have caught him.]], },
	[1212] = { name = [[Island Thunderscale]], sourceText = '|cFFFFD200Drop:|r Island Expeditions',  description = [[One of the larger native dragon species on Azeroth, this subspecies subsists almost entirely on fish. And fishermen.]], },
	[1213] = { name = [[Risen Mare]], sourceText = '|cFFFFD200Drop:|r Island Expeditions|n',  description = [[Something terrible happened in the lost town of Havenswood.]], },
	[1214] = { name = [[Azureshell Krolusk]], sourceText = '|cFFFFD200Vendor: |rProvisioner Stoutforge|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r200|Hcurrency:1717|h|TInterface/ICONS/INV_AllianceWarEffort.BLP:0|t|h',  description = [[Captured from the desolate sands of Vol\'dun, this mount is surprisingly docile in captivity.]], },
	[1215] = { name = [[Rubyshell Krolusk]], sourceText = '|cFFFFD200Vendor: |rProvisioner Mukra|n|cFFFFD200Zone: |rZuldazar|n|cFFFFD200Cost: |r200|Hcurrency:1716|h|TInterface/ICONS/INV_HordeWarEffort.BLP:0|t|h',  description = [[Captured from the desolate sands of Vol\'dun, this mount is surprisingly docile in captivity.]], },
	[1216] = { name = [[Priestess' Moonsaber]], sourceText = '|cFFFFD200Vendor: |rProvisioner Stoutforge|n|cFFFFD200Zone: |rTiragarde Sound|n|cFFFFD200Cost: |r750|Hcurrency:1717|h|TInterface/ICONS/INV_AllianceWarEffort.BLP:0|t|h',  description = [[Once these elegant cats were only given to high-ranking priestesses of Elune, but now they ride into battle with all manner of champion, in the service of vengeance.]], },
	[1217] = { name = [[G.M.O.D.]], sourceText = '|cFFFFD200Drop:|r High Tinker Mekkatorque|n|cFFFFD200Zone:|r Battle of Dazar\'alor',  description = [[Garishly sporting extreme amounts of goblin flair, Gallywix commissioned his Mech of Death to one up Mekkatorque in combat and more importantly in style.]], },
	[1218] = { name = [[Dazar'alor Windreaver]], sourceText = '|cFFFFD200Achievement:|r Glory of the Dazar\'alor Raider|n|cFFFFD200Category:|r Dungeons & Raids|n',  description = [[Soaring high above the Battle of Dazar'alor, these beasts are drawn to the most cunning of victors.]], },
	[1219] = { name = [[Glacial Tidestorm]], sourceText = '|cFFFFD200Drop:|r Jaina Proudmoore (Mythic)|n|cFFFFD200Zone:|r Battle of Dazar\'alor',  description = [[Though she commands a variety of magical powers, Jaina has always been most proficient at summoning powerful elementals of water and ice.]], },
	[1220] = { name = [[Bruce]], sourceText = '|cFFFFD200Quest: |rComplete the Brawler\'s Guild Questline',  description = [[BRUCE!]], },
	[1221] = { name = [[Hogrus, Swine of Good Fortune]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[Hogrus was first seen by Emperor Shaohao on the third day of his meditation, and is considered by the Pandaren to be a sign of good fortune to come to those who take action.]], },
	[1222] = { name = [[Vulpine Familiar]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[A fiercely intelligent and curious species, these fox-like creatures have travelled the Great Dark Beyond and found their way to Azeroth. Drawn to Azeroth\'s champions, they seek adventure and companionship.]], },
	[1223] = { name = [[Sylverian Dreamer]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[An artist in the magical city of Dalaran fell asleep on her brush, and dreamed of painting a beautiful feathered dragon. Her artwork lept from the page, and when she awoke, she found it sleeping gently beside her.]], },
	[1224] = { name = [[Wonderwing 2.0]], sourceText = '|cFFFFD200Achievement: |rBattle for Azeroth Pathfinder, Part Two|n|cFFFFD200Category: |rBattle for Azeroth',  description = [[Don't ask what happened to the first one -- Mechagon technology is still gnome technology!]], },
	[1225] = { name = [[Crusader\'s Direhorn]], sourceText = '|cFFFFD200Paladin|r |n',  description = [[A loyal steed to the holy crusaders of Zandalar.]], },
	[1227] = { name = [[Aerial Unit R-21/X]], sourceText = '|cFFFFD200Drop:|r King Mechagon|n',  description = [[King Mechagon\'s private aerial unit, complete with deployable blast cannon and foot warmers.]], },
	[1229] = { name = [[Rusty Mechanocrawler]], sourceText = '|cFFFFD200Drop:|r Mechagon|n',  description = [[The check engine light is always on, It\\'s when it goes off that you should be worried.]], },
	[1230] = { name = [[Unshackled Waveray]], sourceText = '|cFFFFD200Vendor: |rFinder Pruc|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Faction: |rUnshackled - Exalted|n|cFFFFD200Cost: |r250|Hcurrency:1721|h|TInterface/ICONS/INV_misc_enchantedpearlF.blp:0|t|h',  description = [[The Unshackled have a natural kinship with the species of the deep seas that have seen cruelty at the hands of the naga.]], },
	[1231] = { name = [[Ankoan Waveray]], sourceText = '|cFFFFD200Vendor: |rArtisan Okata|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Faction: |rAnkoan - Exalted|n|cFFFFD200Cost: |r250|Hcurrency:1721|h|TInterface/ICONS/INV_misc_enchantedpearlF.blp:0|t|h',  description = [[The Ankoan have been battling the naga for years, and with a natural affinity for the sea, training the local wildlife was a natural strategy.]], },
	[1232] = { name = [[Azshari Bloatray]], sourceText = '|cFFFFD200Achievement:|r Glory of the Eternal Raider|n|cFFFFD200Category:|r Dungeons & Raids|n',  description = [[The naga raised these rays in captivity, harvesting their gas organs to craft alchemy which allowed them to breathe air more easily.]], },
	[1237] = { name = [[Royal Snapdragon]], sourceText = '|cFFFFD200Drop:|r Ankoan Supplies|n|cFFFFD200Drop:|r Unshackled Supplies',  description = [[Captured at a young age and raised to serve naga hunters, snapdragons are loyal and fierce companions.]], },
	[1238] = { name = [[Snapback Scuttler]], sourceText = '|cFFFFD200Achievement: |rUndersea Usurper|n|cFFFFD200Category: |rBattle for Azeroth',  description = [[Giant crabs are native to Nazjatar and a favored food of the naga. This one must have been well-kept to grow this large.]], },
	[1239] = { name = [[X-995 Mechanocat]], sourceText = '|cFFFFD200Vendor: |rPascal-K1N6|n|cFFFFD200Zone: |rMechagon|n',  description = [[The X-995 is the ultimate in customizeable feline robo-companionship.]], },
	[1240] = { name = [[Obsidian Worldbreaker]], sourceText = '|cFFFFD200Achievement: |rMemories of Fel, Frost and Fire|n|cFFFFD200Category: |rEvents',  description = [["I have had many names throughout history, all of them spoken with dread: Neltharion, Xaxas, and many more. Yet you shall know me best as Deathwing, for so I am! I am the bane of life, the darkness within history, the lord of death, the master of destruction. And I tell you now, and so it is true, that this world is mine!" - Neltharion, the Earth-Warder]], },
	[1242] = { name = [[Beastlord\'s Irontusk]], sourceText = '|cFFFFD200Vendor: |rKronnus|n|cFFFFD200Zone: |rWarspear|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h|n|n|cFFFFD200Vendor: |rTempra|n|cFFFFD200Zone: |rStormshield|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h',  description = [[One of Darmac\'s trained beasts, its tusks can pierce even the hardest Iron Horde steel.]], },
	[1243] = { name = [[Beastlord\'s Warwolf]], sourceText = '|cFFFFD200Vendor: |rKronnus|n|cFFFFD200Zone: |rWarspear|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h|n|n|cFFFFD200Vendor: |rTempra|n|cFFFFD200Zone: |rStormshield|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h',  description = [[One of Darmac\'s trained beasts, it was raised in a crucible of molten fire and shows no fear.]], },
	[1245] = { name = [[Bloodflank Charger]], sourceText = '|cFFFFD200Achievement:|r Two Sides to Every Tale|n',  description = [[Stolen from the Arathi Basin stables, this fire-hearted charger now steeds for the Horde.]], },
	[1246] = { name = [[Ironclad Frostclaw]], sourceText = '|cFFFFD200Achievement:|r Two Sides to Every Tale',  description = [[Captured in the snows of Alterac Valley, this bloodthirsty hunter now roars for the Alliance.]], },
	[1247] = { name = [[Mechacycle Model W]], sourceText = '|cFFFFD200Achievement: |rMecha-Done|n|cFFFFD200Category: |rBattle for Azeroth',  description = [[The rapid technological evolution of the descendents of Mimiron began with the introduction of a new gear, leading to such wonders as the tragically under-produced Model W.]], },
	[1248] = { name = [[Junkheap Drifter]], sourceText = '|cFFFFD200Drop: |rRustfeather|n|cFFFFD200Zone: |rMechagon',  description = [[When you really want to be noticed... and don't mind strapping yourself to a giant, barely-held-together wheel.]], },
	[1249] = { name = [[Child of Torcali]], sourceText = '|cFFFFD200Quest:|r Wander Not Alone|n|cFFFFD200Zone: |rZuldazar',  description = [[This daughter of the great direhorn loa, Torcali, never lost faith that her keeper would help her grow to her full potential.]], },
	[1250] = { name = [[Mollie]], sourceText = '|cFFFFD200Drop: |rDunegorger Kraulok|n|cFFFFD200Zone: |rVol\'dun',  description = [[The kind nature of alpacas makes them perfect as companions for the intrepid adventurer.]], },
	[1252] = { name = [[Mechagon Peacekeeper]], sourceText = '|cFFFFD200Drop: |rHK-8 Aerial Oppression Unit|n|cFFFFD200Zone: |rOperation: Mechagon (Mythic)',  description = [[Keeps the peace by turning you into pieces.]], },
	[1253] = { name = [[Scrapforged Mechaspider]], sourceText = '|cFFFFD200Quest: |rDrive It Away Today|n|cFFFFD200Zone: |rMechagon',  description = [[You built this yourself, you should be proud, even though It\\'s smoky, sparky, and loud.]], },
	[1254] = { name = [[Rustbolt Resistor]], sourceText = '|cFFFFD200Vendor: |rStolen Royal Vendorbot|n|cFFFFD200Zone: |rMechagon|n|cFFFFD200Faction: |rRustbolt Resistance - Exalted|n|cFFFFD200Cost: |r524288|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [["R E S I S T" is carved into the seat in crude letters, along with several, more profane things.]], },
	[1255] = { name = [[Deepcoral Snapdragon]], sourceText = '|cFFFFD200Quest: |rWild Tame|n|cFFFFD200Zone: |rNazjatar',  description = [[The snapdragon is a quiet hunter in the water, but is far less stealthy on land.]], },
	[1256] = { name = [[Snapdragon Kelpstalker]], sourceText = '|cFFFFD200Quest: |rGood Girl|n|cFFFFD200Zone: |rNazjatar',  description = [[When a snapdragon bites you, the worry isn't about the bite itself, but the poison that will begin to eat away at the wound.]], },
	[1257] = { name = [[Silent Glider]], sourceText = '|cFFFFD200Drop: |rSoundless|n|cFFFFD200Zone: |rNazjatar',  description = [[Gasses naturally stored within the creature allowed it to survive within the depths, those same gasses also allow them to effortlessly float through the air.]], },
	[1258] = { name = [[Fabious]], sourceText = '|cFFFFD200Drop:|r Fabious|n',  description = [[He emerges from the water, fins swaying lazily in the breeze, sun-kissed scales glittering. He is... Fabious.]], },
	[1260] = { name = [[Crimson Tidestallion]], sourceText = '|cFFFFD200Vendor: |rMrrl|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r4|Hitem:169783|h|TInterface/ICONS/INV_Misc_Coin_12.blp:0|t|h 2|Hitem:169780|h|TInterface/ICONS/INV_Alchemy_80_AlchemistStone02.BLP:0|t|h 1|Hitem:170100|h|TInterface/ICONS/INV_Misc_Food_111_IcefinFillet.blp:0|t|h|n',  description = [[The blood-red coloration of these creatures is ideal for hiding in large coral formations.]], },
	[1262] = { name = [[Inkscale Deepseeker]], sourceText = '|cFFFFD200Vendor: |rCrafticus Mindbender|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r150|Hitem:168802|h|TInterface/ICONS/INV_misc_token_pvp01.BLP:0|t|h|n|n|cFFFFD200Vendor: |rDazzerian|n|cFFFFD200Zone: |rNazjatar|n|cFFFFD200Cost: |r150|Hitem:168802|h|TInterface/ICONS/INV_misc_token_pvp01.BLP:0|t|h',  description = [[Schools of tidestallions will viciously slam their muscular chests into their prey to take them down.]], },
	[1265] = { name = [[Uncorrupted Voidwing]], sourceText = '|cFFFFD200Quest:|r Lost Flight|n',  description = [[This spawn of Vexiona, cleansed by Wrathion, comes from a flight with a fractured past and uncertain future.]], },
	[1266] = { name = [[Alabaster Stormtalon]], sourceText = '|cFFFFD200In-Game Shop|r|n',  description = [[Chiseled by the stonemasons of Stormwind, this stone gryphon symbolizes the unbending courage of the Alliance.]], },
	[1267] = { name = [[Alabaster Thunderwing]], sourceText = '|cFFFFD200In-Game Shop|r|n',  description = [[Chiseled by the stoneworkers of Orgrimmar, this stone wind rider is a symbol of the unwavering strength of the Horde.]], },
	[1269] = { name = [[Swift Spectral Fathom Ray]], sourceText = '|cFFFFD200Legacy|r',  description = [[Even the dead need a lift every now and then.]], },
	[1270] = { name = [[Swift Spectral Magnetocraft]], sourceText = '|cFFFFD200Legacy|r',  description = [[Even the dead need a lift every now and then.]], },
	[1271] = { name = [[Swift Spectral Armored Gryphon]], sourceText = '|cFFFFD200Legacy|r',  description = [[Even the dead need a lift every now and then.]], },
	[1272] = { name = [[Swift Spectral Pterrordax]], sourceText = '|cFFFFD200Legacy|r',  description = [[Even the dead need a lift every now and then.]], },
	[1282] = { name = [[Black Serpent of N'Zoth]], sourceText = '|cFFFFD200Achievement:|r Through the Depths of Visions',  description = [[The black serpents of N'Zoth are the harbingers of his ultimate vision becoming reality. Only those servents or enemies that can tread this space have any chance of wrangling them.]], },
	[1283] = { name = [[Mechagon Mechanostrider]], sourceText = '|cFFFFD200Achievement: |rAllied Races: Mechagnome',  description = [[The best mount, logically, serves multiple purposes -- sometimes you need to get places AND reach something high up once you get there.]], },
	[1285] = { name = [[Frostwolf Snarler]], sourceText = '|cFFFFD200Achievement: |rAlterac Valley of Olde|n|cFFFFD200Category: |rAlterac Valley',  description = [[Raised in the Alterac Mountains by the Frostwolf Clan, trained to bite dwarves.]], },
	[1286] = { name = [[Caravan Hyena]], sourceText = '|cFFFFD200Achievement: |rAllied Races: Vulpera',  description = [[The canopy atop this dune-wanderer can be stored at your campsite, should you desire to feel the wind in your fur.]], },
	[1287] = { name = [[Explorer\'s Jungle Hopper]], sourceText = '|cFFFFD200Recruit-a-Friend|r ',  description = [[For when you're making a crash landing in the middle of an uncharted swamp because It\\'s the only way to access that ancient temple.]], },
	[1288] = { name = [[Explorer\'s Dunetrekker]], sourceText = '|cFFFFD200Recruit-a-Friend|r ',  description = [[The surly dunetrekker can easily carry a set of explorers to the nearest ruins and back without the need to rest.]], },
	[1289] = { name = [[Ensorcelled Everwyrm]], sourceText = '|cFFFFD200Promotion:|r Shadowlands Heroic Edition',  description = [[Anima is a fundamental force that infuses the Shadowlands. It flows through many creatures, but is also traded, bartered for, and fought over.]], },
	[1290] = { name = [[Squeakers, the Trickster]], sourceText = '|cFFFFD200In Game Shop|r|n',  description = [[The locals in Halfhill swear that whenever a batch of grain is missing, a farmworking instrument is misplaced, or a bushel of berries can't be found, you can hear a mischievous squeaking, and are reminded to slow down and accept what life gives you.]], },
	[1291] = { name = [[Lucky Yun]], sourceText = '|cFFFFD200In Game Shop|r|n',  description = [[The Pandaren believe that merely rubbing one\'s hands on the shiny hide of the ox will grant prosperity, the challenge is catching it.]], },
	[1292] = { name = [[Stormpike Battle Ram]], sourceText = '|cFFFFD200Achievement: |rAlterac Valley of Olde|n|cFFFFD200Category: |rAlterac Valley',  description = [[Raised in the Alterac Mountains by the Stormpike Clan, trained to headbutt orcs.]], },
	[1293] = { name = [[Ny'alotha Allseer]], sourceText = '|cFFFFD200Drop:|r N\'Zoth the Corruptor|n|cFFFFD200Zone:|r Ny\'alotha, the Waking City|n|cFFFFD200Difficulty:|r Mythic',  description = [[To find him, drown yourself in the Circle of Stars.]], },
	[1297] = { name = [[Clutch of Ha-Li]], sourceText = '|cFFFFD200Drop: |rHa-Li|n|cFFFFD200Zone: |rVale of Eternal Blossoms',  description = [[The brood of Ha-Li are comfortable in the blue skies of Pandaria, hunting unsuspecting prey.]], },
	[1298] = { name = [[Hopecrusher Gargon]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[Once the loyal mount of a House Briarbane lord, it turned on the lord and his retinue. Members of House Briarbane now whisper of a dark Gargon lying in wait even baiting venthyr who venture through the darker parts of the Chalice District of Revendreth.]], },
	[1299] = { name = [[Battle Gargon Vrednic]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[Prince Renathal\'s loyal gargon, Vrednic has been by his side for millennia. Adorned in his Sinvyr armor, Vrednic\'s mere presence instills terror to all those who lay eyes on him as he ushers his rider into battle.]], },
	[1302] = { name = [[Dreamlight Runestag]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[Runestags are granted to those who have pledged their service to the Winter Queen and the Court of Night.]], },
	[1303] = { name = [[Enchanted Dreamlight Runestag]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[The majestic runestags are capable of siphoning anima from the ambient light of Ardenweald with their elaborate antlers. The anima is used as nourishment and to power the runestag\'s speed and magic.]], },
	[1304] = { name = [[Mawsworn Soulhunter]], sourceText = '|cFFFFD200Drop: |rGorged Shadehound|n|cFFFFD200Zone: |rThe Maw',  description = [[Soulhunters are used to track wayward souls across the wastes that flee from their dark fates, and are one of the only creatures that feel comfortable traversing the maw.]], },
	[1305] = { name = [[Darkwarren Hardshell]], sourceText = '|cFFFFD200Adventures:|r Shadowlands',  description = [[The Darkwarren Hardshell\'s chitin is sturdy enough to withstand mortar shells and dragon\'s fire. It will also conviently consume anything lacking anima, if needed.]], },
	[1306] = { name = [[Swift Gloomhoof]], sourceText = '|cFFFFD200Drop: |rNight Mare|n|cFFFFD200Zone: |rArdenweald',  description = [[The ballads of Ardenweald claim that the birth of gloomrunner heralds the passing of a particularly benevolent wild god. Seeing a gloomrunner is therefore a blessing and a curse.]], },
	[1307] = { name = [[Sundancer]], sourceText = '|cFFFFD200Drop:|r Sundancer|n|cFFFFD200Zone:|r Bastion|n',  description = [[This sun-dappled courser gallops on lightbeams with carefree abandon.]], },
	[1309] = { name = [[Chittering Animite]], sourceText = '|cFFFFD200Adventures:|r Shadowlands|n',  description = [[These exceptionally agile fliers make excellent mounts once they've been trained not to sap every ounce of anima from their rider.]], },
	[1310] = { name = [[Horrid Dredwing]], sourceText = '|cFFFFD200Drop: |rHarika the Horrid|n|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[Harika the Horrid terrorized the denizens of the Forest Ward, and with her demise her broodlings can now take this horror to denizens farther afield.]], },
	[1311] = { name = [[Ivory Cloud Serpent]], sourceText = '|cFFFFD200Drop:|r Ivory Cloud Serpent|n|cFFFFD200Zone:|r Vale of Eternal Blossoms',  description = [[Ivory Cloud Serpents are often mistaken for roaring thunder as they tear through the skies with blistering speed.]], },
	[1312] = { name = [[Gargantuan Grrloc]], sourceText = '|cFFFFD200In-Game Shop|r |n',  description = [[It\\'s the biggest murloc you've ever seen!]], },
	[1313] = { name = [[Rajani Warserpent]], sourceText = '|cFFFFD200Vendor: |rZhang Ku|n|cFFFFD200Zone: |rVale of Eternal Blossoms|n|cFFFFD200Cost: |r1|Hitem:174230|h|TInterface/ICONS/INV_Misc_MonsterScales_18.blp:0|t|h',  description = [[Fierce companions of the followers of Ra-den, the Rajani Warserpents are a force to be reckoned with.]], },
	[1314] = { name = [[Drake of the Four Winds]], sourceText = '|cFFFFD200Drop: |rIshak of the Four Winds|n|cFFFFD200Zone: |rUldum',  description = [[Rocks erode, fires sputter, but wind remains powerful regardless of the passage of time. -Phaoris]], },
	[1315] = { name = [[Mail Muncher]], sourceText = '|cFFFFD200Drop: |rMail Muncher|n|cFFFFD200Zone: |rHorrific Visions',  description = [[Worming its way through the Postmaster\'s network, this serpent of N'Zoth deprived Azeroth of its primary magical correspondence.]], },
	[1317] = { name = [[Waste Marauder]], sourceText = '|cFFFFD200Drop: |rRotfeaster|n|cFFFFD200Zone: |rUldum',  description = [[Larger than most of its kin, this vulture has shown sick and strong beasts alike why they need to fear the skies.]], },
	[1318] = { name = [[Wastewander Skyterror]], sourceText = '|cFFFFD200Vendor: |rProvisioner Qorra|n|cFFFFD200Zone: |rUldum|n|cFFFFD200Faction: |rUldum Accord - Exalted|n|cFFFFD200Cost: |r24000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Doubting a Wastewander Skyterror\'s capabilities is a quick way to find yourself missing a limb.]], },
	[1319] = { name = [[Malevolent Drone]], sourceText = '|cFFFFD200Drop:|r Corpse Eater|n|cFFFFD200Zone: |rUldum',  description = [[The Aqir drones feast on heroes and cowards alike to grow their ranks.]], },
	[1320] = { name = [[Shadowbarb Drone]], sourceText = '|cFFFFD200Quest: |rMy Own Drone|n|cFFFFD200Zone: |rUldum',  description = [[Void, jelly, and a very full belly is all this drone needs.]], },
	[1321] = { name = [[Wicked Swarmer]], sourceText = '|cFFFFD200Vendor: |rWrathion|n|cFFFFD200Zone: |rChamber of Heart|n|cFFFFD200Cost: |r100000|Hcurrency:1719|h|TInterface/ICONS/Archaeology_5_0_MoguCoin.blp:0|t|h',  description = [[When the Void reached out, the Aqir were formed in its terrible image.]], },
	[1322] = { name = [[Wriggling Parasite]], sourceText = '|cFFFFD200Achievement: |rGlory of the Ny\'alotha Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[It\'s so wiggly! Just looking at it move is chaotic and unnerving.]], },
	[1324] = { name = [[Elusive Quickhoof]], sourceText = '|cFFFFD200Zone: |rVol\'dun',  description = [[The kind nature of alpacas makes them perfect as companions for the intrepid adventurer.]], },
	[1326] = { name = [[Awakened Mindborer]], sourceText = '|cFFFFD200Achievement: |rBattle for Azeroth Keystone Master: Season Four|n|cFFFFD200Category: |rDungeons',  description = [[It may look menacing right now, but It\\'s actually kind of cute when It\\'s asleep.]], },
	[1327] = { name = [[Ren\'s Stalwart Hound]], sourceText = '|cFFFFD200Drop: |rHoundlord Ren|n|cFFFFD200Zone: |rVale of Eternal Blossoms',  description = [[Houndlord Ren used his strongest quilen to carry him across Pandaria to subjugate the Vale for the Mogu empire.]], },
	[1328] = { name = [[Xinlao]], sourceText = '|cFFFFD200Drop: |rAnh-De the Loyal|n|cFFFFD200Zone: |rVale of Eternal Blossoms',  description = [[The only thing stronger than a quilen\'s loyalty is its fierce devotion to making sure it covers its owner in kisses.]], },
	[1329] = { name = [[Springfur Alpaca]], sourceText = '|cFFFFD200Quest: |rAlpaca It In|n|cFFFFD200Zone: |rUldum',  description = [[The kind nature of alpacas makes them perfect as companions for the intrepid adventurer.]], },
	[1332] = { name = [[Silky Shimmermoth]], sourceText = '|cFFFFD200Vendor: |rMaster Clerk Salorn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Cost: |r5000|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = [[The moths of Ardenweald play a vital role in the ecosystem, carrying spores, anima, and pollen from one part of the forest to the other, providing nourishment and variation to the denizens living below.]], },
	[1346] = { name = [[Steamscale Incinerator]], sourceText = '|cFFFFD200In-Game Shop|r|n',  description = [[A mad gnomish inventor had a dream to ride a dragon, so he created this metal monstrosity. He powered it with a trapped demon, in order to create "an authentic dragon soul," and did not survive its activation.]], },
	[1350] = { name = [[Colossal Slaughterclaw]], sourceText = '|cFFFFD200Drop: |rSupplies of the Undying Army|n|cFFFFD200Zone: |rMaldraxxus',  description = [[Only the most stalwart stable masters of Maldraxxus are able to break the will of the colossal slaughterclaws, giving all champions of the Undying Army a fearsome ally in the Shadowlands.]], },
	[1351] = { name = [[Vicious War Spider]], sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 1|nRated Arena and Rated Battleground Season Reward',  description = [[When being a courageous member of the Alliance doesn't strike the right amount of fear into your opponent, add a giant spider.]], },
	[1352] = { name = [[Vicious War Spider]], sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 1|nRated Arena and Rated Battleground Season Reward',  description = [[When being a bellowing member of the Horde doesn't strike the right amount of fear into your opponent, add a giant spider.]], },
	[1354] = { name = [[Shadeleaf Runestag]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[The shadeleaf runestag are bred for their ferocity as well as their beauty by the Wild Hunt. They feel no fear charging into the den of any predator.]], },
	[1355] = { name = [[Wakener\'s Runestag]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[Runestags enjoy lying near wildseeds. The Wakeners believe they can hear the sleepers' dreams. These runestags display a range of emotions in their vigil, signaling the dreams or nightmares of the spirits within.]], },
	[1356] = { name = [[Winterborn Runestag]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[Winterborn runestags are blessed by the Winter Queen herself. Their runes flare brilliantly when predators are nearby, warning the entire herd.]], },
	[1357] = { name = [[Enchanted Shadeleaf Runestag]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[An shadeleaf runestag is capable of goring creatures as powerful as a gorm matriarch in defense of its herd. The Wild Hunt ride these mounts into dangerous corners of the Shadowlands, when their domain must be defended from those who would threaten the wilds.]], },
	[1358] = { name = [[Enchanted Wakener\'s Runestag]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[Wakener runestags fiercely protect their wildseed groves. When the groves starting to fail, many runestags went berserk.]], },
	[1359] = { name = [[Enchanted Winterborn Runestag]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[Winterborn runestags are the favored mount of the Winter Queen herself, and she only bestows such a creature on her most favored subjects.]], },
	[1360] = { name = [[Shimmermist Runner]], sourceText = '|cFFFFD200Area: |rMistveil Tangle|n|cFFFFD200Zone: |rArdenweald',  description = [[Mischievous spriggans and the valerunners have a natural enmity. No valerunner would deign to allow a spriggan rider because they can sense their inherent malevolence. This angers the trickster spriggans who take twisted enjoyment in playing cruel pranks on these majestic creatures.]], },
	[1361] = { name = [[Duskflutter Ardenmoth]], sourceText = '|cFFFFD200Vendor: |rAithlyn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Faction: |rThe Wild Hunt - Exalted|n|cFFFFD200Cost: |r30000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[These ardenmoths prefer the dark and damp forests of northwest Ardenweald, where fungal colonies are perpetually blooming.]], },
	[1362] = { name = [[Spinemaw Gladechewer]], sourceText = '|cFFFFD200Drop: |rGormtamer Tizo|n|cFFFFD200Zone: |rArdenweald',  description = [[Spriggan riders tie bits of anima-less detritus to the end of sticks in front of their gorm mounts to steer. It\'s... an imperfect art.]], },
	[1363] = { name = [[Sinful Gladiator\'s Soul Eater]], sourceText = '|cFFFFD200Achievement: |rSinful Gladiator: Shadowlands Season 1|n|cFFFFD200Category: |rPlayer vs. Player',  description = [[This mount shows a proclivity for snacking upon the souls cast out of Revendreth.]], },
	[1364] = { name = [[War-Bred Tauralus]], sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = [[The soldiers of Maldraxxus protect the Shadowlands, and require a mount that is both capable and calm when faced with overwhelming odds.]], },
	[1365] = { name = [[Plaguerot Tauralus]], sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = [[The tauralus is considered courageous, though scholars of Maldraxxus have long wondered if their lack of fear is simply due to the tiny size of their brains.]], },
	[1366] = { name = [[Bonehoof Tauralus]], sourceText = '|cFFFFD200Drop: |rTahonta|n|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = [[There are quite a few reasons that you do not want to stand behind a tauralus, the hooves and whip-like tails being the least of them.]], },
	[1367] = { name = [[Chosen Tauralus]], sourceText = '|cFFFFD200Achievement: |rThe Gang\'s All Here|n|cFFFFD200Category: |rCovenant Sanctums',  description = [[Even an unarmored tauralus is a valuable ally in battle, its enormous horns capable of piercing even plate armor.]], },
	[1368] = { name = [[Armored War-Bred Tauralus]], sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = [[The tauralus that serve the armies of Maldraxxus have had all traits of compassion bred out of them, and are only adorned in armor when they show that no sword or spell can stop them from carrying their rider to victory.]], },
	[1369] = { name = [[Armored Plaguerot Tauralus]], sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Vendor: |rSu Zettai|n|cFFFFD200Zone: |rMaldraxxus|n|cFFFFD200Cost: |r3000|Hcurrency:1813|h|TInterface/ICONS/Spell_AnimaBastion_Orb.BLP:0|t|h',  description = [[When fully armored, a tauralus can take an incredible amount of damage before falling, making it a valuable ally in battle.]], },
	[1370] = { name = [[Armored Bonehoof Tauralus]], sourceText = '|cFFFFD200Drop: |rSabriel the Bonecleaver|n|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = [[A favored mount of the House of the Chosen, the tauralus only gets more angry and vicious when exposed to battle.]], },
	[1371] = { name = [[Armored Chosen Tauralus]], sourceText = '|cFFFFD200Achievement: |rThings To Do When You\'re Dead|n|cFFFFD200Category: |rCovenant Sanctums',  description = [[The tauralus has two tails, which are both absolutely necessary for whipping away buzzing pests like the bloodlouse.]], },
	[1372] = { name = [[Blisterback Bloodtusk]], sourceText = '|cFFFFD200Drop:|r Warbringer Mal\'korak|n|cFFFFD200Zone: |rMaldraxxus',  description = [[The House of the Chosen train their mounts in all manner of difficult circumstances, so that no matter the battle, they will never falter.]], },
	[1373] = { name = [[Gorespine]], sourceText = '|cFFFFD200Drop: |rNerissa Heartless|n|cFFFFD200Zone: |rMaldraxxus',  description = [[Nerissa trampled over countless bodies on the back of Gorespine as she rose to prominence as the commander of the Lichsworn forces for the House of Rituals.]], },
	[1375] = { name = [[Lurid Bloodtusk]], sourceText = '|cFFFFD200Vendor: |rNalcorn Talsen|n|cFFFFD200Zone: |rMaldraxxus|n|cFFFFD200Faction: |rThe Undying Army - Exalted|n|cFFFFD200Cost: |r30000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Only bloodtusks that survive the Contest of Conquest are deemed worthy enough to become a mount of the Undying Army.]], },
	[1376] = { name = [[Silvertip Dredwing]], sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord',  description = [[Bred by the Revendreth prince, Renathal, these rare dredwing are fiercely loyal and will only let those deemed worthy by the prince himself to interact with them.]], },
	[1377] = { name = [[Rampart Screecher]], sourceText = '|cFFFFD200Achievement: |rGlory of the Nathria Raider|cFFFFD200Category: |rDungeons & Raids',  description = [[Roosting among the ramparts of Castle Nathria, these giant dredbats act as sentinels and are trained to take down threats approaching the castle from the air.]], },
	[1378] = { name = [[Harvester\'s Dredwing]], sourceText = '|cFFFFD200Drop:|r Harvester\'s War Chest|n|cFFFFD200Zone:|r The Maw ',  description = [[The dredwings of the Harvesters fear no mawsworn, and will gleefully sink their teeth into anything that threatens Sinfall.]], },
	[1379] = { name = [[Endmire Flyer]], sourceText = '|cFFFFD200Drop: |rFamu the Infinite|n|cFFFFD200Zone: |rRevendreth',  description = [[Attracted through portals through which little is known these flying devourers have found a home in the Endmire of Revendreth feeding on the latent anima.]], },
	[1382] = { name = [[Inquisition Gargon]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[The gargon of the Inquisitors are some of the most vicious ever carved. The Avowed seek to recapture these beasts and return them to the righteous duty of assisting in the purifying of souls rather than the perversions performed at the hands of the Inquisitors.]], },
	[1384] = { name = [[Sinfall Gargon]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[The gargon are the loyal mounts and companions of the Venthyr and Renathal has found many abandoned at Sinfall, awaiting reawakening and assignment to worthy members of the covenant.]], },
	[1385] = { name = [[Crypt Gargon]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[Loyal hounds of the Curator, these gargon patrol the crypts and catacombs deterring both trespassing and escape.]], },
	[1387] = { name = [[Desire\'s Battle Gargon]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[The Countess maintains a small stable of elegantly adorned gargon mounts for her sole use. Many a rival has been taken in by the finery to their peril as she personally oversees their deadly training.]], },
	[1388] = { name = [[Gravestone Battle Gargon]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[Representative of a new loyal batch of gargon, these battle ready beasts will take Renathal\'s most loyal compatriots into the final battles against the Jailer.]], },
	[1389] = { name = [[Battle Gargon Silessa]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[Silessa fought valiantly alongside her master in the great incursion by the Light, following the battle her master set her to guard over a vault room of great value.]], },
	[1391] = { name = [[Loyal Gorger]], sourceText = '|cFFFFD200Quest: |rA New Pack',  description = [[Crawling forth from the cracks between the realms, the devourers have a voracious appetite for anima. Little is known of their origins, but all denizens of the Shadowlands see their recent boldness as a sign of the end times.]], },
	[1392] = { name = [[Pale Acidmaw]], sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord',  description = [[The acid present in the ichor of this creature sapped away the color from its shell, leaving it a striking shade of snowy white.]], },
	[1393] = { name = [[Wild Glimmerfur Prowler]], sourceText = '|cFFFFD200Drop: |rValfir the Unrelenting|n|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[This prowler\'s brilliant green coat distinguishes it from the blues and purples of Ardenweald, making it much easier for others of its kind to see through the midnight woods.]], },
	[1394] = { name = [[Phalynx of Loyalty]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[Some within the Ascended keep a phalynx as a pet. While they may not be as furry as a larion, they are much less likely to eat your other pets.]], },
	[1395] = { name = [[Phalynx of Humility]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[The phalynx does not fetch, though it is not known why this is.]], },
	[1396] = { name = [[Phalynx of Courage]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[A phalynx is considered both a worthy companion and a lithe opponent to test one\'s reflexes against.]], },
	[1397] = { name = [[Wildseed Cradle]], sourceText = '|cFFFFD200Treasure: |rCache of the Moon|n|cFFFFD200Zone: |rArdenweald',  description = [[The most precious cargo in all Ardenweald, the wildseeds, are cradled by this lovingly-crafted cart. It is imbued with anima to allow it to hover over the ground.]], },
	[1398] = { name = [[Phalynx of Purity]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[Little is known about the origination of phalynx technology, the machinations of which are taught more akin to religion than engineering.]], },
	[1399] = { name = [[Eternal Phalynx of Purity]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[The armored phalynx is a favorite companion of the mightiest warriors of the Ascended as it is both fearless and vicious in battle.]], },
	[1400] = { name = [[Eternal Phalynx of Courage]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[Many bars of gildenite are required to forge one of these sparkling mounts, but the sight of them alone is worth the gathering time.]], },
	[1401] = { name = [[Eternal Phalynx of Loyalty]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[A marvel of steward engineering, the armored phalynx is a rare and awe-inspiring sight.]], },
	[1402] = { name = [[Eternal Phalynx of Humility]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[When an armored phalynx is forged, stewards clap their hammers together in reverence of the skill required in its creation.]], },
	[1404] = { name = [[Silverwind Larion]], sourceText = '|cFFFFD200Treasure: |rGift of the Silver Wind|n|cFFFFD200Zone: |rBastion',  description = [[This gift of the silvery winds is only bestowed upon those that search high and low for the wisdom of Bastion.]], },
	[1405] = { name = [[Restoration Deathwalker]], sourceText = '|cFFFFD200Achievement: |rShadowlands Keystone Master: Season Four|n|cFFFFD200Category: |rDungeons',  description = [[With the death of the Jailer and the ascension of a new Arbiter, the Shadowlands is returning to its great Purpose. Some deathwalkers recognize the role that mortal heroes have played, and are willing to serve as mounts.]], },
	[1406] = { name = [[Marrowfang]], sourceText = '|cFFFFD200Drop:|r Nalthor the Rimebinder|n|cFFFFD200Zone: |rThe Necrotic Wake (Mythic)|n|n|cFFFFD200Drop:|r Challenger\'s Cache|n|cFFFFD200Zone: |rThe Necrotic Wake',  description = [[Marrowfang, Bonefang\'s progeny, was respected by Amarth the Harvester for her insatiable appetite, even if it disrupted any experiments that the sadistic commander wanted to run.]], },
	[1407] = { name = [[Callow Flayedwing]], sourceText = '|cFFFFD200Zone: |rMaldraxxus',  description = [[Flayedwings are a necessary force in the cycle of life for the Shadowlands, acting as both prey and predator for the harsh environment of Maldraxxus.]], },
	[1408] = { name = [[Gruesome Flayedwing]], sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord',  description = [[The flayedwings of Maldraxxus are prized for their ability to soar effortlessly through the skies of the Shadowlands, allowing their rider a surprise opportunity to strike at their foes or support their allies.]], },
	[1409] = { name = [[Bonesewn Fleshroc]], sourceText = '|cFFFFD200Profession:|r Abominable Stitching|n|cFFFFD200Zone:|r Maldraxxus',  description = [[Only expert flesh-stitchers who have demonstrated mastery over the magics of Maldraxxus are powerful enough to construct a deathroc of this size.]], },
	[1410] = { name = [[Hulking Deathroc]], sourceText = '|cFFFFD200Drop: |rViolet Mistake|n|cFFFFD200Zone: |rMaldraxxus',  description = [[Deathrocs are stitched monstrosities from the discarded viscera, bone, and organs that were not required to construct soldiers for the House of Constructs.]], },
	[1411] = { name = [[Predatory Plagueroc]], sourceText = '|cFFFFD200Drop: |rGieger|n|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = [[Geiger\'s everlasting hunger is legendary amonst the House of Constructs, and no beast is too large or bony to fit inside its massive stomach.]], },
	[1413] = { name = [[Dauntless Duskrunner]], sourceText = '|cFFFFD200Covenant Feature: |rThe Ember Court|n|cFFFFD200Covenant: |rVenthyr|n|n|cFFFFD200Covenant Feature: |rQueen\'s Conservatory|n|cFFFFD200Covenant: |rNight Fae|n|n|cFFFFD200Covenant Feature: |rPath of Ascension|n|cFFFFD200Covenant: |rKyrian|n|n|cFFFFD200Covenant Feature: |rAbomination Factory|n|cFFFFD200Covenant: |rNecrolord',  description = [[The Path of Ascension is a challenging trial, and those that display all of the facets the Kyrian hold dear will be duly rewarded.]], },
	[1414] = { name = [[Sinrunner Blanchy]], sourceText = '|cFFFFD200NPC:|r Dead Blanchy |n|cFFFFD200Location:|r Revendreth|n |n',  description = [[In life, Blanchy appeared to be an affectionate, well-behaved horse of excellent disposition and appetite. What evils must she have committed in secret to have earned her place among the condemned souls of Revendreth?]], },
	[1415] = { name = [[Arboreal Gulper]], sourceText = '|cFFFFD200Drop: |rHumon\gozz|n|cFFFFD200Zone: |rArdenweald',  description = [[The wood and foliage growing atop this toad is a natural part of its body, helping it siphon anima from the air to nourish itself.]], },
	[1416] = { name = [[Mawsworn Charger]], sourceText = '|cFFFFD200Achievement:|r Flawless Master|n|cFFFFD200Zone:|r Torghast, Tower of the Damned',  description = [[The fate of all horse mounts.]], },
	[1417] = { name = [[Hand of Hrestimorak]], sourceText = '|cFFFFD200Achievement:|r Glory of the Dominant Raider|n|cFFFFD200Zone:|r Sanctum of Domination',  description = [[The treachery of Hrestimorak was infamous, betraying his people to claim power. Facing damnation in the Maw, he tried to subvert the Jailer... a foolish act that would earn him an eternity of torment.]], },
	[1419] = { name = [[Sintouched Deathwalker]], sourceText = '|cFFFFD200Achievement: |rShadowlands Keystone Master: Season One|n|cFFFFD200Category: |rDungeons',  description = [[Forged from the sins of countless diabolical dungeon denizens. Only those with the skills to overcome them may hold its reins.]], },
	[1420] = { name = [[Umbral Scythehorn]], sourceText = '|cFFFFD200Vendor: |rSpindlenose|n|cFFFFD200Zone: |rArdenweald|n',  description = [[Umbral gorm are the most docile of all gorm, but they also grow the largest horns and mandibles. They are probably so docile because other creatures avoid riling them.]], },
	[1421] = { name = [[Court Sinrunner]], sourceText = '|cFFFFD200Vendor: |rMistress Mihaela|n|cFFFFD200Zone: |rRevendreth|n|cFFFFD200Faction: |rCourt of Harvesters - Exalted|n|cFFFFD200Cost: |r30000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Sinrunners were once the steeds of dark souls, carried in the wake of either their sins or their masters' sins to Revendreth. While some chose to run free, many continue to serve darkness in death as they did in life.]], },
	[1422] = { name = [[Warstitched Darkhound]], sourceText = '|cFFFFD200Adventures:|r Shadowlands',  description = [[These warhounds are crafted by the House of Constructs to carry any rider into the thick of battle, rending and tearing enemy mounts all the while.]], },
	[1423] = { name = [[Highwind Darkmane]], sourceText = '|cFFFFD200Adventures:|r Shadowlands',  description = [[The Forsworn and their mounts take on a darker aspect upon renouncing the path laid forth by the Archon. While tame enough to ride, the Highwind Darkmane has a fiercely independent streak.]], },
	[1424] = { name = [[Snowstorm]], sourceText = '|cFFFFD200Blizzard\'s 30th Anniversary|r ',  description = [[He comes from the ice and snow, under the Azeroth sun where adventurers go.]], },
	[1425] = { name = [[Gilded Prowler]], sourceText = '|cFFFFD200Vendor: |rAdjutant Nikos|n|cFFFFD200Zone: |rBastion|n|cFFFFD200Cost: |r30000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[The mighty larion is an apex predator of Bastion, but that does not stop it from enjoying a nice nap in the sunny fields.]], },
	[1426] = { name = [[Ascended Skymane]], sourceText = '|cFFFFD200Treasure: |rCache of the Ascended|n|cFFFFD200Zone: |rBastion',  description = [[Only those that have proven themselves against the combined might of the Ascended will be able to ride this mount.]], },
	[1428] = { name = [[Amber Ardenmoth]], sourceText = '|cFFFFD200Drop:|r Wild Hunt Supplies|n|cFFFFD200Zone:|r Ardenweald|n',  description = [[The brilliant orange hue of the amber ardenmoth signals to predators that it is dangerous to eat. Consuming one of these creatures will put a predator into a dreamless sleep for years.]], },
	[1429] = { name = [[Vibrant Flutterwing]], sourceText = '|cFFFFD200Vendor: |rCortinarius|n|cFFFFD200Zone: |rArdenweald',  description = [[These minty green moths are very friendly. They particularly enjoy having their head fuzz vigorously brushed. They also enjoy music and poetry.]], },
	[1430] = { name = [[Desertwing Hunter]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[In service of the great Purpose, desertwing hunters were designed to fill distant skies of forgotten realms of the Shadowlands.]], },
	[1431] = { name = [[Pale Regal Cervid]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Looking at these majestic creatures, It\\'s not difficult to see how the idea of cervids developed into the stags found on Azeroth and other mortal worlds.]], },
	[1433] = { name = [[Vespoid Flutterer]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[A flutterer is an overgrown vespoid worker created to carry a rider. Rather than looking to a queen for leadership, it looks to its rider to guide it in all things.]], },
	[1434] = { name = [[Deepstar Aurelid]], sourceText = '|cFFFFD200Drop:|r Hirukon|n|cFFFFD200Zone:|r Zereth Mortis',  description = [[As one of the last remaining polyps of an elusive creature, this aurelid is at home in the water as much as the air.]], },
	[1436] = { name = [[Battle-Hardened Aquilon]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Cost: |r1000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h|n|cFFFFD200Achievement: |rOn the Offensive',  description = [[The aquilon can be a valuable ally in combat, with capabilities exceeding those of the phalynx.]], },
	[1437] = { name = [[Battle-Bound Warhound]], sourceText = '|cFFFFD200Drop: |rTheater of Pain|n|cFFFFD200Zone: |rMaldraxxus',  description = [[Warhounds find comfort in conflict, and the Contest of Conquest is the perfect place to find these beasts honing their abilities.]], },
	[1438] = { name = [[Bulbous Necroray]], sourceText = '|cFFFFD200Drop:|r Oozing Necroray Egg|n',  description = [[The necrorays of Maldraxxus prefer to congregate around the enormous decaying mushrooms of the region.]], },
	[1439] = { name = [[Infested Necroray]], sourceText = '|cFFFFD200Drop:|r Oozing Necroray Egg|n',  description = [[Necrorays lay their eggs in small clusters within the frills and hollows of decaying fungal blooms.]], },
	[1440] = { name = [[Pestilent Necroray]], sourceText = '|cFFFFD200Drop:|r Oozing Necroray Egg|n',  description = [[When a necroray hatches, spores fly everywhere, making a huge mess.]], },
	[1441] = { name = [[Bound Shadehound]], sourceText = '|cFFFFD200Quest:|r Feral Shadehound|n|cFFFFD200Zone: |rThe Maw',  description = [[Created by deciphering and performing powerful runic magic, this Shadehound is bound to its creator.]], },
	[1442] = { name = [[Corridor Creeper]], sourceText = '|cFFFFD200Achievement: |rTwisting Corridors: Layer 8|n|cFFFFD200Category: |rTorghast',  description = [[The loyal hounds of the Jailer creep along the corridors in search of the scent of souls.]], },
	[1443] = { name = [[Voracious Gorger]], sourceText = '|cFFFFD200Achievement: |rGlory of the Shadowlands Hero|n|cFFFFD200Category: |rDungeons & Raids',  description = [[The devourers are presumed to have their origins in the In-Between, a space between the lands of the Shadowlands that few have explored.]], },
	[1444] = { name = [[Viridian Phase-Hunter]], sourceText = '|cFFFFD200Promotion|r',  description = [[Hailing from a species already adept at natural camouflage, the otherworldly energies that transformed Draenor into Outland gave these creatures the ability to shift through space.]], },
	[1445] = { name = [[Slime Serpent]], sourceText = '|cFFFFD200Drop: |rSlime Serpent|n|cFFFFD200Zone: |rPlaguefall',  description = [[This mysterious creature lurks beneath the slime pools of Plaguefall. No one knows where it came from.]], },
	[1446] = { name = [[Tazavesh Gearglider]], sourceText = '|cFFFFD200Achievement: |rFake It \'Til You Make It|n|cFFFFD200Category: |rShadowlands Dungeon',  description = [[Brokers of all types use these gliders to travel around Tazavesh and similar nearby outposts, and carry their goods as well.]], },
	[1448] = { name = [[Serenade]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[These beautiful helicids emerge only at night. When they are pleased, their shells hum with a joyful melody.]], },
	[1449] = { name = [[Lord of the Corpseflies]], sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Drop:|r Fleshwing|n|cFFFFD200Zone:|r Korthia ',  description = [[To be the Lord of the Corpseflies, you've got to lay a LOT of eggs.]], },
	[1450] = { name = [[Soaring Razorwing]], sourceText = '|cFFFFD200Vendor: |rArchivist Roh-Suir|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Faction: |rThe Archivists\' Codex - Tier 6|n|cFFFFD200Cost: |r5000|Hcurrency:1931|h|TInterface/ICONS/INV_Misc_PaperBundle04a.BLP:0|t|h',  description = [[The hide of the razorwing is light, allowing it to fly, but also quite tough, allowing them to survive passage into the Maw.]], },
	[1451] = { name = [[Vicious War Croaker]], sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 3|nRated Arena and Rated Battleground Season Reward',  description = [[These creatures are so enthusiastic when charging into battle that they will croak out something that sounds very much like "FOR THE HORDE!"]], },
	[1452] = { name = [[Vicious War Croaker]], sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 3|nRated Arena and Rated Battleground Season Reward',  description = [[These creatures are so enthusiastic when charging into battle that they will croak out something that sounds very much like "FOR THE ALLIANCE!"]], },
	[1454] = { name = [[Tamed Mauler]], sourceText = '|cFFFFD200Drop:|r Supplies of the Archivist\'s Codex|n|cFFFFD200Zone:|r Korthia |n',  description = [[When it comes to taming devourers, all it takes is a little anima and a lot of patience. And a lot of healing potions.]], },
	[1455] = { name = [[Beryl Shardhide]], sourceText = '|cFFFFD200Drop:|r Death\'s Advance Supplies|n|cFFFFD200Zone:|r Korthia',  description = [[These shardhides find it easy to hide in the underbrush, and it is rare to find one that is tamed.]], },
	[1456] = { name = [[Sapphire Skyblazer]], sourceText = '|cFFFFD200Promotion|r |n',  description = [[These brilliant blue birds burn brighter (and hotter) than their orange cousins, though the origin of both remains a mystery.]], },
	[1458] = { name = [[Wandering Ancient]], sourceText = '|cFFFFD200Promotion|r',  description = [[A staunch defender of the forests of Azeroth, the ancients are also known for their glorious canopies of leaves, which change color with the seasons.]], },
	[1459] = { name = [[Vicious War Gorm]], sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 2|nRated Arena and Rated Battleground Season Reward',  description = [[The gorm love eating the corpses of your enemies, so this seemed like a natural fit.]], },
	[1460] = { name = [[Vicious War Gorm]], sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 2|nRated Arena and Rated Battleground Season Reward',  description = [[The gorm love eating the corpses of your enemies, so this seemed like a natural fit.]], },
	[1465] = { name = [[Vicious Warstalker]], sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 4|nRated Arena and Rated Battleground Season Reward',  description = [[Trample the Horde to dust while riding a majestic protoform wolf! For the Alliance!]], },
	[1466] = { name = [[Vicious Warstalker]], sourceText = '|cFFFFD200PvP:|r Shadowlands: Season 4|nRated Arena and Rated Battleground Season Reward',  description = [[Trample the Alliance to dust while riding a majestic protoform wolf! For the Horde!]], },
	[1467] = { name = [[Noble Bruffalon]], sourceText = '|cFFFFD200Vendor: |rStorykeeper Ashekh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r100000|Hcurrency:2118|h|TInterface/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h',  description = [[It is a great honor to be trusted with one of the Maruuk\'s loyal hunting companions. This Bruffalon is reliable and steadfast through any hardship.]], },
	[1469] = { name = [[Magmashell]], sourceText = '|cFFFFD200Item: |rEmpty Magma Shell|n|cFFFFD200Zone: |rThe Waking Shores',  description = [[Who\'s a good little new-forged snail made entirely of elemental lava? You are! Yes, you!]], },
	[1471] = { name = [[Vengeance]], sourceText = '|cFFFFD200Drop:|r Lady Sylvanas Windrunner|n|cFFFFD200Zone:|r Sanctum of Domination|n|cFFFFD200Difficulty:|r Mythic',  description = [[Sylvanas lost a part of herself the day Arthas took her life, and the darkness within her grew to fill the void.]], },
	[1475] = { name = [[Hand of Bahmethra]], sourceText = '|cFFFFD200Drop:|r Tormentors of Torghast|n|cFFFFD200Zone:|r The Maw|n',  description = [[The most wicked and cruel of her kin, Bahmethra endured the full measure of the Banished One\'s punishments before her fall.]], },
	[1476] = { name = [[Wild Hunt Legsplitter]], sourceText = '|cFFFFD200Drop:|r War Chest of the Wild Hunt|n|cFFFFD200Zone:|r The Maw',  description = [[Integrated into the depleted forces of the Wild Hunt, the fawn riders quickly found that the gorm\'s powerful mandibles were perfect for cutting down close enemy ranks.]], },
	[1477] = { name = [[Undying Darkhound]], sourceText = '|cFFFFD200Drop:|r War Chest of the Undying Army|n|cFFFFD200Zone:|r The Maw ',  description = [[The Undying Darkhound\'s parts have graced many battlefields, yet this latest battle may have been the first time they all fought together.]], },
	[1478] = { name = [[Skyskin Hornstrider]], sourceText = '|cFFFFD200Vendor:|r Brendormi|n|cFFFFD200Cost:|r 150|Hitem:202039|h|TInterface/ICONS/INV_misc_stormlordsfavor.blp:0|t|h 3000|Hcurrency:2118|h|TInterface/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h',  description = [[The instability of primalist storms in potential timelines sometimes causes creatures to absorb the energies from the very storms. These creatures tend to be docile towards those who are able to face the fury of these chronomatic anomalies.]], },
	[1480] = { name = [[Unchained Gladiator\'s Soul Eater]], sourceText = '|cFFFFD200Achievement: |rGladiator: Shadowlands Season 2|n|cFFFFD200Category: |rPlayer vs. Player',  description = [[This soul eater\'s gaze unnerves you. Best not meet its eyes for too long.]], },
	[1481] = { name = [[Cartel Master\'s Gearglider]], sourceText = '|cFFFFD200Drop:|r So\'leah|n|cFFFFD200Zone:|r Tazavesh, the Veiled Market|n',  description = [[Only the richest cartel leaders are able to afford the metals required to craft the Cartel Master\'s Gearglider.]], },
	[1484] = { name = [[Ardenweald Wilderling]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Zone: |rArdenweald',  description = [[As anima flows to the distant wealds of Ardenweald once more, the wilderlings awaken and cautiously emerge from their canopy burrows.]], },
	[1485] = { name = [[Autumnal Wilderling]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Vendor: |rElwyn|n|cFFFFD200Zone: |rArdenweald|n|cFFFFD200Cost: |r7500|Hcurrency:1813|h|TInterface/ICONS\Spell_AnimaBastion_Orb.BLP:0|t|h',  description = [[The wilderlings are benevolent, magical creatures of Ardenweald, and are drawn to souls that are strong and pure.]], },
	[1486] = { name = [[Winter Wilderling]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Cost: |r1000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h|n|cFFFFD200Achievement: |rOn the Offensive',  description = [[Wilderlings serve as support units in battle, preserving their allies and rendering enemy magics useless.]], },
	[1487] = { name = [[Summer Wilderling]], sourceText = '|cFFFFD200Covenant: |rNight Fae|n|cFFFFD200Drop:|r Escaped Wilderling|n|cFFFFD200Zone:|r Korthia |n',  description = [[Stubborn and rebellious, this wilderling is loyal to very few. But those few are bonded for life.]], },
	[1489] = { name = [[Obsidian Gravewing]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Vendor: |rChachi the Artiste|n|cFFFFD200Zone: |rRevendreth|n|cFFFFD200Cost: |r7500|Hcurrency:1813|h|TInterface/ICONS\Spell_AnimaBastion_Orb.BLP:0|t|h',  description = [[In the darkened skies of Revendreth, you cannot see these creatures coming until it is too late.]], },
	[1490] = { name = [[Sinfall Gravewing]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Zone: |rRevendreth',  description = [[With anima once again flowing in Sinfall, the Stonewright has been able to infuse these Stoneborn, making them valuable allies in the battles to come.]], },
	[1491] = { name = [[Pale Gravewing]], sourceText = '|cFFFFD200Covenant: |rVenthyr|n|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Cost: |r1000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h|n|cFFFFD200Achievement: |rOn the Offensive',  description = [[Gravewings know no fear and are nearly indestructible -- perfect for an assault on the Maw.]], },
	[1492] = { name = [[Elysian Aquilon]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Zone: |rBastion',  description = [[With anima once again flowing to the Eternal Forge in Bastion, the plans for the aquilon centurion could be crafted once more.]], },
	[1493] = { name = [[Forsworn Aquilon]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Drop:|r Wild Worldcracker|n|cFFFFD200Zone:|r Korthia |n',  description = [[Originally produced by the Forsworn with stolen blueprints, these powerful centurion are now earning a reputation across the Shadowlands.]], },
	[1494] = { name = [[Ascendant\'s Aquilon]], sourceText = '|cFFFFD200Covenant: |rKyrian|n|cFFFFD200Vendor: |rAdjutant Galos|n|cFFFFD200Zone: |rBastion|n|cFFFFD200Cost: |r7500|Hcurrency:1813|h|TInterface/ICONS\Spell_AnimaBastion_Orb.BLP:0|t|h',  description = [[The aquilon can rapidly transport aspirants, stewards, and even ascended to a location. Hey, sometimes your wings get tired.]], },
	[1495] = { name = [[Maldraxxian Corpsefly]], sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Zone: |rMaldraxxus',  description = [[Corpseflies were once found throughout Maldraxxus, but many died out in the absence of anima. With anima flowing once again, they swarm.]], },
	[1496] = { name = [[Regal Corpsefly]], sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Vendor: |rSu Zettai|n|cFFFFD200Zone: |rMaldraxxus|n|cFFFFD200Cost: |r7500|Hcurrency:1813|h|TInterface/ICONS\Spell_AnimaBastion_Orb.BLP:0|t|h',  description = [[Only the forces of Maldraxxus would call a corpsefly "regal."]], },
	[1497] = { name = [[Battlefield Swarmer]], sourceText = '|cFFFFD200Covenant: |rNecrolord|n|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Cost: |r1000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h|n|cFFFFD200Achievement: |rOn the Offensive',  description = [[Corpseflies are strongly attracted to the smell of dead bodies and will swarm an active battlefield as soon as the first corpse falls.]], },
	[1500] = { name = [[Sanctum Gloomcharger]], sourceText = '|cFFFFD200Drop:|r The Nine|n|cFFFFD200Zone:|r Sanctum of Domination',  description = [[Only the Jailer\'s most powerful lieutenants are gifted these steeds to stalk the halls of the Tower.]], },
	[1501] = { name = [[Soulbound Gloomcharger]], sourceText = '|cFFFFD200Drop:|r Mysterious Gift from Ve\'nari|n|cFFFFD200Zone:|r The Maw',  description = [[Ve'nari has many methods of sneaking around the Maw and disrupting the Jailer\'s plans, including binding his own minions.]], },
	[1502] = { name = [[Fallen Charger]], sourceText = '|cFFFFD200Drop:|r Fallen Charger|n|cFFFFD200Zone:|r The Maw',  description = [[The charger\'s screams echo across the Maw as it continues a never-ending ride across the land, forever looking for escape from the endless torments it has been forced to endure.]], },
	[1503] = { name = [[Hand of Nilganihmaht]], sourceText = '|cFFFFD200Zone:|r The Maw',  description = [[Of all the giants who dared defy the Jailer, Nilganihmaht was the most arrogant. For this transgression, the giant\'s severed hand was hidden within a veil of torments, and the rings that could summon it back were scattered across the Maw.]], },
	[1504] = { name = [[Hand of Salaranga]], sourceText = '|cFFFFD200Achievement:|r Breaking the Chains|n|cFFFFD200Zone:|r Korthia and The Maw',  description = [[Salaranga was the most cunning of her kin and hid amongst the Beastwarrens. She struck at the Banished One with a horde of bestial allies. It did not matter.]], },
	[1505] = { name = [[Amber Shardhide]], sourceText = '|cFFFFD200Vendor: |rDuchess Mynx|n|cFFFFD200Zone: |rKorthia|n|cFFFFD200Faction: |rDeath\'s Advance - Revered|n|cFFFFD200Cost: |r5000|Hcurrency:1767|h|TInterface/ICONS/INV_Stygia.BLP:0|t|h',  description = [[The bright colors of Korthian wildlife indicate that they have either few predators, or are tough enough to fight back.]], },
	[1506] = { name = [[Crimson Shardhide]], sourceText = '|cFFFFD200Drop:|r Malbog|n|cFFFFD200Zone:|r Korthia',  description = [[When Korthia was pulled into the maw, only a few hardy species survived.]], },
	[1507] = { name = [[Darkmaul]], sourceText = '|cFFFFD200Zone:|r Korthia',  description = [[An older shardhide, Darkmaul was relatively tame and friendly around Korthians. He was thought lost to the calamity.]], },
	[1508] = { name = [[Fierce Razorwing]], sourceText = '|cFFFFD200Drop:|r Death\'s Advance Supplies|n|cFFFFD200Zone:|r Korthia',  description = [[Razorwings generally stay away from other Korthian species, but with time and patience, they can be tamed as well.]], },
	[1509] = { name = [[Garnet Razorwing]], sourceText = '|cFFFFD200Drop:|r Reliwik the Defiant|n|cFFFFD200Zone:|r Korthia',  description = [[Korthia was once known for its bright red flowers. Several species were known for consuming or rolling in them, and taking on bright red coloration as a result.]], },
	[1510] = { name = [[Dusklight Razorwing]], sourceText = '|cFFFFD200Zone:|r Korthia',  description = [[Razorwings can be seen throughout Korthia, making their nests on high hills and in trees.]], },
	[1511] = { name = [[Maelie, the Wanderer]], sourceText = '|cFFFFD200Zone:|r Korthia',  description = [[A curious and inquisitive doe, she doesn't mean to worry her master, she just gets distracted easily. Especially with all of the new things to see on Korthia.]], },
	[1513] = { name = [[Sarge\'s Tale]], sourceText = '|cFFFFD200Promotion|r|n',  description = [[Hold on a sec, Sarge isn't that large, someone must have spun a tall tale in the Hearthstone Tavern...]], },
	[1514] = { name = [[Rampaging Mauler]], sourceText = '|cFFFFD200Drop:|r Konthrogz the Obliterator|n|cFFFFD200Zone:|r Korthia',  description = [[One would think controlling a rampaging mount would be difficult, but if you can learn to control the direction of the rampage it can be quite useful.]], },
	[1520] = { name = [[Soultwisted Deathwalker]], sourceText = '|cFFFFD200Achievement: |rShadowlands Keystone Master: Season Two|n|cFFFFD200Category: |rDungeons',  description = [[A howling vortex of tormented souls, ridden only by those who wish to demonstrate their impressive tolerance for pain and suffering.]], },
	[1521] = { name = [[Val'sharah Hippogryph]], sourceText = '|cFFFFD200Vendor: |rAridormi|n|cFFFFD200Zone: |rDalaran|n|cFFFFD200Cost: |r5000|Hcurrency:1166|h|TInterface/ICONS/PVECurrency-Justice.blp:0|t|h',  description = [[These majestic hippogryphs are as close to a being of the Emerald Dream as any resident of Azeroth can be, and do not bond with a rider easily.]], },
	[1522] = { name = [[Heartlight Vombata]], sourceText = '|cFFFFD200Vendor: |rVilo|n|cFFFFD200Zone: |rZereth Mortis|n|cFFFFD200Faction: |rThe Enlightened - Exalted|n|cFFFFD200Cost: |r5000|Hcurrency:1813|h|TInterface/ICONS\Spell_AnimaBastion_Orb.BLP:0|t|h',  description = [[The Enlightened revere all creations of the First Ones, yet for many, the vombata of Zereth Mortis are the most beloved. These creatures are equal parts trusted beast of burden and beloved companion.]], },
	[1523] = { name = [[Curious Crystalsniffer]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Most vombata enjoy tunneling through the ground, but these sniffers seem to have an unerring ability to locate crystals buried deep underground and will congregate around large deposits.]], },
	[1524] = { name = [[Darkened Vombata]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[The schematic to synthesize these darkened vombata seem to have appeared as a reaction to the Mawsworn invasion. The resulting vombata look different--darker, much like the invaders--but behave normally.]], },
	[1525] = { name = [[Adorned Vombata]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Red-colored vombata are considered lucky by the Enlightened. It was a red vombata that was present when they first arrived in Zereth Mortis.]], },
	[1526] = { name = [[Deathrunner]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[These wild cervids are only truly at peace when running through--or between--the lands of Death.]], },
	[1528] = { name = [[Sundered Zerethsteed]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Whatever the purpose of these cervids once was, it is long forgotten. Though they are not Dominated, they have been corrupted by the unintended presence of Mawsworn in the sacred realm of Zereth Mortis.]], },
	[1529] = { name = [[Anointed Protostag]], sourceText = '|cFFFFD200Vendor: |rVilo|n|cFFFFD200Zone: |rZereth Mortis|n|cFFFFD200Faction: |rThe Enlightened - Revered|n|cFFFFD200Cost: |r5000|Hcurrency:1813|h|TInterface/ICONS\Spell_AnimaBastion_Orb.BLP:0|t|h',  description = [[Only those most esteemed by the Enlightened are trusted with the reins of this rare cervid.]], },
	[1531] = { name = [[Wen Lo, the River\'s Edge]], sourceText = '|cFFFFD200In Game Shop|r|n',  description = [[The rivers of Pandaria, like Wen Lo, are unpredictable in nature. They may remain calm, bringing water to the crops, or flood, bringing ruin. Wen Lo reminds us that we must adapt to the challenges of life.]], },
	[1532] = { name = [[Soaring Spelltome]], sourceText = '|cFFFFD200Achievement: |rA Tour of Towers|n|cFFFFD200Category: |rTimewalking',  description = [[In the forgotten depths of the Dalaran library, this oversized tome was found flapping madly around the room. Open the pages of this arcane-infused book and hitch a ride to magical adventure.]], },
	[1533] = { name = [[Forged Spiteflyer]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[There\'s nothing that can make you feel as alive quite like riding through the realm of Death while atop a being that hates you.]], },
	[1534] = { name = [[Buzz]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Buzz buzz buzz, buzz buzz! Buzz buzz buzz buzz buzz. Buzz buzz buzz, buzz buzz buzz.]], },
	[1535] = { name = [[Bronzewing Vespoid]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Bronzewing vespoids are the result of attempting to synthesize a vespoid queen with no other members of a new hive. While no mortal has yet discovered how to synthesize a hive, the bronzewings are reliable mounts just as they are.]], },
	[1536] = { name = [[Mawdapted Raptora]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Since the Jailer\'s incursion, new raptora have been synthesized by Zereth Mortis that are capable of resisting the corrupting influence of the Maw.]], },
	[1537] = { name = [[Raptora Swooper]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[The raptora swooper is one of the most vicious iterations of its kind, perfectly suited for its ecological niche. Needing no further adjustments, the ones synthesized today are identical to those created in the past.]], },
	[1538] = { name = [[Bronze Helicid]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Some helicids of Zereth Mortis have strangely metallic shells. The ones who do are generally a bronze color, indistinct from the metal any novice miner could work, save that this creature\'s construction far surpasses the skill of any novice.]], },
	[1539] = { name = [[Unsuccessful Prototype Fleetpod]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[This creature was not designed by the First Ones, but instead by an unknown being attempting to create a very fast runner. Needless to say the attempt failed, because this mount is very slow.]], },
	[1540] = { name = [[Scarlet Helicid]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[This helicid\'s unusual reddish tint is clearly intentional in its design, but the only other difference between these and other helicids is the unusual lack of any ooze.]], },
	[1541] = { name = [[Genesis Crawler]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[The Enlightened who have observed tarachnids believe that this variety may be some of the first beings to ever walk the land of Zereth Mortis.]], },
	[1542] = { name = [[Tarachnid Creeper]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[These tarachnids make their home in the darkest corners of Zereth Mortis, coming out only to hunt (or, sometimes, carry a rider around).]], },
	[1543] = { name = [[Ineffable Skitterer]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[These tarachnids move with a purpose unknown to others, answering only to a higher calling. Whatever that may be, it appears to include routine assistance to mortals who need a ride. For now.]], },
	[1544] = { name = [[Wastewarped Deathwalker]], sourceText = '|cFFFFD200Achievement: |rShadowlands Keystone Master: Season Three|n|cFFFFD200Category: |rDungeons',  description = [[Raw swirling energy bound together into a cohesive form, it will only bend to the will of those strong enough to bind it.]], },
	[1545] = { name = [[Divine Kiss of Ohn'ahra]], sourceText = '|cFFFFD200Quest:|r A Whispering Breeze|n|cFFFFD200Zone:|r Ohn\'ahran Plains',  description = [[...as though welcomed by the wind itself.]], },
	[1546] = { name = [[Iskaara Trader\'s Ottuk]], sourceText = '|cFFFFD200Vendor: |rTattukiaka|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r1|Hitem:195502|h|TInterface/ICONS/INV_10_DungeonJewelry_Primalist_Trinket_4_Earth.BLP:0|t|h 1|Hitem:195496|h|TInterface/ICONS/INV_10_DungeonJewelry_Primalist_Necklace_1_Air.BLP:0|t|h',  description = [[There are no guarantees when you purchase an ottuk from Tattukiaka. At least they're cute.]], },
	[1547] = { name = [[Goldplate Bufonid]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[This armored bufonid is always ready to leap into battle... as long as that battle is really, really close. In a pinch, it can leap multiple times and eventually reach the battle.]], },
	[1549] = { name = [[Shimmering Aurelid]], sourceText = '|cFFFFD200Achievement: |rGlory of the Sepulcher Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Though it was once created to defend the Sepulcher of the First Ones, with its primary function now completed, it now obediently follows your command.]], },
	[1551] = { name = [[Cryptic Aurelid]], sourceText = '|cFFFFD200Achievement: |rFrom A to Zereth|n|cFFFFD200Category: |rShadowlands',  description = [[The Cyphers of the First Ones are beyond any mortal understanding. A curious effect of the language is that, for reasons unknown, some aurelids are drawn to mortals who aspire to unlock its mystery.]], },
	[1552] = { name = [[Carcinized Zerethsteed]], sourceText = '|cFFFFD200Quest:|r Leading Motives',  description = [[The few of these mysterious creatures that remain appear to be impossibly old, almost timeless. Perhaps they were created by the First Ones, because no schematic to synthesize these has yet been discovered.]], },
	[1553] = { name = [[Liberated Slyvern]], sourceText = '|cFFFFD200Drop: |rBreezebiter|n|cFFFFD200Zone: |rThe Azure Span',  description = [[It\\'s difficult to tell because It\\'s far too angry, but this slyvern is actually glad It\\'s not owned by gnolls anymore.]], },
	[1556] = { name = [[Tangled Dreamweaver]], sourceText = '|cFFFFD200Promotion:|r Dragonflight Heroic Edition|n',  description = [[The last of the sleepers are finally awakening, bringing the dreams of the dragons back to the lands.]], },
	[1563] = { name = [[Highland Drake]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Highland Drake|n',  description = [[The drakes of Azure Span follow a strict personal code of honor as they explore the isles anew.]], },
	[1564] = { name = [[Colossal Soulshredder Mawrat]], sourceText = '|cFFFFD200Achievement:|r Flawless Master (Layer 16)|n|cFFFFD200Category:|r Torghast|n',  description = [[The incredible prowess you have demonstrated within the Jailer\'s Tower has instilled sufficient respect into this fearsome creature, enabling its use as a mount.]], },
	[1565] = { name = [[Colossal Umbrahide Mawrat]], sourceText = '|cFFFFD200Drop:|r Torghast Layers 13 or higher|n|cFFFFD200Location:|r Torghast|n',  description = [[This disturbingly large Mawrat is an anomaly amongst its species. These Colossal Mawrats lurk only in the most distant and darkest reaches of Torghast.]], },
	[1566] = { name = [[Colossal Ebonclaw Mawrat]], sourceText = '|cFFFFD200Achievement:|r The Jailer\'s Gauntlet: Layer 4|n|cFFFFD200Category:|r Torghast|n',  description = [[This vicious and cunning creature has grown to its astonishing size by not only feasting upon the weakened souls in the Jailer\'s Tower, but also vulnerable Mawsworn.]], },
	[1567] = { name = [[Lightforged Ruinstrider]], sourceText = '',  description = [[]], },
	[1568] = { name = [[Lightforged Ruinstrider]], sourceText = '|cFFFFD200Paladin|r |n',  description = [[A loyal steed to the Lightforged Draenei.]], },
	[1569] = { name = [[Patient Bufonid]], sourceText = '|cFFFFD200Quest: |rThe Patient Bufonid|n|cFFFFD200Zone: |rZereth Mortis',  description = [[Some bufonids will sit perfectly still for hours or days at a time if left to their own devices. Fortunately, they're also quite agreeable if a rider would like to move more often than that.]], },
	[1570] = { name = [[Prototype Leaper]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[An ancient schematic allows for the instantiation of this early, but functional, bufonid design.]], },
	[1571] = { name = [[Russet Bufonid]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[Russet is a rare color among bufonids that is normally only seen on specimens that are tainted by other cosmic forces, or synthesized from a perplexing schematic that invokes that color.]], },
	[1572] = { name = [[Cosmic Gladiator\'s Soul Eater]], sourceText = '|cFFFFD200Achievement: |rGladiator: Shadowlands Season 3|n|cFFFFD200Category: |rPlayer vs. Player',  description = [[This soul eater\'s gaze unnerves you. Best not meet its eyes for too long.]], },
	[1573] = { name = [[Magenta Cloud Serpent]], sourceText = '|cFFFFD200Trading Post|r',  description = [[More affectionate than their cousins, Magenta Cloud Serpents are known to form close bonds with their riders.]], },
	[1574] = { name = [[Crusty Crawler]], sourceText = '|cFFFFD200Trading Post|r',  description = [[This rare giant crab has taken a break from its nautical nonsense deep under the sea to explore the surface.]], },
	[1575] = { name = [[Quawks]], sourceText = '|cFFFFD200Trading Post|r',  description = [[Squawks\'s older (and somehow louder) brother appears to tolerate your presence. For now. You should probably track down some crackers while you still can.]], },
	[1576] = { name = [[Jigglesworth Sr.]], sourceText = '|cFFFFD200Achievement:|r Fates of the Shadowlands Raids|n',  description = [[It\\'s unclear how the saddle stays atop this formerly feline friend, but somehow the House of Plagues made it work. Science.]], },
	[1577] = { name = [[Ash'adar, Harbinger of Dawn]], sourceText = '|cFFFFD200Trading Post|r',  description = [[Kaldorei legends whisper of a relentless hunter prowling amongst the deepest shadows of Kalimdor, never sleeping. By the light of the sun or the moon, Ash'adar now hunts with you.]], },
	[1578] = { name = [[[DND] Test Mount JZB]], sourceText = '|cFFFFD200Vendor: |rUncle Bigpocket|cFFFFD200Zone: |rKun-Lai Summit|cFFFFD200Cost: |r120000|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[]], },
	[1579] = { name = [[Coral-Stalker Waveray]], sourceText = '|cFFFFD200Trading Post|r',  description = [[This species of ray seems to have evolved to stalk its prey amidst the brightly colored corals of the seas.]], },
	[1580] = { name = [[Heartbond Lupine]], sourceText = '|cFFFFD200Profession: |rProtoform Synthesis',  description = [[From the moment of its creation, this packless lupine has held nothing more than deepest affection and loyalty towards you.]], },
	[1581] = { name = [[Nether-Gorged Greatwyrm]], sourceText = '|cFFFFD200In-Game Shop|r|n',  description = [[Few mana wyrms find sufficiently stable sources of arcane power to reach such a size. A greatwyrm\'s appetite for mana is unmatched.]], },
	[1582] = { name = [[Savage Green Battle Turtle]], sourceText = '|cFFFFD200Trading Post|r',  description = [[Turtle too slow to chase down your foes? Just add cannons. Nothing can outrun a cannon.]], },
	[1583] = { name = [[Armored Siege Kodo]], sourceText = '|cFFFFD200In-Game Shop|r',  description = [[Only the strongest and most fearless of kodos can be trained to become living siege weapons. Even the highest walls won't keep them out for long.]], },
	[1584] = { name = [[Colossal Plaguespew Mawrat]], sourceText = '|cFFFFD200Drop:|r Rhuv, Gorger of Ruin|n',  description = [[These rodents became Iska\'s infamous sentries, scouting and carrying her plague across the Maw. Their presence is only followed by ruin.]], },
	[1585] = { name = [[Colossal Wraithbound Mawrat]], sourceText = '|cFFFFD200Drop:|r Mawsworn Supply Chest|n',  description = [[Infused by the energies of tormented souls, these creatures have adopted ghostlike abilities that make them quite elusive.]], },
	[1586] = { name = [[Armored Golden Pterrordax]], sourceText = '|cFFFFD200Trading Post|r',  description = [[Painted and adorned with gold, these Pterrordax patrol the skies over the tomb of Atal'Dazar.]], },
	[1587] = { name = [[Zereth Overseer]], sourceText = '|cFFFFD200Drop: |rThe Jailer|n|cFFFFD200Zone: |rSepulcher of the First Ones|n|cFFFFD200Difficulty: |rMythic',  description = [[Enigmatic and aloof, this construct of the First Ones flies on a graceful stream of perfect geometric understanding.]], },
	[1588] = { name = [[Winding Slitherdrake]], sourceText = '|cFFFFD200Achievement:|r A Serpentine Discovery',  description = [[These drakes feel at home in the depths of the earth and the far reaches of the sky.]], },
	[1589] = { name = [[Renewed Proto-Drake]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles: A New Friend',  description = [[As the isles awaken, these proto-drakes answer the call to soar the skies alongside mortals.]], },
	[1590] = { name = [[Windborne Velocidrake]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Windborne Velocidrake',  description = [[The long talons of the velocidrake give it an edge on the plains.]], },
	[1591] = { name = [[Cliffside Wylderdrake]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Cliffside Wylderdrake',  description = [[Often roosting along the steep cliff faces of Thaldraszus, they're swift to the skies in times of danger.]], },
	[1594] = { name = [[Jade, Bright Foreseer]], sourceText = '|cFFFFD200In Game Shop|r|n',  description = [[The presence of this majestic Pandaren rabbit spirit brings joy and happiness and is heralded as a wonderful omen. Legend says that in its eyes, you can glimpse the greatest happiness of your life, even if it is yet to come.]], },
	[1595] = { name = [[Cerulean Marsh Hopper]], sourceText = '|cFFFFD200Trading Post|r',  description = [[Gottum\'s expansion into export markets was inevitable with news of the Trading Post reaching even into the depths of the Frogmarsh.]], },
	[1596] = { name = [[Amalgam of Rage]], sourceText = '|cFFFFD200Promotion:|r Diablo IV',  description = [[The fearsome beast ridden by Astaroth himself is now yours to command.]], },
	[1597] = { name = [[Grimhowl]], sourceText = '|cFFFFD200Quest: |rGood Fiery Boy|n|cFFFFD200Zone: |rBlackrock Depths',  description = [[Otherwise known as The Flame Woof, the Fetcher of the Igneous, Molten Core\'s Best Friend, Forgehound Maximus, the Best Fiery Boy, and "oh no is that an AXE on his face!?"]], },
	[1599] = { name = [[Eternal Gladiator\'s Soul Eater]], sourceText = '|cFFFFD200Achievement: |rGladiator: Shadowlands Season 4|n|cFFFFD200Category: |rPlayer vs. Player',  description = [[This soul eater\'s gaze unnerves you. Best not meet its eyes for too long.]], },
	[1600] = { name = [[Elusive Emerald Hawkstrider]], sourceText = '|cFFFFD200Quest: |rBlood Knight|n|cFFFFD200Zone: |rGhostlands|n|n|cFFFFD200Quest: |rVictory for the Sin\'dorei|n|cFFFFD200Zone: |rGhostlands',  description = [[Unseen for many years, this brightly hued hawkstrider emerged from hiding to help defend the Ghostlands from the Scourge. Its prowess shines as brilliantly as its feathers.]], },
	[1602] = { name = [[Tuskarr Shoreglider]], sourceText = '|cFFFFD200Promotion|r|n',  description = [[The Tuskarr learned long ago that some of the bones of the sea creatures they hunted were light and buoyant, making for effective glider materials.]], },
	[1603] = { name = [[Subterranean Magmammoth]], sourceText = '|cFFFFD200Vendor: |rSacratros|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r100|Hitem:204727|h|TInterface/ICONS/INV_Misc_CuriousCoin.BLP:0|t|h',  description = [[Awakened from a long slumber, this beast has yet to see the light of day.]], },
	[1605] = { name = [[Dragon Isles Drake Model Test]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles Drake|n',  description = [[NYI]], },
	[1607] = { name = [[Swift Spectral Drake]], sourceText = '|cFFFFD200Legacy|r',  description = [[Even the dead need a lift every now and then.]], },
	[1612] = { name = [[Loyal Magmammoth]], sourceText = '|cFFFFD200Vendor: |rYries Lightfingers|n|cFFFFD200Zone: |rThe Waking Shores',  description = [[Liberated from the Qalashi and harnessed by your ingenuity (and your harness), this magmammoth is ready to lava stomp wherever you want to go.]], },
	[1615] = { name = [[Tamed Skitterfly]], sourceText = '|cFFFFD200Faction:|r Dragonscale Expedition|n|cFFFFD200Renown:|r 25|n|cFFFFD200Vendor:|r Granpap Whiskers|n|cFFFFD200Location:|r Dragonscale Basecamp',  description = [[It didn't take long for Granpap Whiskers to tame the skitterflies around the Dragonscale Basecamp and set them to helping move Expedition personnel and cargo.]], },
	[1616] = { name = [[Azure Skitterfly]], sourceText = '|cFFFFD200Faction:|r Dragonscale Expedition|n|cFFFFD200Renown:|r 25|n|cFFFFD200Vendor:|r Granpap Whiskers|n|cFFFFD200Location:|r Dragonscale Basecamp|n|cFFFFD200Cost:|r 5|Hitem:201401|h|TInterface/ICONS/INV_Icon_Feather05d.BLP:0|t|h 20|Hitem:193053|h|TInterface/ICONS/INV_Feather_11.blp:0|t|h 750|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = [[Once Granpap Whiskers finally taught them how to take riders, the azure skitterflies proved to bond closely and loyally to their riders.]], },
	[1617] = { name = [[Verdant Skitterfly]], sourceText = '|cFFFFD200Faction:|r Dragonscale Expedition|n|cFFFFD200Renown:|r 25|n|cFFFFD200Treasure:|r Expedition Scout\'s Pack',  description = [[Verdant skitterflies are the perfect mount for the scouts of the Dragonscale Expedition scouts, as they are fast and blend into the foliage of the Dragon Isles.]], },
	[1619] = { name = [[Ancient Salamanther]], sourceText = '|cFFFFD200Drop:|r The Forbidden Reach|n',  description = [[Due to their preference for dark caves and murky waters, sightings of this unique breed of salamanther are so rare that many researchers thought they had gone extinct.]], },
	[1622] = { name = [[Stormhide Salamanther]], sourceText = '|cFFFFD200Vendor:|r Mythressa|n|cFFFFD200Cost:|r 2000|Hcurrency:2118|h|TInterface/ICONS/INV_Misc_Powder_Thorium.blp:0|t|h',  description = [[Sometimes when storm energies infuse a creature, it simply... becomes larger and more placid. Few salamanthers are willing to be ridden, but these children of the storm are a notable exception.]], },
	[1623] = { name = [[Seething Slug]], sourceText = '|cFFFFD200Treasure: |rSeething Cache|n|cFFFFD200Zone: |rZaralek Cavern',  description = [[Drawn forth from its abyss by the rage of the Zaqali.]], },
	[1626] = { name = [[Shellack]], sourceText = '|cFFFFD200Achievement:|r Glory of the Dragonflight Hero|n',  description = [[Fed by victory and hardened by glory, this snail is nothing less than a tribute to mastery.]], },
	[1627] = { name = [[Gooey Snailemental]], sourceText = '|cFFFFD200Drop:|r The Forbidden Reach|n',  description = [[Formed from the leftover elemental energies of the Primal Storms, this magical mollusk is faster than it appears.]], },
	[1629] = { name = [[Scrappy Worldsnail]], sourceText = '|cFFFFD200Vendor: |rDealer Vexil|n|cFFFFD200Zone: |rThe Waking Shores|n|cFFFFD200Cost: |r1000|Hitem:202173|h|TInterface/ICONS/Creatureportrait_Twilightshammer_lava_magicball.blp:0|t|h',  description = [[For the Worldbreakers, a portable footstool. For you, a hardy mount.]], },
	[1634] = { name = [[Mossy Mammoth]], sourceText = '|cFFFFD200Drop:|r Zskera Vaults|n',  description = [[Preserved for thousands of years in stone, bronze magic has nearly aged this mammoth back into a fossil.]], },
	[1635] = { name = [[Plainswalker Bearer]], sourceText = '|cFFFFD200World Event:|r Centaur Hunts (first hunt of each week)',  description = [[The mammoths of the Ohn'ahran Plains are unaccustomed to carrying people, as their centaur neighbors do not typically ride mounts. They can learn to bear riders, however. Some even do so enthusiastically.]], },
	[1639] = { name = [[Lizi, Thunderspine Tramper]], sourceText = '|cFFFFD200Quest: |rTo Tame A Thunderspine|n|cFFFFD200Zone: |rOhn\'ahran Plains',  description = [[Nothing will stop Lizi from her mission to eat all the world\'s fruit.]], },
	[1644] = { name = [[Raging Magmammoth]], sourceText = '|cFFFFD200Achievement: |rGlory of the Vault Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[Most lava flows. This lava charges.]], },
	[1645] = { name = [[Renewed Magmammoth]], sourceText = '|cFFFFD200Drop:|r Dreamsurges',  description = [[Reforged after destruction at the hands of the Druids of the Flame, this magmammoth is ready to stomp any foes in its path.]], },
	[1653] = { name = [[Brown War Ottuk]], sourceText = '|cFFFFD200Faction:|r Iskaara Tuskarr|n|cFFFFD200Renown:|r 30|n|cFFFFD200Vendor:|r Tatto|n|cFFFFD200Location:|r Iskaara|n|cFFFFD200Cost:|r 5|Hitem:201403|h|TInterface/ICONS/ACHIevement_Reputation_Tuskarr.blp:0|t|h 5|Hitem:201400|h|TInterface/ICONS/TRADE_ARCHAEOLOGY_SHARK JAWS.BLP:0|t|h 1000|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = [[The tuskarr of Iskaara are loathe to war against anyone, but many tuskarr and their allies have nonetheless taken up the spear with a war ottuk beside them.]], },
	[1654] = { name = [[Otterworldly Ottuk Carrier]], sourceText = '|cFFFFD200Achievement: |rThanks for the Carry!|n|cFFFFD200Category: |rMounts|n',  description = [[Some ottuks get into the spirit of carrying people around more than others.]], },
	[1655] = { name = [[Yellow War Ottuk]], sourceText = '|cFFFFD200Faction:|r Iskaara Tuskarr|n|cFFFFD200Renown:|r 30|n|cFFFFD200Vendor:|r Tatto|n|cFFFFD200Location:|r Iskaara|n|cFFFFD200Cost:|r 5|Hitem:201403|h|TInterface/ICONS/ACHIevement_Reputation_Tuskarr.blp:0|t|h 5|Hitem:201400|h|TInterface/ICONS/TRADE_ARCHAEOLOGY_SHARK JAWS.BLP:0|t|h 1000|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = [[The yellow ottuks of the Azure Span are sleek, sneaky, and smart. Those trained for war are the finest tuskarr cavalry mounts, equally capable on the ground and in the waters.]], },
	[1656] = { name = [[Otto]], sourceText = '|cFFFFD200Quest:|r The Way to an Otto\'s Heart|n',  description = [[Relax. The future will unfold as it was meant to, one way or an otter.]], },
	[1657] = { name = [[Brown Scouting Ottuk]], sourceText = '|cFFFFD200Faction:|r Iskaara Tuskarr|n|cFFFFD200Renown:|r 25|n|cFFFFD200Vendor:|r Tatto|n|cFFFFD200Location:|r Iskaara|n|cFFFFD200Cost:|r 2|Hitem:201403|h|TInterface/ICONS/ACHIevement_Reputation_Tuskarr.blp:0|t|h 2|Hitem:201400|h|TInterface/ICONS/TRADE_ARCHAEOLOGY_SHARK JAWS.BLP:0|t|h 750|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = [[Wily and playful in their natural habitat, ottuks possess keen eyes that can scout far afield as ably as they watch for predators.]], },
	[1658] = { name = [[Ivory Trader\'s Ottuk]], sourceText = '|cFFFFD200Vendor: |rTattukiaka|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Cost: |r1|Hitem:193696|h|TInterface/ICONS/INV_10_DungeonJewelry_Primalist_Ring_1_Earth.BLP:0|t|h 1|Hitem:193633|h|TInterface/ICONS/INV_10_DungeonJewelry_Tuskarr_Ring_2_Color4.BLP:0|t|h 1|Hitem:193708|h|TInterface/ICONS/INV_10_DungeonJewelry_Explorer_Ring_1_Color1.BLP:0|t|h',  description = [[No adult knows why some ottuks are white, but any tuskarr child can tell you: It\\'s because they eat too much snow.]], },
	[1659] = { name = [[Yellow Scouting Ottuk]], sourceText = '|cFFFFD200Faction:|r Iskaara Tuskarr|n|cFFFFD200Renown:|r 25|n|cFFFFD200Vendor:|r Tatto|n|cFFFFD200Location:|r Iskaara|n|cFFFFD200Cost:|r 2|Hitem:201403|h|TInterface/ICONS/ACHIevement_Reputation_Tuskarr.blp:0|t|h 2|Hitem:201400|h|TInterface/ICONS/TRADE_ARCHAEOLOGY_SHARK JAWS.BLP:0|t|h 750|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = [[The yellow scouting ottuk love to hide and they blend in well to their surroundings. When not 'playing' hide and seek with their riders, they are perfect companions when scouting enemy encampments.]], },
	[1660] = { name = [[Crimson Gladiator\'s Drake]], sourceText = '|cFFFFD200Achievement:|r Gladiator: Dragonflight Season 1|n|cFFFFD200Category:|r Player vs. Player',  description = [[Forged in the fires of conquest, and armored to the teeth.]], },
	[1662] = { name = [[Telix the Stormhorn]], sourceText = '|cFFFFD200Promotion|r |n',  description = [[Once a simple beetle of the Forbidden Reach, Telix became infused with storm magic when Raszageth was unleashed. With its newly heightened senses, Telix roams the Dragon Isles alongside the Dragonscale Expedition--and you!]], },
	[1664] = { name = [[Guardian Vorquin]], sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Trained using ancient dracthyr methods, these armored vorquin are the pinnacle of tactical mounted ground warfare.]], },
	[1665] = { name = [[Swift Armored Vorquin]], sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Trained using ancient dracthyr methods, these armored vorquin are the pinnacle of tactical mounted ground warfare.]], },
	[1667] = { name = [[Armored Vorquin Leystrider]], sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Trained using ancient dracthyr methods, these armored vorquin are the pinnacle of tactical mounted ground warfare.]], },
	[1668] = { name = [[Majestic Armored Vorquin]], sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r80|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Trained using ancient dracthyr methods, these armored vorquin are the pinnacle of tactical mounted ground warfare.]], },
	[1671] = { name = [[Duskwing Ohuna]], sourceText = '|cFFFFD200Vendor: |rCelestine of the Harvest|n|cFFFFD200Zone: |rThe Waking Shores|n|cFFFFD200Zone: |rOhn\'ahran Plains|n|cFFFFD200Zone: |rThe Azure Span|n|cFFFFD200Zone: |rThaldraszus|n|cFFFFD200Cost: |r1000|Hitem:207026|h|TInterface/ICONS/INV_Enchant_DustStrange.blp:0|t|h',  description = [[If you look to the night sky and see the clouds whisk by don't panic. It may just be the illusive Duskwing Ohuna.]], },
	[1672] = { name = [[Zenet Hatchling]], sourceText = '|cFFFFD200Drop:|r Zenet Egg|n|cFFFFD200Zone:|r Ohn\'ahran Plains',  description = [[The hardest winds begin with a gust and a flap.]], },
	[1674] = { name = [[Temperamental Skyclaw]], sourceText = '|cFFFFD200Vendor:|r Zon\'Wogi|n|cFFFFD200Zone:|r The Azure Span|n|n',  description = [[Slyverns are rarely comfortable acting as mounts. Only those willing to feed their gluttonous appetites and keep a careful eye on them should attempt to ride.]], },
	[1679] = { name = [[Frostbrood Proto-Wyrm]], sourceText = '|cFFFFD200Promotion:|r Wrath of the Lich King Classic|n',  description = [[The same magic of undeath that raised the Frostbrood into the Lich King\'s service has infused this primal wyrm. Its loyalty and ferocity now serve those who defend Azeroth.]], },
	[1681] = { name = [[Hailstorm Armoredon]], sourceText = '|cFFFFD200Achievement: |rDragonflight Keystone Master: Season One|n|cFFFFD200Category: |rDungeons',  description = [[Born in the middle of an ice storm, this armoredon prefers its meals partially frozen.]], },
	[1683] = { name = [[Crimson Vorquin]], sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Respected for their resilience in battle, even against dragonfire, these noble beasts are the prized mounts of the dracthyr cavalry.]], },
	[1684] = { name = [[Sapphire Vorquin]], sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Respected for their resilience in battle, even against dragonfire, these noble beasts are the prized mounts of the dracthyr cavalry.]], },
	[1685] = { name = [[Bronze Vorquin]], sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Respected for their resilience in battle, even against dragonfire, these noble beasts are the prized mounts of the dracthyr cavalry.]], },
	[1686] = { name = [[Obsidian Vorquin]], sourceText = '|cFFFFD200Vendor: |rTreysh|n|cFFFFD200Zone: |rThe Forbidden Reach|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t|n|n|cFFFFD200Vendor: |rTethalash|n|cFFFFD200Zone: |rValdrakken|n|cFFFFD200Cost: |r40|TINTERFACE/MONEYFRAME/UI-GOLDICON.BLP:0|t',  description = [[Respected for their resilience in battle, even against dragonfire, these noble beasts are the prized mounts of the dracthyr cavalry.]], },
	[1688] = { name = [[Vicious Sabertooth]], sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 1|nRated Arena and Rated Battleground Season Reward',  description = [[The elite Alliance forces that use these sabertooth mounts use a battle cry that translates from Darnassian as "here kitty kitty".]], },
	[1689] = { name = [[Vicious Sabertooth]], sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 1|nRated Arena and Rated Battleground Season Reward',  description = [[The elite Horde forces that use these sabertooth mounts use a battle cry that translates from Thalassian as "pspspspsps".]], },
	[1690] = { name = [[Whelpling]], sourceText = '',  description = [[]], },
	[1692] = { name = [[Wondrous Wavewhisker]], sourceText = '|cFFFFD200In-Game Shop|r|n',  description = [[They say one of these magical fish is born when a fisherfolk dreams of the one that got away... and grows bigger with each retelling of the tale.]], },
	[1698] = { name = [[Rocket Shredder 9001]], sourceText = '|cFFFFD200Recruit-a-Friend|r |n',  description = [[The salesgoblin always tells people about the RS-9001\'s huge variety of attachments, but somehow neglects to mention that you never get to pick the attachments it'll have when you mount up.]], },
	[1725] = { name = [[Inferno Armoredon]], sourceText = '|cFFFFD200Achievement: |rDragonflight Keystone Master: Season Two|n|cFFFFD200Category: |rDungeons',  description = [[Its tail smells like campfire but its breath smells like brimstone.]], },
	[1727] = { name = [[Tarecgosa\'s Visage]], sourceText = '|cFFFFD200Quest:|r Rhapsody in Blue',  description = [["Farewell, Tarecgosa. A dear sister, hero, student, and teacher. Though blood does not bind us, you will always be family. May your soul find its place soaring among the stars."]], },
	[1729] = { name = [[Big Slick in the City]], sourceText = '|cFFFFD200Quest:|r Snailed It|n|cFFFFD200Zone: |rZaralek Cavern',  description = [[Big heart, big dreams, Big Slick.]], },
	[1730] = { name = [[Igneous Shalewing]], sourceText = '|cFFFFD200Vendor: |rSaccratros|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r400|Hitem:204727|h|TInterface/ICONS/INV_Misc_CuriousCoin.BLP:0|t|h',  description = [[You'd be forgiven for mistaking this creature for a flying volcanic rock. Because to some extent, it is.]], },
	[1732] = { name = [[Cobalt Shalewing]], sourceText = '|cFFFFD200Drop: |rKaroktra|n|cFFFFD200Zone: |rZaralek Cavern',  description = [[Brood of Karoktra, raised in the underlight of Zaralek Cavern.]], },
	[1734] = { name = [[Shadowflame Shalewing]], sourceText = '|cFFFFD200Achievement: |rGlory of the Aberrus Raider',  description = [[Born from the magma, its carapace was originally molten blobs cooled to form dark-colored volcanic rock. Being imbued by shadowflame has only solidified its unbreakable nature.]], },
	[1735] = { name = [[Cataloged Shalewing]], sourceText = '|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Public Event: |rResearchers Under Fire',  description = [[The first instance of a flaming shalewing ever discovered. Nine out of ten researchers agree: It\\'s hot!]], },
	[1736] = { name = [[Boulder Hauler]], sourceText = '|cFFFFD200Vendor: |rPonzo|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r85|Hitem:205188|h|TInterface/ICONS/INV_Stone_WeightStone_08.blp:0|t|h',  description = [[These shalewings have been specially trained to carry large amounts of Barter Boulders for the niffen\'s bartering activities.]], },
	[1737] = { name = [[Sandy Shalewing]], sourceText = '|cFFFFD200Achievement: |rMaster of Troubled Timeways|n|cFFFFD200Category: |rTimewalking',  description = [[The patterns on the wings of that creature resemble sand flowing through an hourglass. One could gaze upon them for ages...]], },
	[1738] = { name = [[Morsel Sniffer]], sourceText = '|cFFFFD200Vendor: |rHarlowe Marl|n|cFFFFD200Zone: |rZaralek Cavern|n|cFFFFD200Cost: |r800|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = [[A breed of shalewings trained by the niffen to find the deepest of morsels.]], },
	[1739] = { name = [[Obsidian Gladiator\'s Slitherdrake]], sourceText = '|cFFFFD200Achievement:|r Gladiator: Dragonflight Season 2|n|cFFFFD200Category:|r Player vs. Player',  description = [[This mischievous drake enjoys flying through geysers and volcanic plumes.]], },
	[1740] = { name = [[Vicious War Snail]], sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 2|nRated Arena and Rated Battleground Season Reward',  description = [[The Alliance uses these ravenous snail mounts to traverse over any obstacle in battlefields.]], },
	[1741] = { name = [[Vicious War Snail]], sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 2|nRated Arena and Rated Battleground Season Reward',  description = [[The Horde uses these ravenous snail mounts to traverse over any obstacle in battlefields.]], },
	[1742] = { name = [[Felcrystal Scorpion]], sourceText = '|cFFFFD200Trading Post|r',  description = [[The results of a fel infusion experiment with one of Skorpyron\'s offspring.]], },
	[1744] = { name = [[Grotto Netherwing Drake]], sourceText = '|cFFFFD200Quest:|r Dragon Keeping|n|cFFFFD200Zone: |rEmerald Dream',  description = [[After traveling to Outland and back, It\\'s only natural these drakes seek out new skies.]], },
	[1771] = { name = [[Highland Drake]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Highland Drake|n',  description = [[The drakes of Azure Span follow a strict personal code of honor as they explore the isles anew.]], },
	[1772] = { name = [[Scarlet Pterrordax]], sourceText = '|cFFFFD200Quest:|r Time to Fly|n',  description = [[This pterrordax was so eager to take to the skies that its handler couldn't finish training it.]], },
	[1773] = { name = [[Harbor Gryphon]], sourceText = '|cFFFFD200Quest:|r Time to Fly|n',  description = [[This gryphon was the runt of its pride, left behind when its brethren were sent for training. Today, however, it is ready to prove its worth.]], },
	[1774] = { name = [[Valiance]], sourceText = '|cFFFFD200Discovery:|r Valiance|n|cFFFFD200Location:|r Naxxramas',  description = [[Most undead steeds have their riders chosen for them. Valiance, on the other hand, chooses its own rider.]], },
	[1776] = { name = [[White War Wolf]], sourceText = '|cFFFFD200Horde |r|n|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rWarden Krizzik|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h|n|n|cFFFFD200Alliance |r|n|cFFFFD200Item: |rGift of the White War Wolf',  description = [[Can a wolf\'s hunger ever be satiated? These lupine brutes seek and devour the enemies of the Horde.]], },
	[1777] = { name = [[Ravenous Black Gryphon]], sourceText = '|cFFFFD200Alliance |r|n|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rWarden Krizzik|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h|n|n|cFFFFD200Horde |r|n|cFFFFD200Item: |rGift of the Ravenous Black Gryphon',  description = [[War corrupted these once-proud creatures. The susurration of their wings now whispers omens of imminent demise.]], },
	[1778] = { name = [[Gold-Toed Albatross]], sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rSorotis|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = [[Soaring from a distant timeway, this never-before-seen avian variant was born in a world of order.]], },
	[1779] = { name = [[Felstorm Dragon]], sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rFalara Nightsong|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = [[The Burning Legion defeated dragons in a distant timeway. These foul fel wyrms chose corruption and servitude over destruction.]], },
	[1781] = { name = [[Sulfur Hound]], sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rProvisioner Qorra|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = [[The Black Empire subjugated these humbled and hateful hounds. Utterly obedient, they still serve the strong.]], },
	[1782] = { name = [[Perfected Juggernaut]], sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rGill the Drill|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = [[Perfection, like beauty, is in the eye of the beholder. But eyesight can be surgically enhanced.]], },
	[1783] = { name = [[Scourgebound Vanquisher]], sourceText = '|cFFFFD200Drop: |rTime Rifts|n|cFFFFD200Vendor: |rBaron Silver|n|cFFFFD200Zone: |rTyrhold Reservoir|n|cFFFFD200Cost: |r3000|Hcurrency:2594|h|TInterface/ICONS/TimelessCoin_yellow.BLP:0|t|h',  description = [[Once bound in service to the Scourge, these undead dragons exist only to spread death, disease, and destruction.]], },
	[1784] = { name = [[Royal Swarmer]], sourceText = '|cFFFFD200Trading Post|r',  description = [[These drones will swarm anyone who dares approach their queen uninvited.]], },
	[1785] = { name = [[Ancestral Clefthoof]], sourceText = '|cFFFFD200Trading Post|r|n',  description = [[This clefthoof has seen ages pass, witnessing everything and patiently teaching many to ride.]], },
	[1786] = { name = [[Renewed Proto-Drake]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles: A New Friend',  description = [[As the isles awaken, these proto-drakes answer the call to soar the skies alongside mortals.]], },
	[1787] = { name = [[Windborne Velocidrake]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Windborne Velocidrake',  description = [[The long talons of the velocidrake give it an edge on the plains.]], },
	[1788] = { name = [[Cliffside Wylderdrake]], sourceText = '|cFFFFD200Achievement:|r Dragon Isles: Cliffside Wylderdrake',  description = [[Often roosting along the steep cliff faces of Thaldraszus, they're swift to the skies in times of danger.]], },
	[1789] = { name = [[Winding Slitherdrake]], sourceText = '|cFFFFD200Achievement:|r A Serpentine Discovery',  description = [[These drakes feel at home in the depths of the earth and the far reaches of the sky.]], },
	[1794] = { name = [[Pattie]], sourceText = '|cFFFFD200Achievement: |rWhodunnit?|n|cFFFFD200Event: |rSecrets of Azeroth',  description = [[Preservationist Kathos encountered Pattie on her first assignment to Vol\'dun. Pattie developed an affinity to Preservationist work but is eager to delve deeper, helping adventurers in their sleuthing.]], },
	[1796] = { name = [[Whelpling]], sourceText = '',  description = [[]], },
	[1797] = { name = [[Ginormous Grrloc]], sourceText = '|cFFFFD200In-Game Shop|r |n',  description = [[It\\'s the second biggest murloc you've ever seen!]], },
	[1798] = { name = [[Azure Worldchiller]], sourceText = '|cFFFFD200Drop: |rDoomwalker|n|cFFFFD200Zone: |rTanaris',  description = [[In the corners of Azeroth, stories are told of a dreaded dragon who swept a pall of torment over the land. In some versions of this story, his fiery presence brought cataclysmic death and destruction worldwide. In other versions, he is blue.]], },
	[1799] = { name = [[Eve\'s Ghastly Rider]], sourceText = '|cFFFFD200Trading Post|r',  description = [[The preferred transportation of the Fenlow family (especially during Hallow\'s End).]], },
	[1801] = { name = [[Verdant Armoredon]], sourceText = '|cFFFFD200Achievement: |rDragonflight Keystone Master: Season Three|n|cFFFFD200Category: |rDungeons',  description = [[The gnarled horns of this armoredon are far stronger than bone.]], },
	[1808] = { name = [[Blossoming Dreamstag]], sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = [[The pelts of young dreamstags flower with the vibrant colors of the dream.]], },
	[1809] = { name = [[Suntouched Dreamstag]], sourceText = '|cFFFFD200Vendor: |rMoon Priestess Lasara|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1200|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = [[The sun\'s radiant energy nurtures these dreamstags, helping them rapidly reach maturity.]], },
	[1810] = { name = [[Rekindled Dreamstag]], sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = [[Ashes of fallen leaves are fertile soil for dreamstags to arise from.]], },
	[1811] = { name = [[Lunar Dreamstag]], sourceText = '|cFFFFD200Vendor: |rMoon Priestess Lasara|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1200|Hcurrency:2003|h|TInterface/ICONS/INV_Faction_WarResources.BLP:0|t|h',  description = [[The moon\'s pale light is reflected in the pelt of these elusive nocturnal creatures.]], },
	[1813] = { name = [[Mimiron\'s Jumpjets]], sourceText = '|cFFFFD200World Event:|r Secrets of Azeroth|n',  description = [[Created as a prototype by Mimiron. The pieces were stolen from his workshop, sold, and lost to time. That is until now..]], },
	[1814] = { name = [[Shadow Dusk Dreamsaber]], sourceText = '|cFFFFD200Achievement: |rGlory of the Dream Raider|n|cFFFFD200Category: |rDungeons & Raids',  description = [[A dreamsaber strikes quickly from the shadows, capturing small animals and slipping back into darkness without a sound. The lucky few who have seen a feeding dreamsaber can see them revel in lazily toying with their prey before beginning their meal.]], },
	[1815] = { name = [[Winter Night Dreamsaber]], sourceText = '|cFFFFD200Drop:|r Dreamseed Cache|n|cFFFFD200Zone:|r Emerald Dream|n',  description = [[These nocturnal dreamsabers carry the chill of winter with them and will often hibernate for days on end if not otherwise awoken.]], },
	[1816] = { name = [[Evening Sun Dreamsaber]], sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = [[These dreamsabers are ferocious in battle, yet lethargic and often found lazily outstretched upon open fields. After soaking in the warmth of daylight, their flame flares that much brighter.]], },
	[1817] = { name = [[Morning Flourish Dreamsaber]], sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = [[Known for the lavish energy that emanates from them, these sabers are notorious tricksters. Their roar scatters the early birds that wake them.]], },
	[1818] = { name = [[Anu'relos, Flame\'s Guidance]], sourceText = '|cFFFFD200Drop: |rFyrakk|n|cFFFFD200Zone: |rAmirdrassil, the Dream\'s Hope (Mythic)',  description = [[Though her feathers flicker with flame\'s destructive potential, they do not scorch, but simply radiate a comfortable heat.]], },
	[1819] = { name = [[Vicious Moonbeast]], sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 3|nRated Arena and Rated Battleground Season Reward',  description = [[Unlike most of its kind, this moonbeast has a taste for Horde blood and an affinity for the color blue.]], },
	[1820] = { name = [[Vicious Moonbeast]], sourceText = '|cFFFFD200PvP:|r Dragonflight: Season 3|nRated Arena and Rated Battleground Season Reward',  description = [[Unlike most of its kind, this moonbeast has a taste for Alliance blood and an affinity for the color red.]], },
	[1830] = { name = [[Flourishing Whimsydrake]], sourceText = '|cFFFFD200Quest:|r Emerald Welcome|n|cFFFFD200Zone: |rEmerald Dream',  description = [[These playful protectors of the Emerald Dream may be larger than their faerie dragon relatives, but they are no less mischievous.]], },
	[1831] = { name = [[Verdant Gladiator\'s Slitherdrake]], sourceText = '|cFFFFD200Achievement:|r Gladiator: Dragonflight Season 3|n|cFFFFD200Category:|r Player vs. Player',  description = [[This drake visited many heroes in their dreams before joining your cause.]], },
	[1833] = { name = [[Springtide Dreamtalon]], sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = [[Surging with the kind of energy and enthusiasm that can make anyone envious.]], },
	[1834] = { name = [[Ochre Dreamtalon]], sourceText = '|cFFFFD200Quest: |rA Little Hope is Never Without Worth|n|cFFFFD200Zone: |rEmerald Dream',  description = [[Playful dreamtalons often decorate themselves in a myriad of colors that only autumn offers.]], },
	[1835] = { name = [[Snowfluff Dreamtalon]], sourceText = '|cFFFFD200Vendor: |rTalisa Whisperbloom|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hitem:211376|h|TInterface/ICONS/INV_Misc_TrailofFlowers.blp:0|t|h',  description = [[As lighthearted and gentle as the snow, they can bounce from one snowdrift to the next without leaving a print.]], },
	[1837] = { name = [[Delugen]], sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = [[Imagine being so engrossed by a dream that it feels real; some might even call it a delusion.]], },
	[1838] = { name = [[Talont]], sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = [[Some daydreams are filled with wondrous possibilities, but shatter when reminded of reality\'s limitations. Some dreams are not so burdened, even when they carry a rider.]], },
	[1839] = { name = [[Stargrazer]], sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = [[One can't help but think what it might feel like to pluck the stars from their fixture in the sky. Perhaps, Stargrazer will show us how someday.]], },
	[1841] = { name = [[Crimson Glimmerfur]], sourceText = '|cFFFFD200Trading Post|r',  description = [[Gilnean legend states this red-and-white fox only crosses the Winter Veil during one season.]], },
	[1938] = { name = [[Mammyth]], sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = [[In the Dream, sometimes the myth is more real than its basis.]], },
	[1939] = { name = [[Imagiwing]], sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = [[There\'s nothing more beautiful than watching your imagination take flight.]], },
	[1940] = { name = [[Salatrancer]], sourceText = '|cFFFFD200Vendor: |rElianna|n|cFFFFD200Zone: |rEmerald Dream|n|cFFFFD200Cost: |r1|Hcurrency:2777|h|TInterface/ICONS/INV_Legion_Faction_DreamWeavers.BLP:0|t|h',  description = [[While most salamanthers' attention jumps freely from one attraction to the next, this one enjoys long periods of deep observation.]], },
	[1942] = { name = [[Jeweled Copper Scarab]], sourceText = '|cFFFFD200Trading Post|r',  description = [[The rulers of desert kingdoms would attune crystals to their personal mounts for quick summoning.]], },
	[1944] = { name = [[Golden Regal Scarab]], sourceText = '|cFFFFD200|r',  description = [[Plated with gold and socketed with emeralds, this scarab is fit to fly royalty in style.]], },
	[1517] = { name = [[Bound Blizzard]], sourceText = '|cFFFFD200Promotion:|r |n',  description = [[A flurrious, fridgid friend, frost-flecked for frozen fun.]], },
}